const float radius = 0.3;
float sRadius;

float sphere(vec3 p, float radius)
{
  return length(p)-radius;
}
float box(vec3 p, vec3 c)
{
  vec3 q = abs(p)-c;
  return min(0.,max(q.x,max(q.y,q.z))) + length(max(q,0.));
}

float map(vec3 p)
{
    float pz = p.z+iTime;
    p.x += sin(pz);
    p.y += cos(pz);
    p = mod(p,2.)-1.;
    return min(max(box(p,vec3(radius)),-sphere(p,sRadius)),sphere(p,sRadius*0.7));
    //return mix(box(p,vec3(0.25)),sphere(p+vec3(cos(iTime)*.25,0.,sin(iTime)*0.05),.3+.25*sin(iTime*1.5)*0.5),sin(pz*3.33+iTime*.05));
}

vec3 getNormal(vec3 p)
{
  vec2 o = vec2(0.001, 0.);
  return normalize(vec3(  map(p+o.xyy)-map(p-o.xyy),
                          map(p+o.yxy)-map(p-o.yxy),
                          map(p+o.yyx)-map(p-o.yyx)));
}

vec3 lighting(vec3 n, vec3 light)
{
    float lit = dot(n, light);
    float sub = max(0.,0.5+0.5*lit);
    vec3 col = vec3(max(0.,lit))+sub*vec3(0.4,0.1,0.1);
    return col;
}


