    vec3 p = ro;
    vec3 rd = vec3(uv.xy*(0.25+0.75*length(uv.xy)),0.75);
    rd = normalize(rd);

    vec3 light = normalize(vec3(sin(time), cos(time), sin(time*0.25)));
    vec3 bgColor = mix(vec3(0.7,0.2,0.6),vec3(0.1,0.05,0.2),length(uv));

    bool hit = false;
    float shading = 0.;
    vec3 color = bgColor;

    sRadius = radius*1.3+sin(iTime)*radius*0.25;

    rd *= 0.25;
    int i = 0;
    p+=vec3(uv.xy,1.);
    float dd;
    while(i<100)
    {
        float d = map(p);
        if(d<0.01)
        {
            hit = true;

            if(d<0.001)
                break;

            p-=rd*dd;
            rd *=0.5;
        }
