#define true				1
#define false				0

#define PI 3.14

#define saturate(x) 		clamp(x, 0.0, 1.0)

#define MENGER_ITERATIONS	2
#define SOFT_SHADOW_STEPS 	32

#define INTERSECT_STEPS		150
#define INTERSECT_MIN_DIST	0.0002
#define INTERSECT_MAX_DIST	50.0

#define MOVING_BRICKS		false


vec2 rotate2D(vec2 p, float angle)
{
    float sRot = sin(angle);
    float cRot = cos(angle);
    return p * cRot + p.yx * sRot * vec2(-1.0, 1.0);
}

float sdUnitBox(vec3 p)
{
    vec3 d = abs(p) - vec3(1.0);
    return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

vec4 map(in vec3 p)
{
    p.xz = mod(p.xz + 5.0, 2.0) - 1.0;
    p.y  = mod(p.y + 1.0, 2.0) - 1.0;

    float d = sdUnitBox(p);
    vec4 res = vec4(d, 1.0, 0.0, 0.0);

    float s = 1.5;
    for(int i = 0; i < MENGER_ITERATIONS; ++i)
    {	 
        #if MOVING_BRICKS
		p.x += iTime * 0.05;
	    #endif

        vec3 a = mod(p * s, 2.0) - 1.0;
        s *= 11.0;
        vec3 r = abs(1.0 - 6.0 * abs(a));
        float da = max(r.x, r.y);
        float db = max(r.y, r.z);
        float dc = max(r.z, r.x);
        float c = (min(da, min(db, dc)) - 1.0) / s;

        if(c > d)
        {
            d = c;
            res = vec4(d, min(res.y, 0.2 * da * db * dc), 0.0, 1.0);
        }
    }

    return res;
}

vec4 intersect(in vec3 ro, in vec3 rd)
{
    float t = 0.0;
    vec4 res = vec4(-1.0);
    vec4 h = vec4(1.0);
    for (int i = 0; i < INTERSECT_STEPS; ++i)
    {
		if(h.x < INTERSECT_MIN_DIST || t > INTERSECT_MAX_DIST) 
		{
	    	break;
		}

        h = map(ro + rd * t);
        res = vec4(t, h.yzw);
        t += h.x;
    }

    if (t > INTERSECT_MAX_DIST) 
    {
		res = vec4(-1.0);
    }

    return res;
}

float softshadow(in vec3 ro, in vec3 rd)
{
    float res = 1.0;
    float t = 0.0;
    for (int i = 0; i < SOFT_SHADOW_STEPS; ++i)
    {
		vec3 pos = ro + rd * t;
        float h = map(pos).x;
        res = min(res, float(SOFT_SHADOW_STEPS) * h / t);
        if(res < 0.101)
		{
            break;
		}
        t += clamp(h, 0.01, 0.2);
    }
    return saturate(res);
}

vec3 calcNormal(in vec3 pos)
{
    vec3 eps = vec3(0.001, 0.0, 0.0);
    vec3 n;
    n.x = map(pos + eps.xyy).x - map(pos - eps.xyy).x;
    n.y = map(pos + eps.yxy).x - map(pos - eps.yxy).x;
    n.z = map(pos + eps.yyx).x - map(pos - eps.yyx).x;
    return normalize(n);
}

vec3 render(in vec3 ro, in vec3 rd, float intensity)
{
    vec3 color = vec3(0.5);
    vec4 res = intersect(ro,rd);
    if(res.x > 0.0)
    {
        const vec3 light1 = vec3(0.5, 0.5, -0.5);

		vec3 pos = ro + res.x * rd;

        vec3 baseColor = vec3(0.0, 0.4, 0.6); //vec3(saturate(sin(iTime * 0.5)), saturate(cos(iTime * 0.3)), saturate(sin(iTime * 0.4)));
		vec3 ambient = vec3(0.2) * baseColor;

		vec3 normal = calcNormal(pos);
		vec3 reflection = reflect(rd, normal);

		float occ = res.y;
		float shadow1 = softshadow(pos + 0.001 * normal, light1);

		vec3 diffuse = baseColor * shadow1 * occ;

		color = diffuse + ambient;		
		color += 0.8 * smoothstep(0.0, 0.1, reflection.y) * softshadow(pos + 0.01 * normal, reflection);
    }

    return pow(color * intensity, vec3(0.4545));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 p = 2.0 * (fragCoord.xy / iResolution.xy) - 1.0;
    p.x *= iResolution.x / iResolution.y;

    // camera
    vec3 ro = vec3(8.75, 0.9, -0.0);
    #if !MOVING_BRICKS
    ro.x -= 0.1 * iTime;
    #endif
    vec3 ww = normalize(vec3(0.0) - ro);
    ww.xz = rotate2D(ww.xz, iMouse.x * 2.0 * PI);
    vec3 uu = normalize(cross(vec3(0.0, 1.0, 0.0), ww));
    vec3 vv = normalize(cross(ww, uu));
    vec3 rd = normalize(p.x * uu + p.y * vv + 2.0 * ww);

    vec3 color = render(ro + vec3(0.0, -1.0, 0.0), rd, 1.0);

    fragColor = vec4(color, 1.0);
}

#define CLOSE_ENOUGH .001
#define K .6
#define RED vec3(.9,.1,.1)
#define GREEN vec3(1.,.9,1.)

struct sdf_return
{
	float sdf;
    vec3 col;
};

struct sphere
{
	vec3 c;
    float r;
    vec3 col;
};

struct ray
{
	vec3 o;
    vec3 d;
};

struct dirLight
{
	vec3 d;
    float i;
};

float sphDist(vec3 p, sphere s)
{
	return distance(p, s.c) - s.r;
}

//from iq : https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float dist_smooth( float d1, float d2, float k ) 
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

float sdf(vec3 p, sphere s1, sphere s2)
{
    float sdist1 = sphDist(p, s1);
    float sdist2 = sphDist(p, s2);
	return dist_smooth(sdist1, sdist2, K);
}

sdf_return scene(vec3 p, sphere s1, sphere s2)
{

    float sdist1 = sphDist(p, s1);
    float sdist2 = sphDist(p, s2);
    //from iq : https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
    float h = clamp( 0.5 + 0.5*(sdist2-sdist1)/K, 0.0, 1.0 );
    vec3 col = mix( s2.col, s1.col, h); 

    return sdf_return(dist_smooth(sdist1, sdist2, K), col);
}

//from iq https://www.iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 p, sphere s1, sphere s2 )
{
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*sdf( p + k.xyy*CLOSE_ENOUGH,s1,s2 ) + 
                      k.yyx*sdf( p + k.yyx*CLOSE_ENOUGH,s1,s2 ) + 
                      k.yxy*sdf( p + k.yxy*CLOSE_ENOUGH,s1,s2 ) + 
                      k.xxx*sdf( p + k.xxx*CLOSE_ENOUGH,s1,s2 ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - vec2(.5);
    uv.x *= iResolution.x/iResolution.y;

    sphere s1 = sphere(vec3(cos(iTime),-sin(iTime*1.4),10.0), .2, RED);
    sphere s2 = sphere(vec3(sin(iTime),sin(iTime),10.0), .6, GREEN);

    dirLight l = dirLight(normalize(vec3(iTime, iTime, -1000.0)), 1.0);

    ray r = ray(vec3(uv.x, uv.y, .0), normalize(vec3(uv.xy, 4.0)));

    vec3 col = vec3(1.0,1.0,.8);

    float dist = .0;
    while(dist < 50.0)
    {
        vec3 rp = r.o + r.d *dist;
        sdf_return r = scene(rp, s1, s2);

        if(r.sdf < CLOSE_ENOUGH)
        {
        	vec3 n = calcNormal(rp,s1,s2);
            col = r.col * max(dot(l.d, n), .0) * l.i;
            break;
        }
        dist += r.sdf;
    }

    fragColor = vec4(pow(col, vec3(1./2.2)),1.0);
}

// Public domain.

// Constants.
const float pi = 3.1415;   

// Colors.
const vec3 black = vec3(0.);
const vec3 white = vec3(1.);
const vec3 red   = vec3(1., 0., 0.);
const vec3 green = vec3(0., 1., 0.);
const vec3 blue  = vec3(0., 0., 1.);

// Parametric equation of a circle.
// https://en.wikipedia.org/wiki/Parametric_equation#Circle
// 't' from -pi to pi.
vec2 pCircle(float r, float t)
{
    return vec2(r * sin(t), r * cos(t));
}

// Draw a circle.
vec3 circle(vec3 color, vec2 uv, float pos, float r, float blur)
{    
    vec3 res = white;

    // Change the bounds of 't' to draw a part of a circle.
    // (Not parameterized because it requires additional smoothing.)
    for (float t = 0.; t < 2. * pi; t += .01) {
        // Bounds.
        vec2 c1 = pCircle(pos,     t);
        vec2 c2 = pCircle(pos - r, t);

        // Find if a point belongs to a section using the triangle
        // inequality (comparing to 'eps' due to floating-point).
        float d1 = distance(c2, uv);
        float d2 = distance(uv, c1);
        float d3 = distance(c2, c1);

    	// Decreasing this value produces an interesting effect.
    	float eps = .001;

		if (abs(d1 + d2 - d3) < eps) {
            // XXX: This blurs inward, reducing the size of the circle.
            // XXX: When 'blur' == 1, the circle disappears.
            res = color;
            res *= smoothstep(d3, d3 * blur, d1);            
            res *= smoothstep(d3, d3 * blur, d2);
            break;
        }
        else res -= color;
    }

    return res;
}

// Draw a point.
vec3 point(vec3 color, vec2 uv, float pos, float r, float t, float blur)
{  
	// Bounds.
    vec2 c1 = pCircle(pos,     t);
    vec2 c2 = pCircle(pos - r, t);

    // Multiplying one of the arguments by -1 shifts it by pi.
    float d1 = distance(uv, c1);
    float d2 = distance(c1, c2);

    // XXX: Do not invert colors when 'blur' == 1.
    vec3 res = vec3(smoothstep(d2, d2 * blur, d1));

    return res * color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1).
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Move 0.0 to the center.
    uv -= .5;

    // Account for the screen ratio.
    uv.x *= iResolution.x / iResolution.y;

    // Output color.
    vec3 color = black;

    color += red;   
    color -= circle(red - blue, uv, .5, .1, .9);

    // Without a loop, the parametric equation gives a point
    // on a circle, which can be used to create a rotating point.
	//
	// Multiplying 't' by -1 makes it move counterclockwise.
	// Multiplying 'iTime' changes the rotation speed.

    // float t = pi;  // fixed position
    float speed = 1.;
    float t = mod(iTime * speed, 2. * pi);  // rotate
    color -= point(red + blue - green, uv, .45, .01, t, .9);

    // Output to screen.
    fragColor = vec4(color, 1.);
}

