// Created by Stephane Cuillerdier - Aiekick/2019 (twitter:@aiekick)
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// Tuned via NoodlesPlate (https://github.com/aiekick/NoodlesPlate/releases)

mat3 RotZ(float a){return mat3(cos(a),-sin(a),0.,sin(a),cos(a),0.,0.,0.,1.);}

vec2 path(float t)
{
	return vec2(cos(t*0.2), sin(t*0.2)) * 2.;
}

float coefFromRGB(vec3 rgb)
{
	vec3 wl = vec3(564.,533.,437.);
	return length(wl * rgb) / length(wl);
}

float df(vec3 p)
{
	p *= RotZ(p.z*0.4);
	p += (sin(p.zxy * 0.8) + sin(p.xzy * 0.9)) * 2.5;
    return length(p)-3.;
}

vec3 nor( in vec3 pos, float prec )
{
	vec3 eps = vec3( prec, 0., 0. );
	vec3 nor = vec3(
	    df(pos+eps.xyy) - df(pos-eps.xyy),
	    df(pos+eps.yxy) - df(pos-eps.yxy),
	    df(pos+eps.yyx) - df(pos-eps.yyx) );
	return normalize(nor);
}

// return color from temperature 
//http://www.physics.sfasu.edu/astro/color/blackbody.html
//http://www.vendian.org/mncharity/dir3/blackbody/
//http://www.vendian.org/mncharity/dir3/blackbody/UnstableURLs/bbr_color.html
vec3 blackbody(float Temp)
{
	vec3 col = vec3(255.);
    col.x = 56100000. * pow(Temp,(-3. / 2.)) + 148.;
   	col.y = 100.04 * log(Temp) - 623.6;
   	if (Temp > 6500.) col.y = 35200000. * pow(Temp,(-3. / 2.)) + 184.;
   	col.z = 194.18 * log(Temp) - 1448.6;
   	col = clamp(col, 0., 255.)/255.;
    if (Temp < 1000.) col *= Temp/1000.;
   	return col;
}

// get density of the df at surfPoint
// ratio between constant step and df value
float SubDensity(vec3 surfPoint, float prec, float ms) 
{
	vec3 n;
	float s = 0.;
    const int iter = 10;
	for (int i=0;i<iter;i++)
	{
		n = nor(surfPoint,prec); 
		surfPoint = surfPoint - n * ms; 
		s += df(surfPoint);
	}

	return 1.-s/(ms*float(iter)); // s < 0. => inside df
}

float SubDensity(vec3 p, float s) 
{
	vec3 n = nor(p,s); 							// precise normale at surf point
	return df(p - n * s);						// ratio between df step and constant step
}

// from shane sahders
// Tri-Planar blending function. Based on an old Nvidia writeup:
// GPU Gems 3 - Ryan Geiss: http://http.developer.nvidia.com/GPUGems3/gpugems3_ch01.html
vec3 tex3D( sampler2D tex, in vec3 p, in vec3 n )
{
    n = max((abs(n) - .2)*7., .001);
    n /= (n.x + n.y + n.z );  
	p = (texture(tex, p.yz)*n.x + texture(tex, p.zx)*n.y + texture(tex, p.xy)*n.z).xyz;
    return p*p;
}

vec3 shade(vec3 ro, vec3 rd, float d, vec3 lp, float li)
{
	vec3 p = ro + rd * d;											// surface point
	float sb = SubDensity(p, 0.01, 0.076);							// deep subdensity (10 iterations)
	vec3 bb = blackbody(100.*sb+100.).brg;								// bb
	vec3 ld = normalize(lp-p); 										// light dir
	vec3 n = nor(p, 0.1);	// normal at surface point

    // derived from bumpmap func from shane
    const vec2 e = vec2(0.1, 0);
    mat3 m = mat3( tex3D(iChannel0, e.xyy, n), tex3D(iChannel0, e.yxy, n), tex3D(iChannel0, e.yyx, n));
   	vec3 g = vec3(1) * m * 20.;
    g -= n * dot(n, g);
    n =  normalize( n + g );

	vec3 refl = reflect(rd,n);										// reflected ray dir at surf point 
	float amb = 0.1242; 												// ambiance factor
	float diff = clamp( dot( n, ld ), 0.0, 1.0 ); 					// diffuse
	float fre = pow( clamp( 1. + dot(n,rd),0.0,1.0), 4. ); 			// fresnel
	float spe = pow(clamp( dot( refl, ld ), 0.0, 1.0 ),16.);		// specular
	float sss = 1. - SubDensity(p, 7.8); 							// one step sub density of df
	vec3 col = (diff + fre + bb * sb * 0.608 + sss * 0.352) * amb * li + spe * 0.612;

	return mix(col, vec3(1), vec3(coefFromRGB(bb*0.8+0.1*diff + 0.1 *spe)));
}

vec3 cam(vec2 uv, vec3 ro, vec3 cv, float t)
{
	vec3 cu = normalize(vec3(0,1,0));
  	vec3 z = normalize(cv-ro);
    vec3 x = normalize(cross(cu,z));
  	vec3 y= cross(z,x);
  	return normalize(z + uv.x*x + uv.y*y);
}

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
    float t = -iTime*2.;

    vec2 si = iResolution.xy;
    vec2 uv = (2.*fragCoord.xy-si)/si.y;

    vec3 col = vec3(1);

	float ca = iTime;
	float ce = 0.5;
	float cd = 10.;

	vec3 ro = vec3(cos(ca), sin(ce), sin(ca)) * cd;
  	vec3 cv = vec3(0);
	vec3 rd = cam(uv, ro, cv, t);

	float md = 20.;
    float s = 1.;
    float d = 1.;
	float ac = 0.0;

	const float iter = 250.;
    for(float i=0.;i<iter;i++)
    {      
        if (abs(s)<d*d*1e-6||d>md) break;
        s = df(ro+rd*d);
        s = max(s, 0.01);
		d += s * 0.1;
    }

	if (d<md)
	{
		vec3 p = ro+rd*d;

		col = shade(ro, rd, d, ro, 1.0);
	}
	else
    {
        col = vec3(0);
    }   

	col = mix(col, vec3(0), 1.-exp(-0.005*d*d));

	fragColor.rgb = col;
}

const float MAX_STEPS = 32.;
const float EPS = .001;
const float END = 100.;
const float START = .0;
float steps;

float rand(vec2 co) {
    return fract(sin(dot(co.xy, vec2(12.9898,78.233))) * 43758.5453);
}

vec2 uni(vec2 d1, vec2 d2) { 
  float m = d1.y;
  if (d1.x>d2.x) m = d2.y;
  return vec2(min(d1.x, d2.x), m);
}

vec2 suni(vec2 d1, vec2 d2, float k) { 
  float h = clamp(0.5+0.5*(d2.x-d1.x)/k, 0.0, 1.0);
  float m = d1.y;
  if (d1.x>d2.x) m = d2.y;
  return vec2(mix(d2.x,d1.x,h)-k*h*(1.-h), m);
}

float ssubs(float d1, float d2, float k) {
  float h = clamp(0.5-0.5*(d2+d1)/k, 0.0, 1.0);
  return mix(d2, -d1, h)+k*h*(1.0-h);
}

float sintsect(float d1, float d2, float k) { 
  float h = clamp(0.5-0.5*(d2-d1)/k, 0.0, 1.0);
  return mix(d2, d1, h)+k*h*(1.0-h);
}

float sphere(vec3 p, float s) { 
  return length(p)-s;
}

float box(vec3 p, vec3 b) { 
  vec3 d = abs(p)-b;
  return length(max(d,0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}

float torus(vec3 p, vec2 t) {
  vec2 q = vec2(length(p.xy)-t.x,p.z);
  return length(q)-t.y;
}

float elli(in vec3 p, in vec3 r) {
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.)/k1;
}

float cap(vec3 p, vec3 a, vec3 b, float r) {
  vec3 pa = p-a, ba = b-a;
  float h = clamp(dot(pa,ba)/dot(ba,ba), .0, 1.);
  return length(pa-ba*h)-r;
}

float diss(vec3 p, float s) {
    float d1 = sphere(p, s); 
    float d2 = (1.+sin(iTime))/2.*.05*(sin(15.*p.x)*cos(15.*p.y)*sin(15.*p.z));
    return d1+d2;
}

vec2 sdf(in vec3 p) {
    vec3 pos = vec3(.0,sin(iTime)*.5,.0);
    vec2 r = vec2(diss(p-pos,1.),.0);
    r = uni(r, vec2(sphere(p-vec3(-.3,.3,1.5)-pos, .15), 1.));
    r = uni(r, vec2(sphere(p-vec3(.3,.3,1.5)-pos, .15), 1.));
    r = uni(r, vec2(sphere(p-vec3(-.3,.3,1.7)-pos, .05), 2.));
    r = uni(r, vec2(sphere(p-vec3(.3,.3,1.7)-pos, .05), 2.));
    r = uni(r, vec2(torus(p-vec3(.0,-.3,1.3)-pos, vec2(max(.05, (1.+sin(iTime))*.2)/2., .03)), 3.));
    r = suni(r, vec2(elli(p-vec3(.9*(1.+sin(iTime/30.)/4.),-.9*(1.2-sin(iTime)/6.),0.5)-pos, vec3(.2,.3,.4)), 0.), .7);
    r = suni(r, vec2(elli(p-vec3(-.9*(1.+sin(iTime/30.)/4.),-.9*(1.2-sin(iTime)/6.),0.5)-pos, vec3(.2,.3,.4)), 0.), .7);
    r = uni(r, vec2(cap(p-vec3(.1,.5,1.0)-pos, vec3(.1,.1,1.0), vec3(.35,.05,1.0), .03), 2.));
    r = uni(r, vec2(cap(p-vec3(-.1,.5,1.0)-pos, vec3(-.1,.1,1.0), vec3(-.35,.05,1.0), .03), 2.));
    return r;
}

vec2 march(vec3 eye, vec3 dir, float s, float e) {
    float d = s;
    for (float i = 0.; i < MAX_STEPS; i++) {
        vec2 res = sdf(eye + d * dir);
        steps = i;
        if (res.x < EPS) return vec2(d, res.y);
        d += res.x;
        if (d >= e) return vec2(e, .0);
    }
    return vec2(e, .0);
}

vec3 rayDir(float fov, vec2 uv) {
    vec2 xy = uv * 2. - 1.;
    xy.y = xy.y / (16. / 9.);
    float z = 2. / tan(radians(fov / 2.));
    return normalize(vec3(xy, -z));
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sdf(vec3(p.x + EPS, p.yz)).x - sdf(vec3(p.x - EPS, p.yz)).x,
        sdf(vec3(p.x, p.y + EPS, p.z)).x - sdf(vec3(p.x, p.y - EPS, p.z)).x,
        sdf(vec3(p.xy, p.z + EPS)).x - sdf(vec3(p.xy, p.z - EPS)).x
    ));
}

vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye, vec3 lightPos, vec3 lightIntensity) {
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));
    float dotLN = dot(L, N);
    float dotRV = dot(R, V);
    if (dotLN < 0.) return vec3(0., 0., 0.);
    if (dotRV < 0.) return lightIntensity * (k_d * dotLN);
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}

vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 amb = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = amb * k_a;
    vec3 pos1 = vec3(0.0, 1.0*iTime/10.0, 30.0*iTime/20.0);
    vec3 ints1 = vec3(0.4, 0.4, 0.4);
    vec3 phong = phongContribForLight(k_d, k_s, alpha, p, eye, pos1, ints1);
    color += phong;
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/iResolution.xy;
    vec3 eye = vec3(.0,.0,10.);
    vec3 dir = rayDir(60.0, uv);
    vec2 res = march(eye, dir, START, END);
    vec3 color = vec3(.0);
    if (res.x >= END-EPS) {
        float glow = min(max(0.,(steps-15.)*0.08), 1.);
        fragColor = vec4(vec3(glow), 1.0);
        return;
    }
    vec3 p = eye + dir * res.x;
    color = vec3(uv, .5+.5*sin(iTime)); 
    if(res.y == 1.) color = vec3(.9);
    if(res.y == 2.) color = vec3(.0);
    if(res.y == 3.) color = vec3(.9,.0,.0);
    color = phongIllumination(color, color, normalize(vec3(1.0, 1.0, 1.0)), 10.0, p, eye);
    fragColor = vec4(color, 1.);
}

const float CLOSE_ENOUGH = 0.001;
const vec3 COLOR = vec3(1.0,.0,.0);
const vec3 BG_COLOR = vec3(.15,.0,.0);
const vec3 SHADOW_COLOR = clamp((COLOR+vec3(.0,.1,.1))/4.0, .0, 1.0);

struct sphere
{
	vec3 c;
    float r;
};


float sphereDist(vec3 p, sphere s)
{
	return distance(p, s.c) - s.r;
}

//from iq : https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float opSmoothUnion( float d1, float d2, float k ) 
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

float sdf(vec3 p)
{
    float returnVal = .0;
    vec3 rep = vec3(1.0)*(1. + abs(sin(iTime*.11) * 10.0));
	vec3 pr = mod(p, rep) - .5 * rep; 

    sphere s1 = sphere(vec3(.0, .1, .0), .07);
    sphere s2 = sphere(vec3(sin(iTime) * .1, cos(iTime)*.2, .1), .1);
    sphere s3 = sphere(vec3(.1,.1,.1), .04);
    returnVal = opSmoothUnion(sphereDist(pr, s1), sphereDist(pr,s2), .2);
    returnVal = opSmoothUnion(returnVal, sphereDist(pr, s3), .15);
	return returnVal; 
}

//from iq https://www.iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 p )
{
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*sdf( p + k.xyy*CLOSE_ENOUGH ) + 
                      k.yyx*sdf( p + k.yyx*CLOSE_ENOUGH ) + 
                      k.yxy*sdf( p + k.yxy*CLOSE_ENOUGH ) + 
                      k.xxx*sdf( p + k.xxx*CLOSE_ENOUGH ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
	vec2 uv    = fragCoord.xy / iResolution.xy - vec2(0.5);
         uv.x *= iResolution.x / iResolution.y;
	vec3 cameraPos = vec3(.0,.0,-4.);

    // Time varying pixel color
    vec3 ro = vec3(cameraPos.x + uv.x, cameraPos.y + uv.y, cameraPos.z);
	vec3 rd = normalize(vec3(uv.x, uv.y, 1.0));
    vec3 col = BG_COLOR;
    float dist = .0;

    vec3 lightDir = normalize(vec3(1.0, 0.0, -1.0));
    float renderDistance = 300.0 + abs(sin(iTime*5.0))*300.0;
    while(dist < renderDistance)
    {
        float d = sdf(ro + rd * dist);
        if(d < CLOSE_ENOUGH)
        {
            col = COLOR;
            vec3 hit = ro + rd * dist;
    		col *= mix(SHADOW_COLOR, col, max(dot(calcNormal(hit), lightDir), .0));
        	break;
        }
       	dist += d;
    }

    // Output to screen
    fragColor = vec4(col, 1.0f);
}

const float e = 2.7182818284590452353602874713527;
float noise(vec2 texCoord)
{
    float G = e + (iTime * 0.1);
    vec2 r = (G * sin(G * texCoord.xy));
    return fract(r.x * r.y * (1.0 + texCoord.x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    uv *= 13.0;

    float t = iTime+55.;
    uv -= t*vec2(3.1,3.5);
    // Time varying pixel color
    vec4 col;
    for (int i=0; i<7; i++) {
        t *= 1.13879213724+sin(col.r+col.g+col.b)*0.0052863;
	    col.r += sin(uv.x*0.4+t);
	    col.g += cos(uv.y*0.4+t*1.1379);
        col.rgba = col.gbar;
    }

    col.rgb = clamp(col.rgb/7.0+0.5,0.0,1.0);
    // Output to screen
    float n = noise(uv);
    fragColor = vec4(n>col.a?col.rgb:col.rgb*0.977+0.033*col.brg,1.0);
}

// SDF stands for Signed Distance Function :
//
// - if point inside surface, return negative value
// - if point outside surface, return positive value
// - if point on the surface, return 0

struct Sdf {
	float dist;
    vec3  norm;
};

Sdf sphereSDF(vec3 point, vec3 pos, float radius)
{
    return Sdf(
        length(point-pos) - radius,
        normalize(point-pos)
    );    
}

vec3  light1Pos      = vec3(-1.0, +1.0, +0.0);
vec3  light1Color    = vec3(+1.0, +0.5, +0.0);

vec3  light2Pos      = vec3(+1.0, +1.0, +0.0);
vec3  light2Color    = vec3(+0.0, +0.5, +1.0);

vec3  sphere1Pos     = vec3(+0.0, +0.0, +0.0);
vec3  sphere1Color   = vec3(+1.0, +1.0, +1.0);

vec3 computeScene(in vec3 eyePos, in vec3 eyeDir, in vec2 uv)
{
    float depth = -100.0;
    for (int i = 0; i < 32; i++)
    {
        vec3 pt = eyePos + depth * eyeDir;

        Sdf s1 = sphereSDF(pt, sphere1Pos, 1.0);
        Sdf s2 = sphereSDF(pt, light1Pos, 0.1);
        Sdf s3 = sphereSDF(pt, light2Pos, 0.1);

        float d = min(s1.dist, min(s2.dist, s3.dist));
        if (d < 0.0001)
        {
            if (d == s2.dist)
        	{
            	return light1Color;
        	}

        	if (d == s3.dist)
        	{
            	return light2Color;
        	}

            // we have a hit, now compute color
            vec3 normal = s1.norm;

            float diffuse1 = clamp(dot(normal, normalize(light1Pos)), 0.0, 1.0);
            float diffuse2 = clamp(dot(normal, normalize(light2Pos)), 0.0, 1.0);

            vec3 color1 = diffuse1 * light1Color;
            vec3 color2 = diffuse2 * light2Color;

            return mix(color1, color2, 0.5) + sphere1Color * 0.7;
        }

        depth += d;
        if (depth > 100.0)
            break;
    }

    return vec3(smoothstep(0.0, 1.0, max(0.0, 0.1-uv.y)));
}

const float PI = 3.1415;

void moveLights()
{
    light1Pos.xz = 2.0 * vec2(sin(iTime), cos(iTime));
    light1Pos.y = +2.5;

    light2Pos.xz = 2.0 * vec2(sin(iTime+PI), cos(iTime+PI));
    light2Pos.y = +2.5;
}

void moveSphere()
{
    sphere1Pos.xz = vec2(0.0);
    sphere1Pos.y = -0.5 + sin(iTime*2.1)/2.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // prepare coordinate so that :
    //     -1.0 <= ux.y <= +1.0
    // and -rat <= ux.x <= +rat
    vec2 uv = (fragCoord/iResolution.xy)-0.5;
    uv.x *= iResolution.x / iResolution.y;

    moveLights();
    moveSphere();

    // define camera position and direction
	vec3 camPos = vec3(0.0, 0.5, 20.0);
    vec3 camDir = normalize(vec3(uv, -1.0 / tan(PI/10.0)));

    // compute scene
    vec3 col = computeScene(camPos, camDir, uv);
    fragColor = vec4(vec3(col),1.0);
}

