void FontRender(sampler2D fontSampler, inout vec3 background)
{
	vec4 fc = font_deferred_color;
	if (fc.a > 0.0) {
		fc.a *= texture(fontSampler, font_deferred_uv).r;
		background = mix(background, fc.rgb, fc.a);
		font_deferred_color = vec4(0);
	}
	background += vec3(font_overdraw*0.2);
	font_overdraw = 0.0;
}

