void DrawUIntFillZeros(uint x, uint digits)
{
	uint d = 1U;
	bool fill_zeros = (digits != 0U);
	if (digits == 0U) {
		while (d <= x) {
			d *= 10U;
			digits++;
		}
	}
	if (fill_zeros) {
