// sky map
#define sund normalize(vec3(2.5, 1.5, -.5))

vec3 sky_map (vec3 d) {
	float dimness = .5+.5*cos(d.y*PI);
	dimness = pow(dimness, 1.);

	// return texture(iChannel1, d).rgb;

	vec2 g = vec2(d.x/d.y, d.z/d.y)*2.*sign(d.y);

	// get ths sky color
	float cloudamt = 0.;
	cloudamt += .7*texture(iChannel1, vec2(g.x+g.y, g.x-g.y)/430.).r;
	cloudamt += .3*texture(iChannel1, vec2(g.x+g.y, g.x-g.y)/150.).r;
	cloudamt = pow(cloudamt, 2.);
		// cloudamt *= .4;
		// cloudamt += .0*texture(iChannel1, vec2(g.x+g.y, g.x-g.y)/90.).r;
		float sunamt = clamp(dot(d, sund), 0., 1.);
		float sunglow = pow(sunamt, 20.);

		vec3 SKY_COLOR = vec3(.0, .5, 1.);
		SKY_COLOR += cloudamt;
		// SKY_COLOR *= 1.-sunhalo;
		// SKY_COLOR += vec3(1., .9, .6)*sunhalo;
		SKY_COLOR *= 1.-sunglow;
		SKY_COLOR += vec3(1., 1., 1.)*sunglow;

		dimness = mix(dimness, 1., sunglow);
		// dimness = mix(dimness, 1., sunhalo);//sunamt*sunamt);
	if (d.y > .2) {
		return SKY_COLOR*pow(dimness, .05)*mix(.7, 1., sunglow);
	}
	else {
		// return vec3(1.)*mod(floor(g.x)+floor(g.y), 2.);
		vec3 GROUND_COLOR = vec3(.3, .5, .3)*(.0+.8*texture(iChannel1, g/10.).r);
		float dirtamt = texture(iChannel1, g/54.).r;
		GROUND_COLOR *= 1.-dirtamt;
		GROUND_COLOR += vec3(.5, .3, .0)*dirtamt;

		GROUND_COLOR = 3.*GROUND_COLOR*GROUND_COLOR-2.*GROUND_COLOR*GROUND_COLOR*GROUND_COLOR;
		GROUND_COLOR = 3.*GROUND_COLOR*GROUND_COLOR-2.*GROUND_COLOR*GROUND_COLOR*GROUND_COLOR;
		GROUND_COLOR = 3.*GROUND_COLOR*GROUND_COLOR-2.*GROUND_COLOR*GROUND_COLOR*GROUND_COLOR;
		GROUND_COLOR *= 17.;
		GROUND_COLOR = clamp(GROUND_COLOR, 0., 1.);

		float puddleamt = .5+.5*sin(g.x*length(g)*.05);
		puddleamt = .5*puddleamt+.5*texture(iChannel1, vec2(g.x, g.y)/20.).r;
		puddleamt = pow(puddleamt, .3);
		dimness = mix(dimness, 1., puddleamt)*.5;

		if (puddleamt > .8) {
			float amt = (puddleamt-.3)/.6;
			amt = clamp(amt, 0., 1.);
			amt = pow(amt, 2.);
			GROUND_COLOR = mix(GROUND_COLOR, SKY_COLOR+vec3(.4), amt);
		}

		if (-.2 <= d.y && d.y <= .2) {
			float amt = (d.y+.2)/.4;
			dimness = mix(
				dimness,
				mix(
					dimness*2.,
					1.,
					-puddleamt
				),
				amt
			);
			amt = 3.*amt*amt-2.*amt*amt*amt;
			amt = 3.*amt*amt-2.*amt*amt*amt;
			amt = 3.*amt*amt-2.*amt*amt*amt;
			GROUND_COLOR = mix(GROUND_COLOR, SKY_COLOR, amt);
		}
		return GROUND_COLOR*dimness*mix(0., 1., clamp(abs(d.y)*12., 0., 1.));

	}

}




// conformap transforms

#define complex_exp(z) vec3(exp(z.x)*vec2(cos(z.y), sin(z.y)), floor(z.y/TAU) )
#define complex_ln(Z) vec3(\
	log(length(Z.xy)),\
	mod(atan(Z.y, Z.x), TAU)+Z.z*TAU,\
	Z.z\
)

vec3 complex_multZK (vec3 z, vec3 k) {
	float th = mod(atan(z.y, z.x), TAU)+z.z*TAU;
	float ph = mod(atan(k.y, k.x), TAU)+z.z*TAU;
	return vec3(
		(length(z.xy)*length(k.xy))*vec2(cos(th+ph), sin(th+ph)),
		floor(th/TAU+ph/TAU)
	);
}

vec3 complex_powZK (vec3 z, vec3 k) {
	float th = mod(atan(z.y, z.x), TAU)+z.z*TAU;
	return vec3(
		pow(length(z.xy), k.x)*vec2(cos(th*k.x), sin(th*k.x)),
		floor(th*k.x/TAU)
	);
}

#define phaselen 3

vec3 f (vec3 z, float[phaselen] phaselvl) {
	#define scl 26./TAU
	#define zoom 1.3
	#define pan vec2(0., 0.)
	int lvli = phaselen-1;

	z.x -= arg;

	z.xy /= scl;

	#if 1
	z.z = phaselvl[lvli];
	--lvli;
	//z = complex_multZK(z, vec3(.5, -.5, -1.) );
	z.xy = vec2(z.x*.5+z.y*.5, -z.x*.5+z.y*.5);
	z = complex_exp(z);
	#endif

	#if 0
	// complex arctangent
	// atan(z) = (1/2i)ln(-z+i/z+i)
	z.z = phaselvl[lvli];
	--lvli;
	z = complex_multZK(
		vec3(0., 1., 0.)+complex_multZK(z, vec3(-1., 0., 0.) ),
		complex_powZK(
			vec3(0., 1., 0.)+z,
			vec3(-1., 0., 0.)
		)
	);
	z.z = phaselvl[lvli];
	--lvli;
	z = complex_ln(z);
	z = complex_multZK(z, vec3(0., -.5, 0.));
	#endif


	// mobius
	z.x += .2;
	float th = mod(atan(z.y, z.x), TAU)+z.z*TAU;
	z.xy = vec2(cos(-th), sin(-th))/length(z.xy);
	z.x -= 2.;

	//z.xy = vec2(z.x*.5+z.y*.5, -z.x*.5+z.y*.5);

	z.xy *= zoom;
	z.xy += pan;
	return z;

}
vec3 inv_f (vec3 z, out float[phaselen] phaselvl) {
	int lvli = 0;

	z.xy -= pan;
	z.xy /= zoom;

	//z.xy = vec2(z.x-z.y, z.x+z.y);

	// mobius
	z.x += 2.;
	float th = mod(atan(z.y, z.x), TAU)+z.z*TAU;
	z.xy = vec2(cos(-th), sin(-th))/length(z.xy);
	z.x -= .2;

	#if 0
	// complex tangent
	// 2i*z then e^z then blah
	// tan(z) = blah(  iz-i/-z-1  )
	z = complex_multZK(z, vec3(0., 2., 0.));
	z = complex_exp(z);
	phaselvl[lvli] = z.z;
	++lvli;
	z = complex_multZK(
		vec3(0., -1., 0.)+complex_multZK(z, vec3(0., 1., 0.)),
		complex_powZK(
			vec3(-1., 0., 0.)+complex_multZK(z, vec3(-1., 0., 0.)),
			vec3(-1., 0., 0.)
		)
	);
	phaselvl[lvli] = z.z;
	++lvli;
	#endif

	#if 1
	z = complex_ln(z);
	//z = complex_multZK(z, vec3(1., 1., 0.));
	z.xy = vec2(z.x-z.y, z.x+z.y);
	phaselvl[lvli] = z.z;
	++lvli;
	#endif

	z.xy *= scl;

	z.x += arg;


	return z;	
}



