//Based off https://www.shadertoy.com/view/MtjGRd by Flyguy
#define RGB(r,g,b) (vec3(r,g,b) / 255.0)

//Uncomment for unecessary full CGA!!!
//#define CGACOMPLETE
#ifdef CGACOMPLETE
#define PAL_SIZE 16.0
vec3 CGACompletePal[16] = vec3[](RGB(0, 0, 0),
                                 RGB(85, 85, 85),
                                 RGB(0, 0, 170),
                                 RGB(0, 170, 0),
                                 RGB(170, 0, 0),
                                 RGB(170, 85, 0),
                                 RGB(0, 170, 170),
                                 RGB(170, 0, 170),
                                 RGB(170, 170, 170),
                                 RGB(85, 85, 255),
                                 RGB(85, 255, 85),
                                 RGB(255, 85, 85),
                                 RGB(85,255, 255), 
                                 RGB(255, 85, 255), 
                                 RGB(255, 255, 85),
                                 RGB(255, 255, 255));
#else
#define PAL_SIZE 4.0
vec3 CGAPal[4] = vec3[](RGB(0, 0, 0), 
                        RGB(255, 85, 255), 
                        RGB(85,255, 255), 
                        RGB(255, 255, 255));
#endif

vec3 dither8x8(vec2 position, float brightness) {  
  float idx = clamp(brightness,0.0,1.0)* (PAL_SIZE - 1.0);

  #ifdef CGACOMPLETE
  vec3 c1 = CGACompletePal[int(idx)];
  vec3 c2 = CGACompletePal[int(idx) + 1];
  #else  
  vec3 c1 = CGAPal[int(idx)];
  vec3 c2 = CGAPal[int(idx) + 1];
  #endif

  float limit = texture(iChannel0, position / iChannelResolution[0].xy).r;
  float mixAmt = float(fract(idx) > limit);

  return mix(c1,c2,mixAmt);
}
// Sphere SDF from http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdSphere( vec3 p, float s ){
  return length(p)-s;
}

float intersect(vec3 point){
	return min(sdSphere(vec3(cos(iTime), 1.0, 5.0 + sin(iTime)) - point, 1.0), point.y + 0.1);
}

float march(vec3 rayOrigin, vec3 rayDirection){
	float minimumHit = 0.0;

    for(int i = 0; i < 100; i++){
    	vec3 hit = rayOrigin + rayDirection * minimumHit;
        float dist = intersect(hit);
        minimumHit += dist;
        if(dist > 100.0 || dist < 0.01) break;
    }
    return minimumHit;
}

vec3 getNormal(vec3 point){
	float distPoint = intersect(point);
    vec2 e = vec2(0.01, 0);

    vec3 norm = distPoint - vec3(intersect(point - e.xyy), intersect(point - e.yxy), 
                            	 intersect(point - e.yyx));
    return normalize(norm);
}

float lightCalculate(vec3 lightPos, vec3 point){
	vec3 lightDirection = normalize(lightPos - point);
    vec3 normal = getNormal(point);
    float brightness = clamp(dot(normal, lightDirection), 0.0, 1.0);

    float shadowCatch = march(point + normal * 0.10, lightDirection);
    if(shadowCatch < length(lightPos - point)) brightness *= 0.1;

    return brightness;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 col;

    vec3 rayOrigin = vec3(0.0, 1.5, -0.5);
    vec3 rayDirection = normalize( vec3(uv,1) );

    vec3 lightPos = vec3(1, 5, 1);
    lightPos.xz += vec2(sin(iTime), cos(iTime)) * 2.0;

    float hitDist = march(rayOrigin, rayDirection);
    vec3 hitPoint = rayOrigin + rayDirection * hitDist;

    col = dither8x8(fragCoord, lightCalculate(lightPos,hitPoint));

    // Output to screen
    fragColor = vec4(col,1.0);
}

// using 2d vector graphics library (https://www.shadertoy.com/view/lslXW8)

vec3 weight(vec3 p) {
    return vec3(
		(p[0]+p[1])*0.5,
        p[1],
        (p[1]+p[2])*0.5);
}

float interpolate2d(mat3 p, vec2 o) {
    vec3 s = vec3(
    	(1.0 - o.x)*(1.0 - o.x),
    	2.0*(1.0 - o.x)*o.x,
    	o.x*o.x);
    vec3 t = vec3(
        (1.0 - o.y)*(1.0 - o.y),
    	2.0*(1.0 - o.y)*o.y,
    	o.y*o.y);
    vec3 q = vec3(
    	dot(weight(p[0]), s),
    	dot(weight(p[1]), s),
    	dot(weight(p[2]), s));

	return dot(weight(q), t);

}

