#define pow(a,b) pow(abs(a),(b))

const int MARCHING_STEPS = 150;
const int ALIASING_STEPS = 1;
const float MIN_DIST = 0.;
const float MAX_DIST = 40.;
const float EPSILON = 1e-3;

struct Light {
	vec3 iamb; // ambient intensity
	vec3 idiff; // diffuse intensity
	vec3 ispec; // specular intensity
	vec3 pos;
	float shininess;
};

struct Material {
	vec3 amb; // ambient constant
	vec3 diff; // diffuse constant
	vec3 spec; // specular constant
};

mat2 rot(float a) {
	return mat2(cos(a), sin(a), -sin(a), cos(a));
}

float sdSphere(vec3 p, float radius) {
	return length(p) - radius;
}

float sdBox(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
	return length(max(d, 0.)) + min(max(d.x, max(d.y, d.z)), 0.);
}

float sdPlane(vec3 p, vec4 n) {
  return dot(p,n.xyz) + n.w;
}

vec3 repeat(vec3 p, vec3 s) {
    return mod(p, s) - .5 * s;
}

float map(vec3 p) {
    float d = 1e10;
    vec3 q = vec3(mod(p.x, 1.), p.yz);
    d = min(d, sdPlane(p, vec4(0, 1, 0, 1.)));

    {
        vec3 p2 = p  - vec3(-4, 1, 0);
        p2.zx *= rot(iTime);
    	d = min(d, sdBox(p2, vec3(1, 2, 1)));
    }

    d = min(d, sdSphere(p - vec3(4, 0., 1), 1.3));
    d = min(d, sdBox(p - vec3(0, 2, - 2), vec3(2, 2, .01)));
	return d;
}

vec3 mapGradient(vec3 p) {
    vec2 e = vec2(EPSILON, 0.);
	return normalize(vec3(
		map(p + e.xyy) - map(p - e.xyy),
		map(p + e.yxy) - map(p - e.yxy),
        map(p + e.yyx) - map(p - e.yyx)
	));
}

float rayProcess(vec3 camPos, vec3 rayDir, float start, float end) {
	float depth = start;
	for(int i = 0; i < MARCHING_STEPS; ++i) {
		float dist = map(camPos + depth * rayDir);
		if(dist < EPSILON) return depth;
		depth += dist;
		if(dist >= end) return end;
	}
	return end;
}

vec3 rayDirection(float camAngle, vec2 coord) {
	vec2 uv = (coord - .5) * iResolution.xy;
	float focalDist = iResolution.y / 2. / tan(radians(camAngle) / 2.);
	return normalize(vec3(uv, -focalDist));
}


float checkerboard(in vec3 p) {
    vec3 q = floor(p);
    return mod(q.x + q.y + q.z,2.);              
}

// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    // bounding volume
    const float maxHei = 2.;
    float tp = (maxHei-ro.y)/rd.y; 
    if( tp>0.0 ) tmax = min( tmax, tp );

    float res = 1.0;
    float t = mint;
    for( int i=min(0, iFrame); i<16; i++ )
    {
		float h = map( ro + rd*t );
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 applyLight(Light light, vec3 p, vec3 rd, vec3 nor) {
    vec3 col = vec3(0.);

    // material        
    vec3 mate = vec3(.3);
    if(p.y < -.999) {
        float f = checkerboard(p);
        mate = 0.15 + f*vec3(0.05);
    }
    else if(abs(p.x) < 2.) {
        vec2 uv = fract(p.yx * .25 - vec2(0, -.5));
        mate = texture(iChannel0, uv.yx).rgb;
        //mate = vec3(uv, 0.) * .3;
    }

    // key light
    vec3  lig = normalize(light.pos);
    vec3  hal = normalize( lig-rd );
    float dif = clamp( dot( nor, lig ), 0.0, 1.0 );

    float spe = pow( clamp( dot( nor, hal ), 0.0, 1.0 ),40.0)*
        dif * (0.04 + 0.99*pow( clamp(1.0+dot(hal,rd),0.0,1.0), 6.0 ));


    dif *= calcSoftshadow(p, lig, .1, 3.);

    col = mate * 3.3*dif*vec3(.80,0.70,0.6);
    col +=      7.0*spe*vec3(1.00,0.70,0.5);

    // ambient light
    float amb = .5;
    col += mate*amb;

	return col;
}

vec3 shading(vec3 ro, vec3 rd) {
	Light light;
	light.iamb = vec3(.5, .6, .7);
	light.idiff = vec3(.5);
	light.ispec = vec3(.4);
	light.pos = vec3(-8., 8., 5.);
	light.shininess = 6.;

	//vec3 col = applyLight(mat, light, p, eye);
    vec3 resCol = vec3(0);
    vec3 p = ro;
    float alpha = 1.;
    for(int i = 0; i < 2; ++i) {
        float depth = rayProcess(p, rd, MIN_DIST, MAX_DIST);
    	p += depth * rd;
    	vec3 nor = mapGradient(p);

    	vec3 col = applyLight(light, p, rd, nor);
        col *= pow(smoothstep(MAX_DIST, 10., depth), 2.); // fog

        resCol += col * alpha;
        alpha *= .25;

    	rd = reflect(rd, nor);
        p += rd * 1e-3; // small incr to avoid null dist
    }


	return resCol;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 tot = vec3(0.);

    for(int i = 0; i < ALIASING_STEPS; ++i) {
        for(int j = 0; j < ALIASING_STEPS; ++j) {
            vec2 offset = vec2(i, j) / 2. - .5;
            vec3 camPos = vec3(0., 5., 7.);
            vec3 dir = rayDirection(60., (fragCoord + offset) / iResolution.xy);

            //float ang = -.3 + .5*sin(.5 * iTime);
            float ang = .5 * iTime;
            camPos.zx *= rot(ang);
            dir.yz *= rot(.5);
            dir.zx *= rot(ang);

            vec3 col = vec3(0.);
            col = shading(camPos, dir);

            // gamma
            tot += pow(col, vec3(1. / 1.7));
        }
    }

    tot /= float(ALIASING_STEPS * ALIASING_STEPS);
	fragColor = vec4(tot, 1.);
}

#define PI 3.1416
#define SKY_COLOR vec3(0.027, 0.151, 0.354)
#define LIGHT_SKY vec3(0.45, 0.61, 0.98)
#define STAR_COLOR vec3(0.92, 0.92, 0.14)
#define MOON_COLOR vec3(0.81, 0.81, 0.81)

//Noise functions from https://www.youtube.com/watch?v=zXsWftRdsvU
float noise11(float p) {
	return fract(sin(p*633.1847) * 9827.95);
}

float noise21(vec2 p) {
	return fract(sin(p.x*827.221 + p.y*3228.8275) * 878.121);
}

vec2 noise22(vec2 p) {
	return fract(vec2(sin(p.x*9378.35), sin(p.y*75.589)) * 556.89);
}

//From https://codepen.io/Tobsta/post/procedural-generation-part-1-1d-perlin-noise
float cosineInterpolation(float a, float b, float x) {
    float ft = x * PI;
    float f = (1. - cos(ft)) * .5;
    return a * (1. - f) + b * f;
}

float smoothNoise11(float p, float dist) {
    float prev = noise11(p-dist);
    float next = noise11(p+dist);

    return cosineInterpolation(prev, next, .5);
}

float smoothNoise21(vec2 uv, float cells) {
	vec2 lv = fract(uv*cells);
    vec2 id = floor(uv*cells);

    //smoothstep function: maybe change it later!
    lv = lv*lv*(3.-2.*lv);

    float bl = noise21(id);
    float br = noise21(id+vec2(1.,0.));
    float b = mix(bl, br, lv.x);

    float tl = noise21(id+vec2(0.,1.));
    float tr = noise21(id+vec2(1.,1.));
    float t = mix(tl, tr, lv.x);

    return mix(b, t, lv.y);
}

vec2 smoothNoise22(vec2 uv, float cells) {
	vec2 lv = fract(uv*cells);
    vec2 id = floor(uv*cells);

    //smoothstep function: maybe change it later!
    lv = lv*lv*(3.-2.*lv);

    vec2 bl = noise22(id);
    vec2 br = noise22(id+vec2(1.,0.));
    vec2 b = mix(bl, br, lv.x);

    vec2 tl = noise22(id+vec2(0.,1.));
    vec2 tr = noise22(id+vec2(1.,1.));
    vec2 t = mix(tl, tr, lv.x);

    return mix(b, t, lv.y);
}


float valueNoise11(float p) {
	float c = smoothNoise11(p, 0.5);
    c += smoothNoise11(p, 0.25)*.5;
    c += smoothNoise11(p, 0.125)*.25;
    c += smoothNoise11(p, 0.0625)*.125;

    return c /= .875;
}

float valueNoise21(vec2 uv) {
	float c = smoothNoise21(uv, 4.);
    c += smoothNoise21(uv, 8.)*.5;
    c += smoothNoise21(uv, 16.)*.25;
    c += smoothNoise21(uv, 32.)*.125;
    c += smoothNoise21(uv, 64.)*.0625;

    return c /= .9375;
}

vec2 valueNoise22(vec2 uv) {
	vec2 c = smoothNoise22(uv, 4.);
    c += smoothNoise22(uv, 8.)*.5;
    c += smoothNoise22(uv, 16.)*.25;
    c += smoothNoise22(uv, 32.)*.125;
    c += smoothNoise22(uv, 64.)*.0625;

    return c /= .9375;
}

vec3 point(vec2 p, vec2 uv, vec3 color, float size, float blur) {
	float dist = distance(p, uv);

    return color*smoothstep(size, size*(0.999-blur), dist);
}

float mapInterval(float x, float a, float b, float c, float d) {
	return (x-a)/(b-a) * (d-c) + c;
}

float blink(float time, float timeInterval) {
    float halfInterval = timeInterval / 2.0;
    //Get relative position in the bucket
    float p = mod(time, timeInterval);


    if (p <= timeInterval / 2.) {
    	return smoothstep(0., 1., p/halfInterval);
    } else {
        return smoothstep(1., 0., (p-halfInterval)/halfInterval);
    }
}

vec3 sampleBumps(vec2 p, vec2 uv, float radius) {
	float dist = distance(p, uv);

    if (dist < radius) {
    	return vec3((1.-valueNoise21(uv*10.))*.1);
    }
    return vec3(0.); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ouv = uv;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec2 mouse = iMouse.xy / iResolution.xy;

    float time = iTime + mouse.x*10.;

    //vec3 col = vec3(0.0);
    //float m = valueNoise21(uv);    
	vec3 col = mix(LIGHT_SKY, SKY_COLOR, ouv.y+.6);

    col *= .65 + (1.-ouv.y);

    //Add clouds
    vec2 timeUv = uv;
    timeUv.x += time*.1;
    timeUv.y += valueNoise11(timeUv.x+.352)*.01;
    float cloud = valueNoise21(timeUv);
    col += cloud*.1;

    //Add stars in the top part of the scene
    float timeInterval = 5.;
    float timeBucket = floor(time / timeInterval);

    // Moon
    vec2 moonPosition = vec2(-0.600, 0.25);

    col += point(moonPosition, uv, MOON_COLOR, 0.15, 0.3);
    // Moon bumps
    col += sampleBumps(moonPosition, uv, 0.12);

    for (float i = 0.; i < 25.; i++) {
	    vec2 starPosition = vec2(i/10., i/10.);

        starPosition.x = mapInterval(valueNoise11(timeBucket + i*827.913)-.4, 0., 1., -0.825, 0.825);
        starPosition.y = mapInterval(valueNoise11(starPosition.x)-.3, 0., 1., -0.445, 0.445);

        float starIntensity = blink(time, timeInterval);
        //Hide stars that are behind the moon
        if (distance(starPosition, moonPosition) > 0.14) {
        	col += point(starPosition, uv, STAR_COLOR, 0.001, 0.0)*clamp(starIntensity-.1, 0.0, 1.0)*10.0;
        	col += point(starPosition, uv, STAR_COLOR, 0.009, 3.5)*starIntensity*3.0;
        }
    }
  //col = vec3(blink(time, timeInterval));

    // Output to screen
    fragColor = vec4(col,1.0);
}

