// Signed distance to a 2D circle
// see: http://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm


float sdCircle( vec2 p, float r )
{
  return length(p) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

	float d = sdCircle( p, 0.6 );

    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d));
	col *= 0.8 + 0.2*cos(140.0*d + 9.0*iTime);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(d)) );

	fragColor = vec4(col,1.0);
}

//line interpolation functions
float plot(vec2 st, float pct){
	return smoothstep(pct-0.02, pct, st.y) - smoothstep(pct, pct+0.02, st.y); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 st = fragCoord.xy/vec2(iResolution);

    //Adding time to x value for continuos sin
    st.x += 1.4*iTime;
    //Smoothstepping between max sin values, 1 and 0. 
    float y = smoothstep(-1.0, 0.0, sin(st.x)) - smoothstep(0.0, 1.0, sin(st.x));

    //line drawing to the screen
    vec3 color = vec3(y);
    float line = plot(st, y);

    color = line * vec3(0.0, 1.0, 0.0);
    fragColor =vec4(color, 1.0);

}

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

vec3 Repeat(vec3 p){
    return mod(p,4.)-2.;
}

float N31(vec3 p)
{
    return fract(sin(dot(p.xyz, vec3(12.9898, 78.233, 56.787))) * 43758.5453);
}

vec3 N33(vec3 p)
{
    float n = N31(p);
    return vec3(fract(n*cos(p.x)), fract(n*sin(p.y)), fract(n*cos(p.z)));
}

struct fracpos{
    vec3 fp;
    vec3 id;
};

fracpos GetFracPos(vec3 p){
    vec3 fp = mod(p,4.)-2.;
    vec3 id = floor(p/4.);
    return fracpos(fp,id);
}

vec3 SphereCenterLocalPos(vec3 id, vec3 offs){
    vec3 s = vec3(0.,0.,0.);
    vec3 n = N33(id+offs);
    s += vec3(cos(iTime*n.x*2.),sin(iTime*n.y),cos(iTime*n.z))*1.8;
    return s + offs*4.0;
}

float LineDist(vec3 p, vec3 a, vec3 b, float r){
    vec3 pa = p-a;
    vec3 ba = b-a;
    float t = clamp(dot(pa,ba)/dot(ba,ba),0.,1.);
    return length(pa - ba * t)-r;
}

float LineRadius(vec3 p,vec3 q){
    return 1./(length(p-q)*30.);
    //return (1.-(smoothstep(0.,4., length(p-q))))*.1;
}

float smoothMin(float d1, float d2, float k){
    float h = exp(-k * d1) + exp(-k * d2);
    return -log(h) / k;
}


float GetDist(vec3 p){
    fracpos pos = GetFracPos(p);

    vec3 q[27];
    int i = 0;
    for (int z = -1; z <= 1; z++){
        for (int y = -1; y <= 1; y++){
            for (int x = -1; x <= 1; x++){
                q[i] = SphereCenterLocalPos(pos.id, vec3(x,y,z));
                i++;
            }

        }
    }

    float sphereDist = length(pos.fp-q[13])-.1;

    float lineDistMin = 100.;
    float minLength = 3.;

	lineDistMin = length(q[13] - q[4]) < minLength ? min(lineDistMin, LineDist(pos.fp, q[13], q[4], LineRadius(q[13], q[4]))) : lineDistMin;
	lineDistMin = length(q[13] - q[22]) < minLength ? min(lineDistMin, LineDist(pos.fp, q[13], q[22], LineRadius(q[13], q[22]))) : lineDistMin;
	lineDistMin = length(q[13] - q[12]) < minLength ? min(lineDistMin, LineDist(pos.fp, q[13], q[12], LineRadius(q[13], q[12]))) : lineDistMin;
	lineDistMin = length(q[13] - q[14]) < minLength ? min(lineDistMin, LineDist(pos.fp, q[13], q[14], LineRadius(q[13], q[14]))) : lineDistMin;
	lineDistMin = length(q[13] - q[10]) < minLength ? min(lineDistMin, LineDist(pos.fp, q[13], q[10], LineRadius(q[13], q[10]))) : lineDistMin;
	lineDistMin = length(q[13] - q[16]) < minLength ? min(lineDistMin, LineDist(pos.fp, q[13], q[16], LineRadius(q[13], q[16]))) : lineDistMin;


    float d = smoothMin(sphereDist,lineDistMin,20.);
    return d;
}

float RayMarch(vec3 ro, vec3 rd){
    float dO=0.;
    for(int i=0; i<MAX_STEPS; i++){
        vec3 p = ro + rd*dO;
        float dS = min(min(min((step(0.0, rd.x) - fract(p.x)) / rd.x, (step(0.0, rd.z) - fract(p.z)) / rd.z), (step(0.0, rd.y) - fract(p.y)) / rd.y) + 0.01, GetDist(p));
        dO += dS;
        if(dO>MAX_DIST||dS<SURF_DIST)break;
    }
    return dO;
}

vec3 GetNormal(vec3 p){
    vec2 m = vec2(.01,0.);
    return normalize(vec3(
        GetDist(p+m.xyy)-GetDist(p-m.xyy),
        GetDist(p+m.yxy)-GetDist(p-m.yxy),
        GetDist(p+m.yyx)-GetDist(p-m.yyx)));
}

float GetLight(vec3 p){
    vec3 LightPos = vec3(0,10,6);
    LightPos.z += iTime*5.;
    vec3 l = normalize(LightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n,l),0.,1.);
    float d =RayMarch(p + n*SURF_DIST,l);
    if(d<length(LightPos-p)) dif *= 1.;
    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0.,0.,iTime*5.);
    vec3 rd = normalize(vec3(uv.x,uv.y,3.));
    vec3 col = vec3(0.);

    float dO = RayMarch(ro,rd);
    vec3 p = ro + rd*dO;
    float dS = GetDist(p);

    if(dS<.1){
        col = vec3(GetLight(p));
    }else{
        col = vec3(1.);
    }

    fragColor = vec4(col,1.0);
}

vec2 path(float z){
    float x = sin(z) + 2.0 * cos(z * 0.3) - 1.5 * sin(z * 0.12345);
    float y = cos(z) + 1.5 * sin(z * 0.3) + 2.0 * cos(z * 0.12345);
    return vec2(x,y);
}

float map(vec3 p){
	//p = fract(p/2.0) * 6.0 - 3.0;
    vec2 o = path(p.z) / 4.0;
	float tBase = max(-length(p.xy - o) + 1.0 
                      //* (1.0 + sin(p.z) * 0.5)
                      ,length(p.xy - o) - 1.2 
                      //* (1.0 + sin(p.z) * 0.5)
                     );
    float tdonut = max(-length(p.xy - o) + 0.5
                      ,length(p.xy - o) - 0.6 
                     );
    tdonut = max(tdonut,abs(fract(p.z) - 0.5));
    float tTube1 = length(p.xy - o + vec2(0.3,0.3)) - 0.025;
    float tTube2 = length(p.xy - o + vec2(0.5,0.2)) - 0.025;
    float tTube3 = length(p.xy - o + vec2(0.2,0.5)) - 0.025;    
    float tTube4 = length(p.xy - o + vec2(-0.3,-0.3)) - 0.025;
    float tTube5 = length(p.xy - o + vec2(-0.5,-0.2)) - 0.025;
    float tTube6 = length(p.xy - o + vec2(-0.2,-0.5)) - 0.025;
    float tTube = min(min(tTube1,tTube2),tTube3);
    tTube = min(min(tTube,tTube4),min(tTube5,tTube6));

    float bound = 2.0;
    float tSplit = (fract(p.z)-0.5);
    tSplit = min(tSplit, abs((p.x - o.x))-0.15);
    tSplit = min(tSplit, abs((p.y - o.y))-0.15);

    return min(min(max(tBase,-tSplit),tdonut),tTube);
}
const float EPS = 0.001;
vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 32; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return vec4(getNormal(p),t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float PI = 3.14159265;
    vec2 uv = fragCoord.xy /iResolution.xy;
	uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 r = normalize(vec3(uv,0.5));

    float z = iTime * 4.0 ;

    r.xy *= mat2( sin(iTime),cos(iTime),
    				-cos(iTime),sin(iTime));
	vec2 a = path(z);
    vec3 o = vec3(a / 4.0,z);
    vec4 data = trace(o,r);
    float t = data.w;
    float fog = 1.0 / (1.0 + t * t * 0.2);
    vec3 fc = mix(vec3(0.5 - data.x,0.5 -data.y,0.5-data.z),vec3(0),1.0 - fog);
	//fc = vec3(fog);
    // Output to screen
    fragColor = vec4(fc,1.0);
}

