#define M_PI 3.1415926535897932384626433832795

float weight(float dist)
{
    return pow(max(0., 1. - dist), 100.);
}

vec2 BezierDeg3(vec2 p0, vec2 p1, vec2 p2, float t)
{
    // bezier formula following bernstein Bez(t) = E(i->n)Bern(t).Pi with t=[0,1]
    // Deg3 -> Bez(t) = (1-t)Â²P0 + 2t(1-t)P1 +tÂ²P2
    return p0*pow(1.-t, 2.) + p1*2.*t*(1.-t) + p2*t*t;
}

// Cardano's method : https://en.wikipedia.org/wiki/Cubic_function#Cardano's_method
vec3 Cardan(float p, float q)
{
    float p3 = p*p*p;
    float D = - (4.*p3+27.*q*q);
    if (D > 0.)
    {
        float a = 2.*sqrt(-p/3.);
        float b = acos(sqrt(27./(-p3))*-q/2.)/3.;
        return vec3(a*cos(b), a*cos(b+2.*M_PI/3.), a*cos(b+4.*M_PI/3.));
    }
    else if (D < 0.)
    {
        float coef = 1./3.;
        float DD = sqrt(-D/27.);
        float u = (-q+DD)/2.;
        u = sign(u)*abs(pow(abs(u), 1./3.));
        float v = (-q-DD)/2.;
        v = sign(v)*pow(abs(v), 1./3.);
        return vec3(u + v);
    }
    else
    {
        if (p == 0. || q == 0.)
            return vec3(0.);
        float r = 3.*q/p;
        float r2 = -3.*q / (2.*p);
        return vec3(r, r2, r2);
    }
}

// Resolve cubic equation
vec3 resolveEqDeg3(float a, float b, float c, float d)
{
    vec3 A = vec3(b, c, d) / a;
    float p = A.y - A.x*A.x/3.;
    float q = A.x*(2.*A.x*A.x-9.*A.y)/27.+A.z;
    return Cardan(p, q) - (b / (3.*a));

}

// Main element to bezier curve SDF
float dBezier(vec2 pos, vec2 p0, vec2 p1, vec2 p2)
{
    // dP/dt(t) = 2(A+Bt)
    // simplified from 
    // dP/dt(t) = -2(1-t)P0 + 2(1-2t)P1 + 2tP2
    vec2 A = p1-p0;
    vec2 B = p2 - p1 - A;

    // search for equation
    // MP.dP/dt = 0
    vec2 M = p0 - pos;
    vec3 t = resolveEqDeg3(dot(B, B), dot(A, B)*3., dot(A,A)*2.+dot(M,B), dot(M,A));

    float d = 1000.;

    if (t.x >= 0. && t.x <= 1.)
        d = min(d, distance(pos, BezierDeg3(p0, p1, p2, t.x)));
	if (t.y >= 0. && t.y <= 1.)
        d = min(d, distance(pos, BezierDeg3(p0, p1, p2, t.y)));
    if (t.z >= 0. && t.z <= 1.)
        d = min(d, distance(pos, BezierDeg3(p0, p1, p2, t.z)));
    return d;
}

// signed function to a quadratic bezier curve
float dBezierCorrect(vec2 uv, vec2 p0, vec2 p1, vec2 p2)
{
    float d = distance(p0,uv);
    d = min (d, distance(p2,uv));
    d = min (d, dBezier(uv, p0, p1, p2));
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    vec2 p0 = vec2(1.3,0.9)*cos(iTime*1.1 + vec2(0.0,5.0) );
    vec2 p1 = vec2(1.3,0.9)*cos(iTime*1.2 + vec2(3.0,4.0) );
    vec2 p2 = vec2(1.3,0.9)*cos(iTime*1.3 + vec2(2.0,1.0) );

    float d = dBezierCorrect(uv, p0, p1, p2);

    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-4.0*abs(d));
	col *= 0.8 + 0.2*cos(140.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );


    fragColor = vec4(col, 1.);
}

#define AA 3

// Constants
const float pi = 3.14159265358979323;
const float th = pi / 4.;
const mat2 m = mat2(cos(th), sin(th), -sin(th), cos(th));
const float rs2 = 1. / sqrt(2.);

// polynomial smooth min
// from iq: https://www.iquilezles.org/www/articles/smin/smin.htm
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

// Jigsaw pattern function, returns random piece colour in RGB and SDF in A.
vec4 jigsaw(vec2 uv)
{
    vec2 uv2 = m * (uv - .5) * rs2 * 2.;

    vec2 c = floor(uv), f = uv - c - .5;
    vec2 c2 = floor(uv2), f2 = uv2 - c2;

    bool flip = texelFetch(iChannel0, ivec2(c2) & 255, 0).r > .5;

    vec2 mdir = step(abs(f).yx, abs(f)) * sign(f);

    vec2 f2b = m * (f2 - .5);

    if(mod(c2.x + c2.y, 2.) > .5)
        f2b = vec2(-f2b.y, f2b.x);

    // SDF
    float d = smin(flip ? -f2b.y : f2b.y, length(f2b - vec2(0, flip ? -.1 : .1)) - .25, 1. / 32.);

    if(mod(c2.x, 2.) < .5 != flip != mod(c.x + c.y, 2.) > .5)
        d = -d;

    if(d < 0.)
    	c += mdir;

    return vec4(texelFetch(iChannel1, ivec2(c) & 255, 0).rgb, abs(d));
}

float hmap(float h)
{
    return sqrt(smoothstep(0., .07, h));
}

void render( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2. - 1.;
	uv.x *= iResolution.x / iResolution.y;

    uv *= 3.;

    float th = sin(iTime / 10.);

    // Animation
    uv = mat2(cos(th), sin(th), -sin(th), cos(th)) * uv;
    uv *= 1. + .1 * sin(iTime);
    uv.y += cos(iTime / 6.) * 3.;

    vec2 e = vec2(1e-3, 0);

    vec4 jig0 = jigsaw(uv);

    // Extract gradient for shading
    float h0 = hmap(jigsaw(uv + e.xy).a);
    float h1 = hmap(jigsaw(uv - e.xy).a);
    float h2 = hmap(jigsaw(uv + e.yx).a);
    float h3 = hmap(jigsaw(uv - e.yx).a);

    vec3 n = normalize(vec3(h0 - h1, e.x * 8., h3 - h2));

    vec3 col;
    vec2 tuv = uv;

    if(jig0.b > .5)
        tuv = tuv.yx;

    // Shading
    col = .05 + .95 * textureLod(iChannel2, jig0.rg, 0.).rgb;
    vec3 w = textureLod(iChannel3, tuv / 2. + jig0.rg, 1.).rrr;
    col *= smoothstep(-.1, 1., w);

    // Increase saturation
    float b = dot(col, vec3(1. / 3.));
    col = vec3(b) + (col - b) * 2.;

    fragColor.rgb = col * (.5 + .5 * dot(n, normalize(vec3(1)))) *
        		mix(.1, 1., smoothstep(0., .04, abs(jig0.a)));

    fragColor.rgb += pow(w.x, 2.) * pow(clamp(dot(n, normalize(vec3(1))), 0., 1.), 16.) * 1.;    

    if(iMouse.z > .5)
    {
        // Pattern structure visualisation
        fragColor.rgb /= 2.;
        float mask0 = step(min(fract(uv.x), fract(uv.y)), .03);
    	vec2 uv2 = m * (uv - .5) * rs2 * 2.;
        float mask1 = step(min(fract(uv2.x), fract(uv2.y)), .03 / rs2);
        fragColor.rgb = mix(fragColor.rgb, vec3(1, .2, 0), step(jig0.a, .02));
        fragColor.rgb = mix(fragColor.rgb, vec3(1, 1, 0), mask1);
        fragColor.rgb = mix(fragColor.rgb, vec3(0, .5, 1), mask0);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor.a = 1.;
    fragColor.rgb = vec3(0);

    // Anti-aliasing loop
    for(int y = 0; y < AA; ++y)
        for(int x = 0; x < AA; ++x)
        {
            vec4 rc;
            render(rc, fragCoord + vec2(x, y) / float(AA));
            fragColor.rgb += clamp(rc.rgb, 0., 1.);
        }

    fragColor.rgb /= float(AA * AA);

    fragColor.rgb = fragColor.rgb / (fragColor.rgb + 1.)  * 2.5;

    fragColor.rgb = pow(clamp(fragColor.rgb, 0., 1.), vec3(1. / 2.2));
}

#define FALLOFF_START 1.0
#define DECAY_START 0.1
#define DECAY 1.0

#define GRIDW 0.02

#define GRID_R 0.672443156957688
#define GRID_G 0.0103298230296269
#define GRID_B 0.246201326707835
#define FALLOFF_GRID 0.75

//#define GRID_R 0.0544802764424424
//#define GRID_G 0.0
//#define GRID_B 0.0761853814813079

float sRGB(float x) {
    if (x <= 0.00031308)
        return 12.92 * x;
    else
        return 1.055*pow(x,(1.0 / 2.4) ) - 0.055;
}

float saw(float t) {
 	return t - floor(t);   
}

float tri(float t) {
    return 2.0 * abs(t - floor(t + 0.5));
}

float grid(vec2 pix, float t) {

    float d = t + (pix.y / 30.0) + sin(pix.x * 500.0);

    float distortion = (sin(d * 400.0) + (sin(d * 600.0) / 2.0) + (sin(d * 800.0) / 3.0)) / 4.0;

    //float w = max(0.0, tri((pix.x + distortion) / 8.0) - (1.0 - GRIDW)) / GRIDW;
    //float h = max(0.0, tri((pix.y + distortion) / 8.0) - (1.0 - GRIDW)) / GRIDW;

    float w = tri((pix.x + distortion) / 50.0);
    float h = tri((pix.y + distortion) / 50.0);


    float power = 1.0 + 0.25 * tri(pix.y / 50.0 + t / 6.0);

    float falloff = power * FALLOFF_GRID;

    float dist = min(w, h) * 25.0;



    //return min(power, max(w, h)
    return min(power, power * (falloff * falloff) / (dist * dist));


    //return w;
}

float electron_beam(vec2 pix, float t) {


    float beam_x = (cos(t * 1.0) * 250.0 + 1.0) / 2.0;
    float beam_y = (sin(t * 10.0 / 9.0) * 200.0 + 1.0) / 2.0;

    float dist = distance(pix, vec2(beam_x, beam_y));

    float power = 1.0 - (min(iTime - t, DECAY) / DECAY);

    float falloff = power * FALLOFF_START;

    return min(power, power * (falloff * falloff) / (dist * dist));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{



    vec2 center = iResolution.xy / 2.0;

    float scale = 512.0 / iResolution.x;

    vec2 uv = (fragCoord - center) * scale;



    float beam = 0.0;

    float t = 0.0;
    for (int i = 0; i < 100; i++) {
        beam += electron_beam(uv, iTime - t);
        t += 0.01;
    }

    vec2 uv_r = uv - vec2(sin(iTime / 5.0 + uv.y / 100.0) * 2.0, sin(uv.x / 75.0 + iTime / 8.0));
    vec2 uv_g = uv;
    vec2 uv_b = uv + vec2(sin(iTime / 4.0 + uv.y / 100.0) * 2.0, cos(uv.x / 50.0 + iTime / 4.0));

    float grid_r = grid(uv_r, iTime) * GRID_R;
    float grid_g = grid(uv_g, iTime - 0.1) * GRID_G;
    float grid_b = grid(uv_b, iTime + 0.1) * GRID_B;

    vec3 grid_col = vec3(sRGB(grid_r), sRGB(grid_g), sRGB(grid_b));
    vec3 beam_col = vec3(sRGB(beam * 0.25), sRGB(beam), sRGB(beam));

    fragColor = vec4(grid_col + beam_col, 1.0);
}

