float length2( vec2 v ) { return dot(v,v); }

float segment_dis_sq( vec2 p, vec2 a, vec2 b ){
	vec2 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length2( pa - ba*h );
}

float segment_param( vec2 p, vec2 a, vec2 b ){
	vec2 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return h;
}

float parametric_segments_dis_sq(vec2 uv){
    float d0 = 1e38;
    vec2 a = parametric(0.);
    for( int i=1; i<num_segments; i++ )
    {
        float t = float(i)/float(num_segments-1);
		vec2 b = parametric(t);
        d0 = min(d0,segment_dis_sq(uv, a, b ));
        a = b;
    }

    return d0;
}

float parametric_segments_dis(vec2 uv){
	return sqrt(parametric_segments_dis_sq(uv));
}

float parametric_normal_iteration(float t, vec2 uv){
	vec2 uv_to_p=parametric(t)-uv;
	vec2 tang=parametric_diff(t);

	float l_tang=dot(tang,tang);
	return t-factor*dot(tang,uv_to_p)/l_tang;
}

float parametric_normal_iteration2(float t, vec2 uv){
	vec2 uv_to_p=parametric(t)-uv;
	vec2 tang=parametric_diff(t);
	vec2 snd_drv=parametric_snd_diff(t);

	float l_tang=dot(tang,tang);

	float fac=dot(tang,snd_drv)/(2.*l_tang);
	float d=-dot(tang,uv_to_p);

	float t2=d/(l_tang+fac*d);

	return t+factor*t2;
}

float parametric_dis_approx_sq(vec2 uv){
	float d0 = 1e38;

	float t0=0.;
	float t;

	for(int i=0;i<num_start_params;i++){
		t=t0;
		for(int j=0;j<num_iterations;j++){
			#ifdef SECOND_ORDER
			t=parametric_normal_iteration2(t,uv);
			#else
			t=parametric_normal_iteration(t,uv);
			#endif
		}
		t=clamp(t,0.,1.);
		vec2 uv_to_p=parametric(t)-uv;
		d0=min(d0,dot(uv_to_p,uv_to_p));

		t0+=1./float(num_start_params-1);
	}

	return d0;
}

float parametric_dis_approx_sq2(vec2 uv){
	float d0 = 1e38;
	float d1 = 1e38;

	float t0=0.;

	float[6] nt;
	float[6] nd=float[6](1e38,1e38,1e38,1e38,1e38,1e38);

	for(int i=0;i<num_start_params2;i++){
		vec2 uv_to_p=parametric(t0)-uv;
		d1=dot(uv_to_p,uv_to_p);
		//d1=abs(dot(uv_to_p,parametric_diff(t0)));

		if(d1<nd[2]){
			if(d1<nd[0]){
				nd[5]=nd[4];
				nt[5]=nt[4];

				nd[4]=nd[3];
				nt[4]=nt[3];

				nd[3]=nd[2];
				nt[3]=nt[2];

				nd[3]=nd[2];
				nt[3]=nt[2];

				nd[2]=nd[1];
				nt[2]=nt[1];

				nd[1]=nd[0];
				nt[1]=nt[0];

				nd[0]=d1;
				nt[0]=t0;
			}
			else if(d1<nd[1]){
				nd[5]=nd[4];
				nt[5]=nt[4];

				nd[4]=nd[3];
				nt[4]=nt[3];

				nd[3]=nd[2];
				nt[3]=nt[2];

				nd[2]=nd[1];
				nt[2]=nt[1];

				nd[1]=d1;
				nt[1]=t0;
			}
			else{
				nd[5]=nd[4];
				nt[5]=nt[4];

				nd[4]=nd[3];
				nt[4]=nt[3];

				nd[3]=nd[2];
				nt[3]=nt[2];

				nd[2]=d1;
				nt[2]=t0;
			}
		}
		else{
			if(d1<nd[3]){
				nd[5]=nd[4];
				nt[5]=nt[4];

				nd[4]=nd[3];
				nt[4]=nt[3];

				nd[3]=d1;
				nt[3]=t0;
			}
			else if(d1<nd[4]){
				nd[5]=nd[4];
				nt[5]=nt[4];

				nd[4]=d1;
				nd[4]=t0;
			}
			else if(d1<nd[5]){
				nd[5]=d1;
				nt[5]=t0;
			}
		}

		t0+=1./float(num_start_params2-1);
	}

	d0=1e38;

	for(int i=0;i<num_mins;i++){
		for(int j=0;j<num_iterations2;j++){
			#ifdef SECOND_ORDER
			nt[i]=parametric_normal_iteration2(nt[i],uv);
			#else
			nt[i]=parametric_normal_iteration(nt[i],uv);
			#endif
		}
		nt[i]=clamp(nt[i],0.,1.);

		vec2 uv_to_p=parametric(nt[i])-uv;
		d0=min(d0,dot(uv_to_p,uv_to_p));
	}

	return d0;
}

