	vec2 uv=gl_FragCoord.xy/iResolution.xy;
	uv-=.5;
    uv.y*=iResolution.y/iResolution.x;

	vec2 mouse=iMouse.xy/iResolution.xy;
	mouse-=.5;
    mouse.y*=iResolution.y/iResolution.x;

	border*=zoom;
	uv*=zoom;
	mouse*=zoom;

	vec3 curve_col=vec3(1,1,1);

	#ifdef HYBRID
	float dis=parametric_dis_approx_hybrid(uv);
	#elifdef SEGMENTS
	float dis=parametric_segments_dis(uv);
	#else
	#ifdef USE_VARIANT
	float dis=parametric_dis_approx2(uv);
	#else
	float dis=parametric_dis_approx(uv);
	#endif
	#endif

	#ifdef SHOW_DISTANCE_FIELD
	float sgn=1.;
	dis/=zoom;
	//iq's sd color scheme
	vec3 color = vec3(1) - sgn*vec3(0.1,0.4,0.7);
	color *= 1.0 - exp(-8.0*dis);
	color *= 0.8 + 0.2*cos(480.0*dis*sgn);
	color = mix( color, curve_col, 1.0-smoothstep(0.0,0.005,dis) );
	#else
	vec3 color=bg_col;
	color=mix(line_col,color,smoothstep(0.,border,dis-line_size));
	#endif

	float t0=mod(iTime*.05,1.);
	vec2 p0=parametric(t0);

	dis=distance(uv,p0)-dot_size;
	color=mix(point_col,color,smoothstep(0.,border,dis));

	fragColor=vec4(color,1);
}

float map(vec3 p){
    p = fract(p) * 2.0 - 1.0;
    p.xz *= mat2(cos(iTime),-sin(iTime)
             ,sin(iTime),cos(iTime));
        p.yz *= mat2(cos(1.0),-sin(1.0)
             ,sin(1.0),cos(1.0));
    float cil_side = length(p.xz) - abs(sin(iTime))/1.0;
    float cil_top_bottom = abs(p.y) - abs(cos(iTime)) * 2.0;
    float cil = max(cil_side,cil_top_bottom);
    float holes = 10000000.0;
    float d1 = abs(sin(iTime));
    float d2 = abs(cos(iTime));
    for(float xi = -10.0; xi < 10.0; xi++){
        for(float yi = -10.0; yi < 10.0; yi++){
        	float h1 = length(p.xy - (xi * 0.4*d1,yi * 0.4*d2)) -0.1 * d1;
            holes = min(holes,h1);
        }
    }
        p.yz *= mat2(cos(iTime),-sin(iTime)
             ,sin(iTime),cos(iTime));
    for(float xi = -10.0; xi < 10.0; xi++){
        for(float yi = -10.0; yi < 10.0; yi++){
        	float h1 = length(p.xy - (xi * 0.4*d1,yi * 0.4*d2)) -0.1 * d1;
            holes = min(holes,h1);
        }
    }
        p.xy *= mat2(cos(iTime),-sin(iTime)
             ,sin(iTime),cos(iTime));
    for(float xi = -10.0; xi < 10.0; xi++){
        for(float yi = -10.0; yi < 10.0; yi++){
        	float h1 = length(p.xy - (xi * 0.4*d1,yi * 0.4*d2)) -0.1 * d1;
            holes = min(holes,h1);
        }
    }
    float sph = length(p) - 10.5;
	return max(holes,sph);
}

float trace (vec3 o, vec3 r){
	float t = 0.0;
    for(int i = 0; i < 32; ++i){
        vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float PI = 3.14159265;
    vec2 uv = fragCoord.xy /iResolution.xy;
	uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 r = normalize(vec3(uv,1.0));

    vec3 o = vec3(0,0,-2.0);
    float t = trace(o,r);
    float fog = 1.0 / (1.0 + t * t * 0.2);
    vec3 fc = vec3(fog);

    // Output to screen
    fragColor = vec4(fc,1.0);
}

