float sphereSDF(vec2 p, float size) {
	return length(p) - size;
}

float boxSDF(vec2 p, vec2 size) {
	vec2 r = abs(p) - size;
    return min(max(r.x, r.y),0.) + length(max(r,vec2(0,0)));
}

void addObj(inout float dist, inout vec3 color, float d, vec3 c) {
    if (dist > d) {
        dist = d;
        color = c;
    }
}

void scene(in vec2 pos, out vec3 color, out float dist) {
    dist = 1e9; color = vec3(0,0,0);
    addObj(dist, color, boxSDF(pos - vec2(-4,1), vec2(1,1)), 	vec3(-.6,.8,1.0));
    addObj(dist, color, sphereSDF(pos - vec2(4,1), 0.5), 		vec3(1, 0.9, 2.8));
    addObj(dist, color, boxSDF(pos - vec2(0,3.0*sin(iTime)), 	vec2(1.0, 3.5)), vec3(.4,.1,.1));
}

void trace(vec2 p, vec2 dir, out vec3 c) {
    for (int j = 0; j < 20; j++) {
        float d;
        scene(p, c, d);
        if (d < 1e-3) {
            //c = vec3(0,.1,0);
            return;
        }
        if (d > 1e1) break;
        p -= dir * d;
    }
    c = vec3(0,0,0);
}

float random (in vec2 _st) {
    return fract(sin(dot(_st.xy,
        vec2(12.9898,78.233)))*
        43758.5453123);
}


#define SAMPLES 128


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;
    vec2 uv = (gl_FragCoord.xy-(iResolution.xy/2.0))/iResolution.y*10.0;

    vec3 col = vec3(0,0,0);
    for (int i = 0; i < SAMPLES; i++) {
        float t = (float(i) + random(uv+float(i))) / float(SAMPLES) * 2. * 3.1415;
        vec3 c;
        trace(uv, vec2(cos(t), sin(t)), c);
        col += c;
    }
    col /= float(SAMPLES);

    // Output to screen
    fragColor = vec4(col*2.0,3.0);
}

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01


//Shapes

    float torus( vec3 p, vec2 t )
	{
  		vec2 q = vec2(length(p.xz)-t.x,p.y);
  		return length(q)-t.y;
	}


//Shape unions

float opUnion( float d1, float d2 ) //Merges two objects
{
    return min(d1,d2);			
}

float opSmoothUnion( float d1, float d2, float k )  //smooth merges two objects
{
	float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
	return mix( d2, d1, h ) - k*h*(1.0-h);


}

float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); }

float opSmoothIntersection( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h); }


// Distance calculations

float GetDist(vec3 p)// function for calculating distance. This is where the atual objects are coded
{

    vec3 objP1 = vec3(0, sin(iTime), 6);


    vec4 s = vec4(objP1, 1); //this genirates a sphere (x,y,z,radius)

	float t1 = torus(objP1, vec2(0.5,1.));


    float sphereDist = length(p-s.xyz)-s.w; // length from origin is ray position - the pos of the shpere - the radious of the sphere
    float planeDist = p.y; // this genirates the floor plane. it is calculated by seeing how high the ray is from the ground.



    float shapeMix = opSmoothUnion(sphereDist, planeDist, 1.0);

    float shapeMix2 = opSmoothUnion(shapeMix, t1, 1.0);

	float d = min(t1, planeDist); // which object is the ray closer to? ** you need to add any other objects in the scene to this line **

    return shapeMix2;
}

float RayMarch(vec3 ro, vec3 rd)
{
 	float dO=0.0; // dO is the distance to the origin

    for(int i=0; i<MAX_STEPS; i++)
    {
        vec3 p = ro + rd*dO; //current ray postion. Move in the direction of the ray (rd), by the distance the object is from the origin (dO or camera)
        float ds = GetDist(p); //calculates distance the currenmt ray position is from the object
        dO += ds;			//moves the current ray position by the distance it is from the object
        if(dO>MAX_DIST || ds<SURF_DIST) break; // has the ray position moved past the max distance, or gotten close enough to the object to stop?

    }

    return dO;

}

vec3 GetNormal(vec3 p)
{
 	float d = GetDist(p);
    vec2 e = vec2( 0.1,0);

    vec3 n = d - vec3        //this moves 0.1 in eash direction from the point and takes a distance sample used to calculate the slope of the surface
        (
            GetDist(p-e.xyy),
            GetDist(p-e.yxy),
            GetDist(p-e.yyx)
        );

    return normalize(n);
}

float GetLight(vec3 p)
{
 	vec3 lightPos = vec3(0.,5,3); //postion of the light source
    lightPos.xy += vec2(sin(iTime), cos(iTime))*2.;
 	vec3 l = normalize(lightPos-p); //at direction is the surface from the light source
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l),0.,1.); //diffrence in angle between the normal and light vectors. clamps to 0 and 1 to prevent neg numbers

    //Shadows
    float d = RayMarch(p+n*SURF_DIST*2., l);    //raymarch from the point towards the light. the n*SURF_DIST moves it just far enough away from the surface so that it does not stop the march due to already being at a surface.
    if(d<length(lightPos-p))	//Does the ray hit an object before hiting the light?
    	{
            dif *= 0.1;			//If so make the point 10 times darker.
        }

    return dif;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    //Camera
    vec3 ro = vec3(0,1,0); //Ray origin 
    vec3 rd = normalize(vec3(uv.x, uv.y, 1)); //Ray direction. Normalize function returns the direction with a lenth of 1

    float d = RayMarch(ro,rd);

    vec3 p = ro + rd * d; //location that the raymarch function determined is the surface

    float dif = GetLight(p);// dif mean defused light

//    dif /=6.0;  //brings the distance value into the visable range?? used for testing REMOVE
	col = vec3(dif);

    fragColor = vec4(col,1.0);
}

//afl_ext 2017 

// its from here https://github.com/achlubek/venginenative/blob/master/shaders/include/WaterHeight.glsl 
float wave(vec2 uv, vec2 emitter, float speed, float phase){
	float dst = distance(uv, emitter);
	return pow((0.5 + 0.5 * sin(dst * phase - iTime * speed)), 5.0);
}

#define GOLDEN_ANGLE_RADIAN 2.39996
float getwaves(vec2 uv){
	float w = 0.0;
	float sw = 0.0;
	float iter = 0.0;
	float ww = 1.0;
    uv += iTime * 0.5;
	// it seems its absolutely fastest way for water height function that looks real
	for(int i=0;i<6;i++){
		w += ww * wave(uv * 0.06 , vec2(sin(iter), cos(iter)) * 10.0, 2.0 + iter * 0.08, 2.0 + iter * 3.0);
		sw += ww;
		ww = mix(ww, 0.0115, 0.4);
		iter += GOLDEN_ANGLE_RADIAN;
	}

	return w / sw;
}
float getwavesHI(vec2 uv){
	float w = 0.0;
	float sw = 0.0;
	float iter = 0.0;
	float ww = 1.0;
    uv += iTime * 0.5;
	// it seems its absolutely fastest way for water height function that looks real
	for(int i=0;i<24;i++){
		w += ww * wave(uv * 0.06 , vec2(sin(iter), cos(iter)) * 10.0, 2.0 + iter * 0.08, 2.0 + iter * 3.0);
		sw += ww;
		ww = mix(ww, 0.0115, 0.4);
		iter += GOLDEN_ANGLE_RADIAN;
	}

	return w / sw;
}

float H = 0.0;
vec3 normal(vec2 pos, float e, float depth){
    vec2 ex = vec2(e, 0);
    H = getwavesHI(pos.xy) * depth;
    vec3 a = vec3(pos.x, H, pos.y);
    return normalize(cross(normalize(a-vec3(pos.x - e, getwavesHI(pos.xy - ex.xy) * depth, pos.y)), 
                           normalize(a-vec3(pos.x, getwavesHI(pos.xy + ex.yx) * depth, pos.y + e))));
}
mat3 rotmat(vec3 axis, float angle)
{
	axis = normalize(axis);
	float s = sin(angle);
	float c = cos(angle);
	float oc = 1.0 - c;

	return mat3(oc * axis.x * axis.x + c,           oc * axis.x * axis.y - axis.z * s,  oc * axis.z * axis.x + axis.y * s, 
	oc * axis.x * axis.y + axis.z * s,  oc * axis.y * axis.y + c,           oc * axis.y * axis.z - axis.x * s, 
	oc * axis.z * axis.x - axis.y * s,  oc * axis.y * axis.z + axis.x * s,  oc * axis.z * axis.z + c);
}
