    float walls = 10000.0;
    for(float zi = 0.5; zi < 15.0; zi += 0.5){
        p.xy *= genRotMat(0.5 * PI /12.0 );
    	walls = min(walls,
                    wall(beginZ + zi,p,
                         pow(0.2/((beginZ + zi - time(1.0)) + 0.3),2.0)
                        )
                   );
    }
    return walls;
}

