#define fGlobalTime iTime

// rotation matrice function
mat2 rot (float a)
{return mat2 (cos(a), sin(a), -sin(a),cos(a));}

// split the screen in two in the x axis
float banding (vec2 uv)
{
    // 3 ways of doing it:
    // 1.
    //if(uv.x < 0.) return 0.;
    //else return 1.;
	// 2.
    //return (uv.x<0.) ? 0.: 1.;
	// 3.
    return smoothstep(0., 0.1,uv.x);

}

float triangle (vec2 uv, float size)
{
    // symmetry in the y axis
    uv.x = abs(uv.x);
    return step(max(-uv.y,dot(uv,vec2(1.,0.6))), size);
}

float square (vec2 uv, float size)
{
    uv = abs(uv);
    return step(max(uv.x, uv.y),size);
}


float circle (vec2 uv, float size)
{return step(length(uv), size);}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.*(fragCoord/iResolution.xy)-1.;
    uv.x /= iResolution.y / iResolution.x;
	// scale the uv
    uv *= 5.;
    // repeating the uv into a grid in 2 ways
    // 1.
    //vec2 guv = mod(uv,1)-.5;
    // 2.
    vec2 guv = fract(uv)-.5;
    // id of each cells
    vec2 id = floor(uv);
    guv *= rot(fGlobalTime*(length(id)+0.8)*0.3);

    float b = banding(uv);
    // clamp the value is a security if you want to use those shapes as 
    // mask for example
    float t = clamp(triangle(guv,0.15)-triangle(guv,0.05),0.,1.);
    float s = clamp(square(guv, 0.35) - square(guv, 0.31),0.,1.);
    float c = clamp(circle(uv,.8) - circle(uv,.7), 0., 1.);

    // also necessary here because when the square and triangle intersect
    // the color value is 2
    vec3 col = clamp(vec3(s+t),0.,1.); 

    fragColor = vec4(col,1.);
}

float tt, g;

mat2 rotate(float a) {
  return mat2(cos(a), -sin(a), sin(a), cos(a));
}

float smin( float a, float b, float k ) {
  float h = max( k-abs(a-b), 0.0 )/k;
  return min( a, b ) - h*h*h*k*(1.0/6.0);
}

float sphereSDF (vec3 p, vec3 c, float r) {
  return length(c - p) - r;
}

float cubeSDF (vec3 p, vec3 c, vec3 dimensions, float borderRoundness) {
  vec3 pos = abs(c - p) - dimensions;
  return length(max(pos, 0.)) - borderRoundness + min(max(pos.x, max(pos.y, pos.z)), 0.);
}

vec2 map (vec3 p) {
  //fractal

  float n = sin(tt * 6.) * .05 + .05;
  for (int i=0; i<2; i++) {
    p = abs(p) - vec3(4., 3., 5.);
    //p = abs(p) - vec3(2., 1., 3.);

    p.xz *= rotate(sin(tt - p.y + p.x) * n + .6);
    p.yz *= rotate(cos(tt + p.x) * (n * .7) + 5.8);
    p.xy *= rotate(cos(tt + p.z) * (n * 1.2) + 2.);
    //p += length(p.yx * sin(tt) * .2 + .4) * .5;
  }

  p *= .7; // to scale fractal

  // primitives

  float c1 = cubeSDF(abs(p) - vec3(length(p.yz) + .2, .1, .8), vec3(0.), vec3(.5, .2, .8), .05);
  c1 += sin(p.x * 4.) * cos(p.y * 6. + sin(tt)) * sin(p.z * 3.) * .3;
  float c2 = cubeSDF(abs(p) - vec3(.5, .3, 2.), vec3(0.), vec3(.3, .1, .6), .01);

  vec2 t = vec2(smin(c1 * .1, c2 * .05, .5), 2.);

  p += sin(length(p) * 22. + tt * .2) * .04;
  float c3 = cubeSDF(abs(p) - vec3(.3, .2, .2), vec3(2.), vec3(.2, .4, .3), .03) * .3;  
  vec2 h = vec2(smin(c3 * .2, c2 * .3, 1.2), .1);
  t = (t.x < h.x) ? t : h; // merge materials

  return t / .8;
}

vec2 trace (vec3 ro, vec3 rd) {
  const float MAX_DEPTH = 50.;
  vec2 h, t = vec2(.1);

  for (int i = 0; i < 268; i++) {
    h = map(ro + rd * t.x);
    if (h.x < .00001 || t.x > MAX_DEPTH) break;
    t.x += h.x; t.y = h.y;
  }
  if (t.x > MAX_DEPTH) t.x = 0.;
  return t;
}

vec3 getNormal (vec3 p) {
  float d = map(p).x;
  vec2 e = vec2(.01, 0.);

  return normalize(d - vec3(
    map(p - e.xyy).x,
    map(p - e.yxy).x,
    map(p - e.yyx).x));
}

struct Material {
  float ambient;
  float diffuse;
  float specular;
};

float getLight (vec3 lightPos, vec3 p, vec3 rd, float lightOcclusion, Material material) {
  // https://www.shadertoy.com/view/ll2GW1
  vec3 light = normalize(lightPos - p);
  vec3 normal = getNormal(p);

  // phong reflection
  float ambient = clamp(.5 + .5 * normal.y, 0., 1.);
  float diffuse = clamp(dot(normal, light), 0., 1.);
  vec3 half_way = normalize(-rd + light);
  float specular = pow(clamp(dot(half_way, normal), 0.0, 1.0), 16.);

  return (ambient * material.ambient * lightOcclusion) +
   (diffuse * material.diffuse * lightOcclusion) +
   (diffuse * specular * material.specular * lightOcclusion);
}

vec3 getRayDirection (vec2 uv, vec3 rayOrigin, vec3 lookat, float zoom) {
  // https://www.youtube.com/watch?v=PBxuVlp7nuM
  vec3 forward = normalize(lookat - rayOrigin);
  vec3 right = normalize(cross(vec3(0., 1., 0.), forward));
  vec3 up = cross(forward, right);
  vec3 center = rayOrigin + forward * zoom;
  vec3 intersection = center + uv.x * right + uv.y * up;
  return normalize(intersection - rayOrigin);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
  uv.x *= iResolution.x / iResolution.y;
  tt = mod(iTime * .1, 100.);

  // camera
  vec3 ro = vec3(0., 0., 30. + sin(tt * 1.2) * 10.);
  //vec3 ro = vec3(0., 0., 35.);
  ro.xz *= rotate(tt * .25);
  ro.yz *= rotate(tt * .5);
  vec3 rd = getRayDirection(uv, ro, vec3(0.), 2.);

  // color, fog and light direction
  //vec3 ld = normalize(vec3(.5, .8, .5));
  vec3 ld = vec3(5., 8., 3.);
  vec3 fogColor = vec3(.1, .4, .5);
  //vec3 fogColor = vec3(.2, .3, .8) * .1;
  vec3 fog = fogColor * (.5 + (length(uv) - .3));
  vec3 color = fog;

  // scene
  vec2 sc = trace(ro, rd);
  float t = sc.x;

  if (t > 0.) {
    vec3 p = ro + rd * t;
    vec3 normal = getNormal(p);
    vec3 albido = vec3(.2, .3, .8) * 2.;//base color

    if (sc.y > .9) {
      albido = vec3(.8, .2, .3) * 2.;
      color = getLight(ld, p, rd, 1.2, Material(0., .8, 1.)) * vec3(.3, .5, .2) * 2.;
      color *= albido * 2.;
    } else {
      color = getLight(ld, p, rd, .5, Material(.7, .3, 4.5)) * vec3(.3, .5, .2) * 2.;
      color /= albido * 3.;
    }

    // lightning
    ld.xz *= rotate(tt * 10.);
    color = mix(color, getLight(ro, p, rd, .5, Material(0., .7, 5.2)) * albido, .4);

    color = mix(color, fog, 1. - exp(-.00006*t*t*t)); //gradient
  } 

  fragColor = vec4(pow(color, vec3(.45)), 1.);
}



void mainImage(out vec4 FragColor, in vec2 FragCoord){
    vec2 uv = FragCoord.xy/iResolution.xy;
    uv = 2.*uv - 1.;
    uv.x *= iResolution.x / iResolution.y;


    float da = iTime/(5.);
    vec2 pts = vec2(sin(da*2.), cos(da*2.));

    float d = length(cos(  abs((abs(uv)+atan(da))*pts))*sin(atan(abs(uv))));

    d = atan(d) - cos(da+d*d)*sin(da - d*d);
    d -= atan(cos(da)*abs(  fract(d*d)-da) + 0.5);

    d -= sin(d)*abs(uv.x*uv.y);
    d *= length(d + abs(uv.y));

    float fill = step(tan(fract(abs(d)-0.5)*da),0.2);


    vec3 col = vec3(fill - sin(d-da ));



    col += vec3(0., 0.2, 1.);
    col *= vec3(1., 0.1, 0.2);
    FragColor = vec4(col, 1.);
	//da += uv.x;
}

