float mandelbrot(vec2 c)
{
    vec2 z = vec2(-.3, .0);
    for(float i = 0.; i < N; i++)
    {        
        if(length(z)>2.)
            return i;
     	else 
        	z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y)+c;
    }
    return N;
}

