    Triangle[i].material.k_rg = 0.5 * Triangle[i].material.k_r;
    Triangle[i].material.n = 128.0;
    vec3 A = Triangle[i].A;
    vec3 B = Triangle[i].B;
    vec3 C = Triangle[i].C;
