    Triangle[i].aabb.A = vec3(getMin(A[0],B[0],C[0]),getMin(A[1],B[1],C[1]),getMin(A[2],B[2],C[2]));
    Triangle[i].aabb.B = vec3(getMax(A[0],B[0],C[0]),getMax(A[1],B[1],C[1]),getMax(A[2],B[2],C[2]));
}
/////////////////////////////////////////////////////////////////////////////
// Initializes a box.
/////////////////////////////////////////////////////////////////////////////
void InitBox(in int i){
    Box[i].debug = false;
    float x0 = min(Box[i].cornerA.x, Box[i].cornerB.x);
    float x1 = max(Box[i].cornerA.x, Box[i].cornerB.x);
    float y0 = min(Box[i].cornerA.y, Box[i].cornerB.y);
    float y1 = max(Box[i].cornerA.y, Box[i].cornerB.y);
    float z0 = min(Box[i].cornerA.z, Box[i].cornerB.z);
    float z1 = max(Box[i].cornerA.z, Box[i].cornerB.z);
    Box[i].aabb.A = vec3(x0,y0,z0);
    Box[i].aabb.B = vec3(x1,y1,z1);
    //front
    Box[i].xys[0].x0 = x0;
    Box[i].xys[0].x1 = x1;
    Box[i].xys[0].y0 = y0;
    Box[i].xys[0].y1 = y1;
    Box[i].xys[0].k = z1;
    Box[i].xys[0].flip = 1.0;
