/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a triangle and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
/////////////////////////////////////////////////////////////////////////////
bool IntersectTriangle( in Triangle_t obj, in Ray_t ray, in float tmin, in float tmax,out float t, out vec3 hitPos, out vec3 hitNormal ) {
    if(isAABB)
        if(!IntersectAABB(obj.aabb, ray, tmin, tmax))
            return false;
    vec3 E = ray.o;
    vec3 D = ray.d;
    vec3 A = obj.A;
    vec3 B = obj.B;
    vec3 C = obj.C;
    if(dot(D,cross(B-A,C-B)) <= 0.0)
        return false;
    float a = A[0] - B[0];
    float b = A[1] - B[1];
    float c = A[2] - B[2];
    float d = A[0] - C[0];
    float e = A[1] - C[1];
    float f = A[2] - C[2];
    float g = D[0];
    float h = D[1];
    float i = D[2];
    float j = A[0] - E[0];
    float k = A[1] - E[1];
    float l = A[2] - E[2];
    //Cramer's rule
    float M = a*(e*i - h*f) + b*(g*f - d*i) + c*(d*h - e*g);
    float tmpt = -(f*(a*k-j*b) + e*(j*c -a*l) + d*(b*l - k*c))/M;
    if(tmpt < tmin || tmpt > tmax)
        return false;
    float lambda = (i*(a*k -j*b) + h*(j*c - a*l) + g*(b*l - k*c))/M;
    if(lambda < 0.0 || lambda > 1.0)
        return false;
    float beta =( j*(e*i - h*f) + k*(g*f - d*i) + l*(d*h - e*g))/M;
    if(beta < 0.0 || beta > 1.0 - lambda)
        return false;
    t = tmpt;
    hitPos = ray.o + tmpt*ray.d;
    hitNormal = normalize(cross(C-B,B-A));
    return true;
}
