/////////////////////////////////////////////////////////////////////////////
// Computes intersection between a box and a ray.
// Returns true if there is an intersection where the ray parameter t is
// between tmin and tmax, otherwise returns false.
/////////////////////////////////////////////////////////////////////////////
bool IntersectXYRec( in xy_rect obj, in Ray_t ray, in float tmin, in float tmax,out float t, out vec3 hitPos, out vec3 hitNormal ) {
    float tmpt = (obj.k-ray.o.z) / ray.d.z;
    if (tmpt < tmin || tmpt > tmax)
        return false;
    float x = ray.o.x + tmpt*ray.d.x;
    float y = ray.o.y + tmpt*ray.d.y;
    if (x < obj.x0 || x > obj.x1 || y < obj.y0 || y > obj.y1) 
        return false;
    vec3 tmpnormal = obj.flip*vec3(0.0, 0.0, 1.0);
    if (dot(ray.d,tmpnormal) > 0.0){
        return false;
    }
    t = tmpt;
    hitPos = ray.o + tmpt*ray.d;
    hitNormal = obj.flip*vec3(0.0, 0.0, 1.0);
    return true;
}
