// Return random noise in the range [0.0, 1.0], as a function of x.
float Noise2d( in vec2 x )
{
    float xhash = cos( x.x * 37.0 );
    float yhash = cos( x.y * 57.0 );
    return fract( 415.92653 * ( xhash + yhash ) );
}

// Convert Noise2d() into a "star field" by stomping everthing below fThreshhold to zero.
float NoisyStarField( in vec2 vSamplePos, float fThreshhold )
{
    float StarVal = Noise2d( vSamplePos );
    if ( StarVal >= fThreshhold )
        StarVal = pow( (StarVal - fThreshhold)/(1.0 - fThreshhold), 6.0 );
    else
        StarVal = 0.0;
    return StarVal;
}

// Stabilize NoisyStarField() by only sampling at integer values.
float StableStarField( in vec2 vSamplePos, float fThreshhold )
{
    // Linear interpolation between four samples.
    // Note: This approach has some visual artifacts.
    // There must be a better way to "anti alias" the star field.
    float fractX = fract( vSamplePos.x );
    float fractY = fract( vSamplePos.y );
    vec2 floorSample = floor( vSamplePos );    
    float v1 = NoisyStarField( floorSample, fThreshhold );
    float v2 = NoisyStarField( floorSample + vec2( 0.0, 1.0 ), fThreshhold );
    float v3 = NoisyStarField( floorSample + vec2( 1.0, 0.0 ), fThreshhold );
    float v4 = NoisyStarField( floorSample + vec2( 1.0, 1.0 ), fThreshhold );

    float StarVal =   v1 * ( 1.0 - fractX ) * ( 1.0 - fractY )
        			+ v2 * ( 1.0 - fractX ) * fractY
        			+ v3 * fractX * ( 1.0 - fractY )
        			+ v4 * fractX * fractY;
	return StarVal;
}


/////////////////////////////////////////////////////////////////////////////
// Casts a ray into the scene and returns color computed at the nearest
// intersection point. The color is the sum of light from all light sources,
// each computed using Phong Lighting Model, with consideration of
// whether the interesection point is being shadowed from the light.
// If there is no interesection, returns the background color, and outputs
// hasHit as false.
// If there is intersection, returns the computed color, and outputs
// hasHit as true, the 3D position of the intersection (hitPos), the
// normal vector at the intersection (hitNormal), and the k_rg value
// of the material of the intersected object.
/////////////////////////////////////////////////////////////////////////////
vec3 CastRay( in Ray_t ray, 
              out bool hasHit, out vec3 hitPos, out vec3 hitNormal, out vec3 k_rg ) 
{
    // Find whether and where the ray hits some object. 
    // Take the nearest hit point.

    bool hasHitSomething = false;
    float nearest_t = DEFAULT_TMAX;   // The ray parameter t at the nearest hit point.
    vec3 nearest_hitPos;              // 3D position of the nearest hit point.
    vec3 nearest_hitNormal;           // Normal vector at the nearest hit point.
    int nearest_hitMatID;             // MaterialID of the object at the nearest hit point.
    Material_t nearest_hitMat;
    float temp_t;
    vec3 temp_hitPos;
    vec3 temp_hitNormal;
    bool temp_hasHit;

    /////////////////////////////////////////////////////////////////////////////
    // TASK:
    // * Try interesecting input ray with all the planes and spheres,
    //   and record the front-most (nearest) interesection.
    // * If there is interesection, need to record hasHitSomething,
    //   nearest_t, nearest_hitPos, nearest_hitNormal, nearest_hitMatID.
    /////////////////////////////////////////////////////////////////////////////

    /////////////////////////////////
    // TASK: WRITE YOUR CODE HERE. //
    /////////////////////////////////
    //check intersection with all planes
    int planeDirection; // 0 xz 1 3xy 2 4yz
    bool isAddLight = false;
    for(int i = 0; i < NUM_PLANES; i ++ ){
        if(IntersectPlane(Plane[i], ray, DEFAULT_TMIN, DEFAULT_TMAX, temp_t, temp_hitPos, temp_hitNormal)){
            hasHitSomething = true;
            if (temp_t <= nearest_t){
                nearest_t = temp_t;
                nearest_hitPos = temp_hitPos;
                nearest_hitNormal = temp_hitNormal;
                nearest_hitMatID = Plane[i].materialID;
                planeDirection = i;
                isAddLight = false;
            }
        }
    }
    if(isSphere){
    // check intersection with all spheres
    for(int i = 0; i < NUM_SPHERES; i ++ ){
        if(IntersectSphere(Sphere[i], ray, DEFAULT_TMIN, DEFAULT_TMAX, temp_t, temp_hitPos, temp_hitNormal)){
            hasHitSomething = true;
            if (temp_t <= nearest_t){
                nearest_t = temp_t;
                nearest_hitPos = temp_hitPos;
                nearest_hitNormal = temp_hitNormal;
                nearest_hitMatID = Sphere[i].materialID;
                isAddLight = false;
                if(i >= 18 && i < NUM_SPHERES )
                    isAddLight = true;
                if(nearest_hitMatID == -1){
                    nearest_hitMat = Sphere[i].material;
                }
            }
        }
    }
    }
    // check intersection with all boxes
    for(int i = 0; i < NUM_BOXES; i ++ ){
        if(IntersectBox(Box[i], ray, DEFAULT_TMIN, DEFAULT_TMAX, temp_t, temp_hitPos, temp_hitNormal)){
            hasHitSomething = true;
            if (temp_t <= nearest_t){
                nearest_t = temp_t;
                nearest_hitPos = temp_hitPos;
                nearest_hitNormal = temp_hitNormal;
                nearest_hitMatID = Box[i].materialID;
                isAddLight = false;
                if(nearest_hitMatID == -1){
                    nearest_hitMat = Box[i].material;
                }
            }
        }
    }
    // check intersection with all triangles
    for(int j = 0; j < NUM_Octahedrons; j ++){
        if(IntersectAABB(Octahedron[j].aabb,ray,DEFAULT_TMIN, DEFAULT_TMAX)){
            for(int i = 0; i < NUM_TRIANGLE; i ++ ){
                if(IntersectTriangle(Triangle[i], ray, DEFAULT_TMIN, DEFAULT_TMAX, temp_t, temp_hitPos, temp_hitNormal)){
                    hasHitSomething = true;
                    if (temp_t <= nearest_t){
                        nearest_t = temp_t;
                        nearest_hitPos = temp_hitPos;
                        nearest_hitNormal = temp_hitNormal;
                        nearest_hitMatID = Triangle[i].materialID;
                        // isAddLight = false;
                        if(i > 8)
                            isAddLight = false;
                        else
                            isAddLight = false;
                        if(nearest_hitMatID == -1){
                            nearest_hitMat = Triangle[i].material;
                        }
                    }
                }
            }
        }
    }
    // for(int i = 0; i < NUM_TRIANGLE; i ++ ){
    //     if(IntersectTriangle(Triangle[i], ray, DEFAULT_TMIN, DEFAULT_TMAX, temp_t, temp_hitPos, temp_hitNormal)){
    //         hasHitSomething = true;
    //         if (temp_t <= nearest_t){
    //             nearest_t = temp_t;
    //             nearest_hitPos = temp_hitPos;
    //             nearest_hitNormal = temp_hitNormal;
    //             nearest_hitMatID = Triangle[i].materialID;
    //             isAddLight = true;
    //             if(nearest_hitMatID == -1){
    //                 nearest_hitMat = Triangle[i].material;
    //             }
    //         }
    //     }
    // }



    // One of the output results.
    hasHit = hasHitSomething;
    if ( !hasHitSomething ) return BACKGROUND_COLOR;

    vec3 I_local = vec3( 0.0 );  // Result color will be accumulated here.

    /////////////////////////////////////////////////////////////////////////////
    // TASK:
    // * Accumulate lighting from each light source on the nearest hit point. 
    //   They are all accumulated into I_local.
    // * For each light source, make a shadow ray, and check if the shadow ray
    //   intersects any of the objects (the planes and spheres) between the 
    //   nearest hit point and the light source.
    // * Then, call PhongLighting() to compute lighting for this light source.
    /////////////////////////////////////////////////////////////////////////////

    /////////////////////////////////
    // TASK: WRITE YOUR CODE HERE. //
    /////////////////////////////////
    for (int i = 0; i < NUM_LIGHTS; i ++){
        if(i < NUM_LIGHTS - 1 || (i == NUM_LIGHTS - 1 && isAddLight)){
            vec3 L = normalize(Light[i].position - nearest_hitPos);
            vec3 N = nearest_hitNormal;
            vec3 V = -ray.d;
            Ray_t shadowRay;
            shadowRay.o = nearest_hitPos;
            shadowRay.d = L;
            bool inshadow = checkIntersection (shadowRay);
            if(nearest_hitMatID == -1){
                I_local += PhongLighting(L, N, V, inshadow, nearest_hitMat, Light[i]);
            }else if(nearest_hitMatID == -2){
                //vec3 tcolor;
                Material_t textureM;
                vec3 tcolor = vec3( 0.1, 0.2, 0.4 ) * nearest_hitPos.y / 30.0;
                    // Note: Choose fThreshhold in the range [0.99, 0.9999].
                    // Higher values (i.e., closer to one) yield a sparser starfield.
                float StarFieldThreshhold = 0.97;

                    // Stars with a slow crawl.
                float xRate = 0.001;
                float yRate = -0.0006;
                if(planeDirection == 0){
                    tcolor = texture(iChannel1,mod(nearest_hitPos.xz/10.0,6.0)).rgb;//texture(iChannel1, fract((nearest_hitPos.xz + vec2(iTime))/ 100.)).xyz;
                    textureM = Material_t(0.5*tcolor, 1.0*tcolor, 2.0*tcolor, 0.0*tcolor, 64.0);
                    I_local += PhongLighting(L, N, V, inshadow, textureM, Light[i]);
                }
                else if (planeDirection % 2 == 1){
                    vec2 vSamplePos = nearest_hitPos.xy*10.0 + vec2( xRate * float( iFrame ), yRate * float( iFrame ) );
                    float StarVal = StableStarField( vSamplePos, StarFieldThreshhold );
                    tcolor += vec3( StarVal );
                    textureM = Material_t(1.0*tcolor, 0.5*tcolor, 1.0*tcolor, 0.5*tcolor, 64.0);
                    I_local += PhongLighting(L, N, V, false, textureM, Light[i]);
                }
                else if (planeDirection % 2 == 0){
                    vec2 vSamplePos = nearest_hitPos.zy*10.0 + vec2( xRate * float( iFrame ), yRate * float( iFrame ) );
                    float StarVal = StableStarField( vSamplePos, StarFieldThreshhold );
                    tcolor += vec3( StarVal );
                    textureM = Material_t(0.0*tcolor, 0.5*tcolor, 1.0*tcolor, 0.5*tcolor, 64.0);                   
                    I_local += PhongLighting(L, N, V, false, textureM, Light[i]);                
                }
            }else
                I_local += PhongLighting(L, N, V, inshadow, Material[nearest_hitMatID], Light[i]);
        }
    }


    // Populate output results.
    hitPos = nearest_hitPos;
    hitNormal = nearest_hitNormal;
    k_rg = Material[nearest_hitMatID].k_rg;

    return I_local;
}



/////////////////////////////////////////////////////////////////////////////
// Execution of fragment shader starts here.
// 1. Initializes the scene.
// 2. Compute a primary ray for the current pixel (fragment).
// 3. Trace ray into the scene with NUM_ITERATIONS recursion levels.
/////////////////////////////////////////////////////////////////////////////
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    InitScene();

    // Scale pixel 2D position such that its y coordinate is in [-1.0, 1.0].
    vec2 pixel_pos = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;

    // Position the camera.
    vec3 cam_pos = vec3((18.0+(iMouse.y/iResolution.y)*15.0)*sin(0.2*iTime), 2.0 + (iMouse.y/iResolution.y)*15.0, (15.0+(iMouse.y/iResolution.y)*15.0)*cos(0.2*iTime));//vec3( -8, 2, 5 );
    vec3 cam_lookat = vec3( 0.0, 6.0, 0.0 );
    vec3 cam_up_vec = vec3( 0.0, 1.0, 0.0 );

    // Set up camera coordinate frame in world space.
    vec3 cam_z_axis = normalize( cam_pos - cam_lookat );
    vec3 cam_x_axis = normalize( cross(cam_up_vec, cam_z_axis) );
    vec3 cam_y_axis = normalize( cross(cam_z_axis, cam_x_axis));

    // Create primary ray.
    float pixel_pos_z = -1.0 / tan(FOVY / 2.0);
    Ray_t pRay;
    pRay.o = cam_pos;
    pRay.d = normalize( pixel_pos.x * cam_x_axis  +  pixel_pos.y * cam_y_axis  +  pixel_pos_z * cam_z_axis );


    // Start Ray Tracing.
    // Use iterations to emulate the recursion.

    vec3 I_result = vec3( 0.0 );
    vec3 compounded_k_rg = vec3( 1.0 );
    Ray_t nextRay = pRay;

    for ( int level = 0; level <= NUM_ITERATIONS; level++ ) 
    {
        bool hasHit;
        vec3 hitPos, hitNormal, k_rg;

        vec3 I_local = CastRay( nextRay, hasHit, hitPos, hitNormal, k_rg );

        I_result += compounded_k_rg * I_local;

        if ( !hasHit ) break;

        compounded_k_rg *= k_rg;

        nextRay = Ray_t( hitPos, normalize( reflect(nextRay.d, hitNormal) ) );
    }

    fragColor = vec4( I_result, 1.0 );
    if(Box[0].xys[0].debug)
        fragColor = vec4( 1.0,0.0,0.0, 1.0 );
}

float PI = 3.14159265;
float map(vec3 p){
    vec2 t = vec2(0.3, 0.05);
    vec3 q = p;
    vec3 r = p;
    p = fract(p / 2.0) * 2.0 - 0.5;
    p.yz *= mat2(cos((iTime - 1.0/2.0) * PI),-sin((iTime - 1.0/2.0) * PI)
                 ,sin((iTime - 1.0/2.0) * PI),cos((iTime - 1.0/2.0) * PI));
    vec2 r1 = vec2(length(p.xy) - t.x, p.z);
    float torus1 = length(r1) - t.y;
    q = fract(q / 2.0) * 2.0 - 1.5;
    q.yz *= mat2(cos((-iTime - 1.0/2.0) * PI),-sin((-iTime - 1.0/2.0) * PI)
                 ,sin((-iTime - 1.0/2.0) * PI),cos((-iTime - 1.0/2.0) * PI));
    vec2 r2 = vec2(length(q.xy) - t.x, q.z);
    float torus2 = length(r2) - t.y;
    r = fract(r / 2.0) * 2.0 - 1.0;
    float tubes = min(length(r.xz -vec2(0.5,0)) - 0.1,length(r.yz -vec2(0.5,0)) - 0.1);

    return min(min(torus2,torus1),tubes);
}

const float EPS = 0.001;
vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 96; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.2;
    }
    return vec4(getNormal(p),t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy /iResolution.xy;
	uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 r = normalize(vec3(uv,1.0));
    vec3 o = vec3(0.5,0.5,iTime);
    vec4 data = trace(o,r);
    float t = data.w;
    float fog = 1.0 / (1.0 + t * t * 0.2);
    vec3 fc = mix(vec3(0.5 - data.x,0.5 -data.y,0.5-data.z),vec3(0),1.0 - fog);
    vec3 col1 = vec3(sin(iTime),cos(iTime),1);
    vec3 col2 = vec3(1);
	fc = mix(col1/2.0,col2,fog);
    // Output to screen
    fragColor = vec4(fc,1.0);
}

