vec3 sunDir;
vec2 gVec[7], hVec[7];
float tCur, dstFar;
const float pi = 3.14159, sqrt3 = 1.7320508;

void HexVorInit ()
{
  vec3 e = vec3 (1., 0., -1.);
  gVec[0] = e.yy;
  gVec[1] = e.xy;
  gVec[2] = e.yx;
  gVec[3] = e.xz;
  gVec[4] = e.zy;
  gVec[5] = e.yz;
  gVec[6] = e.zx;
  for (int k = 0; k < 7; k ++) hVec[k] = HexToPix (gVec[k]);
}

float HexVor (vec2 p)
{
  vec4 sd;
  vec2 ip, fp, d, u;
  float amp, a;
  amp = 0.5;
  ip = PixToHex (p);
  fp = p - HexToPix (ip);
  sd = vec4 (4.);
  for (int k = 0; k < 7; k ++) {
    u = Hashv2v2 (ip + gVec[k]);
    a = 2. * pi * (u.y - 0.5) * tCur;
    d = hVec[k] + amp * (0.4 + 0.6 * u.x) * vec2 (cos (a), sin (a)) - fp;
    sd.w = dot (d, d);
    if (sd.w < sd.x) {
      sd = sd.wxyw;
    } else sd = (sd.w < sd.y) ? sd.xwyw : ((sd.w < sd.z) ? sd.xyww : sd);
  }
  sd.xyz = sqrt (sd.xyz);
  return SmoothMin (sd.y, sd.z, 0.1) - sd.x;
}

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 bSize;
  float dMin, db;
  dMin = dstFar;
  bSize = vec2 (4., 2.);
  q = p;
  q.y -= bSize.y + 0.11;
  db = PrRoundBoxDf (q, vec3 (bSize + 0.1, 0.01), 0.01);
  if (db < 0.1) {
    dMin = min (dMin, PrBoxDf (q, vec3 (bSize, 0.01 - 0.05 * smoothstep (0.1, 0.7,
       HexVor (4. * q.xy)))));
    dMin = min (dMin, SmoothMax (db, - PrBox2Df (q.xy, bSize - 0.05), 0.01));
  } else dMin = db;
  return 0.6 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    if (d < 0.0005 || dHit > dstFar || p.y < 0.) break;
    dHit += d;
  }
  if (p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.001, -0.001);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = 0; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.01 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return sh;
}

vec3 SkyCol (vec3 rd)
{
  rd.y = abs (rd.y);
  return mix (vec3 (0.3, 0.35, 0.7), vec3 (0.8, 0.8, 0.8),
     clamp (2. * (Fbm2 (2. * rd.xz / rd.y + 0.1 * tCur) - 0.1) * rd.y, 0., 1.));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, colR, gCol, vn, ror, rdr;
  vec2 w;
  float dstObj, dstObjR, dstFlr, nDotL, sh;
  bool isRefl;
  HexVorInit ();
  gCol = vec3 (0.5, 0.2, 0.1);
  dstFlr = (rd.y < 0.) ? - ro.y / rd.y : dstFar;
  dstObj = ObjRay (ro, rd);
  dstObjR = dstFar;
  isRefl = false;
  if (dstFlr < min (dstObj, dstFar)) {
    ror = ro + dstFlr * rd;
    rdr = reflect (rd, vec3 (0., 1., 0.));
    ror += 0.01 * rdr;
    dstObjR = ObjRay (ror, rdr);
    isRefl = true;
  }
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    nDotL = max (dot (vn, sunDir), 0.);
    nDotL *= nDotL;
    col = gCol * (0.2 + 0.1 * max (vn.y, 0.) + 0.7 * nDotL * nDotL) +
       0.3 * pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
  } else if (rd.y < 0.) {
    ro += dstFlr * rd;
    vn = vec3 (0., 1., 0.);
    sh = ObjSShadow (ro, sunDir);
    nDotL = max (dot (vn, sunDir), 0.);
    w = floor (mod (ro.xz, 2.));
    col = vec3 (0.95, 1., 0.95) * ((w.x == w.y) ? vec3 (0.7): vec3 (0.3));
    col = col * (0.2 + 0.1 * max (vn.y, 0.) + 0.7 * sh * nDotL) +
       0.2 * step (0.95, sh) * pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
    col = mix (col, SkyCol (rd), 0.97 * smoothstep (0.5, 1., dstFlr / 100.));
  } else col = SkyCol (rd);
  if (isRefl) {
    rd = rdr;
    if (dstObjR < dstFar) {
      ro = ror + dstObjR * rd;
      vn = ObjNf (ro);
      nDotL = max (dot (vn, sunDir), 0.);
      nDotL *= nDotL;
      colR = gCol * (0.2 + 0.1 * max (vn.y, 0.) + 0.7 * nDotL * nDotL) +
         0.3 * pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
    } else colR = SkyCol (rd);
    col = mix (col, colR, 0.4);
  }
  return clamp (col, 0., 1.);
}

#define AA  1

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, ori, ca, sa;
  float az, el, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.11 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.7 * pi * mPtr.y;
  }
  el = clamp (el, -0.45 * pi, 0.);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -20.);
  ro.y += 1.;
  sunDir = normalize (vec3 (0., 0.5, -1.));
  sunDir.xz = Rot2D (sunDir.xz, pi * sign (2. * floor (mod (0.03 * tCur, 2.)) - 1.) *
     SmoothBump (0.3, 0.7, 0.2, mod (0.03 * tCur, 1.)));
  zmFac = 5.;
  dstFar = 100.;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s = vec3 (0.);
  float a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  float s;
  const vec3 e = vec3 (0.1, 0., 0.);
  s = Fbmn (p, n);
  g = vec3 (Fbmn (p + e.xyy, n) - s, Fbmn (p + e.yxy, n) - s,
     Fbmn (p + e.yyx, n) - s);
  return normalize (n + f * (g - n * dot (n, g)));
}

mat2 genRot(float val){
	return mat2(cos(val),-sin(val),sin(val),cos(val));
}
float PI = 3.14159265;
vec3 path(float t){
	float x = clamp(sin(t) * pow(2.0,0.5),-1.0,1.0);
    float y = clamp(cos(t) * pow(2.0,0.5),-1.0,1.0);
    float z = 0.;
    return vec3(x,y,z);
}
float grid(vec3 p, float a){
    //p.xz *= genRot(iTime);
    p = fract(p / a) * a;
    p -= vec3(a / 2.0);
	float d1 = max(abs(p.x),abs(p.y)) - a/(9.+3.*sin(iTime * 2.0));
	float d2 = max(abs(p.y),abs(p.z)) - a/(9.+3.*sin(iTime * 2.0));
	float d3 = max(abs(p.z),abs(p.x)) - a/(9.+3.*sin(iTime * 2.0));
	return (min(min(d1,d2),d3));
}
float map(vec3 p){
	float k = grid(p,1.0);
    for(float i = 2.0; i < 256.0; i *= 2.0 ){
        k = max(k,-grid(p,1.0/i));
    }
    return k;
}

const float EPS = 0.001;
vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 128; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return vec4(getNormal(p),t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float PI = 3.14159265;
    vec2 uv = fragCoord.xy /iResolution.xy;
	uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 r = normalize(vec3(uv,1.2));
    r.xz *= genRot(iTime);
    vec3 o = vec3( sin(iTime * PI / 4.) * 1.2,  0.5 + iTime * 1.0, cos(iTime * PI / 4.) * 1.2);
    vec4 data = trace(o,r);
    float t = data.w;
    float fog = 1.0 / (1.0 + t * t * 0.05);
    vec3 fc = mix(vec3(0.5 - data.x,0.5 -data.y,0.5-data.z),vec3(0.),1.0 - fog);
	//fc = vec3(fog);
    // Output to screen
    fragColor = vec4(fc,1.0);
}

