const float PI = 3.1415926;
const float EPS = .001;
#define time iTime
vec2 rot(vec2 p, float a){return vec2(p.x*cos(a)-p.y*sin(a),p.x*sin(a)+p.y*cos(a));}
vec2 pmod(vec2 p,float m){
    float a=PI/m-atan(p.y,p.x);
    float r=2.*PI/m;
    a=floor(a/r)*r;
    return rot(p,a);
}
// https://docs.google.com/presentation/d/1j4t4mcLw8F1PfqvKP3P8meMJ5dWfDXkfb9lc63qOFVM/edit#slide=id.g238edbc4b4_0_2
vec4 tglad(vec3 p)
{
	vec3 q=vec3(mod(p,2.))-1.1;
	vec3 s=vec3(.01,2.+.3*cos(time),-.8+.35*sin(time));
	vec3 s2=vec3(1.2+.01*cos(time),9.+6.*sin(time),1.4*cos(time));
	vec3 scale=vec3(-PI);
	for(int i=0;i<3;++i){
		q=clamp(q, -1., 1.)*1.99-q;
		q*=scale/clamp(dot(q,q),.33,1.);
		q+=s*.9;
	}
	return vec4((length(max(abs(q)-s2,.0))-.06)/pow(PI,3.+EPS),q);
}
vec4 map(vec3 p){
    vec3 q=p;
    for(int i=0;i<3;++i){
        q=abs(q)-1.5;
        q.xy=pmod(q.xy,1.);
        q.y-=.1;
        q.yz=pmod(q.yz,-1.);
        q*=.7;
    }
    vec4 d=tglad(q);
    return vec4(d.x-.1,d.yzw);
}
vec3 norm(vec3 p){vec2 e=vec2(.01,.0);return .000001+map(p).x-vec3(map(p-e.xyy).x,map(p-e.yxy).x,map(p-e.yyx).x);}
// https://www.shadertoy.com/view/3dXSDH
float sss(vec3 p, vec3 r, float d) {return clamp(map(p+r*d).x*3.0,0.0,1.0);}
float rnd(float x){return fract(sin(x+12.345)*98.765);}
float dots(vec3 p, float j) {
    p*=4.+sin(j);
    p.x -= rnd(floor(p.y));
    p*=PI;
    return clamp(0.1-length(vec2(sin(p.x),cos(p.y))),0.0,1.0)*30.;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x,iResolution.y);
    vec3 col = vec3(0.0);
    vec3 ro=vec3(-4.3*sin(.1*time),0.,-2.)
    ,ta=ro-vec3(2.+sin(time),.8+cos(time),1.-sin(time))
    ,up=vec3(0.,1.,.0);
    ro.xy=rot(ro.xy,-1.3);
    ta.yz=rot(ta.yz,-1.3);
