Material g_NoMaterial = Material(vec3(1.0, 0.0, 1.0), 0.0, 1.0);

///////////////////////////////////////////////////////////////////////////////////

float blerp(float x, float y0, float y1, float y2, float y3) {
	float a = y3 - y2 - y0 + y1;
	float b = y0 - y1 - a;
	float c = y2 - y0;
	float d = y1;
	return a * x * x * x + b * x * x + c * x + d;
}

float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float perlin(float x, float h) {
	float a = floor(x);
	return blerp(mod(x, 1.0),
		rand(vec2(a-1.0, h)), rand(vec2(a-0.0, h)),
		rand(vec2(a+1.0, h)), rand(vec2(a+2.0, h)));
}

float hash1( uint n ) 
{
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return float( n & uvec3(0x7fffffffU))/float(0x7fffffff);
}

///////////////////////////////////////////////////////////////////////////////////
// IQ's noise functions

float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);
	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 rg = textureLod( iChannel0, (uv+ 0.5)/256.0, 0. ).yx;
	return mix( rg.x, rg.y, f.z );
}

///////////////////////////////////////////////////////////////////////////////////

#define MARCH_ITERATIONS 	320
#define MARCH_DELTA			0.05
#define MARCH_DELTA2		1.01

Result g_result;

float mist(vec3 p, int LOD)
{   
    vec3 p2 = p;
    p *= 0.2;
    float weight = 0.25;
    float totalweight = 0.0;
    float value = 0.0;
    for (int i=0; i<LOD; i++)
    {
        totalweight += weight;
        value += noise(p)*weight;    
        p *= 2.03;
        weight *= 0.6;
    }

    return (value/totalweight + abs(p2.y)*0.1 + abs(p.x)*0.0003 - 0.1);
}

///////////////////////////////////////////////////////////////////////////////////

Result raymarch_query(Ray ray, int iterations, float delta)
{
    Result result = Result(ray.pos+ray.dir*10000.0, vec3(0.0, 0.0, 0.0), g_NoMaterial, vec4(0.0, 0.5, 0.0, 0.0));    
    float dist = 0.0;
    float fog=0.0;
    float dstalpha = 0.0;
    float srcalpha = 0.0;
    const float densitythreshold = 0.70;
    const float densityscale = 100.0/(1.0-densitythreshold);

    vec3 lighting = vec3(-1.0, -1.0, 1.0);
    //vec3 lighting2 = vec3(1.0, 0.0, 1.0);
    lighting = normalize(lighting);

	for (int i=0; i<iterations; i++)
    {           
        // the 2 values of the fog at the query position and one shifted slightly toward the light
		float v1 = mist(ray.pos, 6);
		float v2 = mist(ray.pos + lighting*0.3, 5);
		//float v3 = mist(ray.pos + lighting2*0.3);

		float value=0.0;;
        float vmax=0.7;
        float vmin=0.5;

        if (v1 >= vmax)
            value = 1.0;
        else if (v1 <= vmin)
            value = 0.0;
        else
            value = (v1-vmin)/(vmax-vmin);

        value=value*value;

        // density is the value times the step

        float density = clamp(value*delta*0.7, 0.0, 1.0);
        density*=clamp(delta*4.0, 0.0, 1.0);
        // update the alpha
        srcalpha = density;

        if (srcalpha>0.01)
        {
            // the colour of the fog is dependent on its density, dark grey = high density, white = low
            vec3 fogcolour = mix(vec3(0.9), vec3(0.0, 0.0, 0.1), value); 

            // add light reflected from the sun
            //fogcolour += clamp((v2-v1)*33.0, 0.0, 1.0)*vec3(1.0, 0.7, 0.3)*1.0;
            //fogcolour += clamp((v3-v1)*6.0, 0.0, 1.0)*vec3(0.9, 0.0, 0.0)*1.0;

            // some bi-directional lighting - totally unreal but looks really funky mixed with the fog colour
            if ((v2-v1) >= 0.0)
            {
                fogcolour += (v2-v1)*(v2-v1)*250.0*vec3(1.0, 0.3, 0.0)*1.0;
                fogcolour += pow((v2-v1), 3.0)*1000.0*vec3(1.0, 0.3, 0.0)*1.0;
            }
            else
            {
                fogcolour += (v1-v2)*(v1-v2)*66.0*vec3(0.0, 0.5, 1.0)*1.0;
                fogcolour += pow((v1-v2), 3.0)*500.0*vec3(1.0, 1.0, 1.0)*1.0;
            }


            if (dstalpha == 0.0)
            {	// first sample with some density in it
                result.fog.xyz = fogcolour;
                dstalpha = srcalpha;
            }
            else
            {
                // modify the destingation alpha, based on the current srcalpha
                float prevdstalpha = dstalpha;
                dstalpha = dstalpha + srcalpha*(1.0 - dstalpha);
                result.fog.xyz = mix(fogcolour, result.fog.xyz, prevdstalpha/dstalpha);
                //result.fog.xyz += fogcolour*density;            
            }
        }
        if (dstalpha>0.9)
        {
		   	result.fog.w = dstalpha;
    		return result;            
        }

        ray.pos += ray.dir*delta;
        delta*=MARCH_DELTA2;
    }

   	result.fog.w = dstalpha;
    return result;
}

///////////////////////////////////////////////////////////////////////////////////

vec3 raymarch(Ray inputray)
{
    vec3 colour = vec3(0.0, 0.0, 0.0);
    Ray ray=inputray;        
    g_result = raymarch_query(ray, MARCH_ITERATIONS, MARCH_DELTA);

//    colour = vec3(g_result.fog.w);	
//    colour = g_result.fog.xyz;    
    colour = mix(vec3(0.0, 0.15, 0.3), g_result.fog.xyz, g_result.fog.w);       
    //colour = mix(vec3(0.0), g_result.fog.xyz, g_result.fog.w);       

    return colour;    
}

