    ww.xz = rotate2D(ww.xz, -mouseXY.x * 2.0 * PI);
    vec3 uu = normalize(cross(vec3(0.0, 1.0, 0.0), ww));
    vec3 vv = normalize(cross(ww, uu));
    vec3 rd = normalize(p.x * uu + p.y * vv + 2.0 * ww);

    ro.x -= iTime * 0.1; //Camera movement

 	vec3 color = render(ro + vec3(0.0, -1.0, 0.0), rd); 
    fragColor = vec4(color, 1.0);
}

float addAlpha(float a1, float a2){
	return a1 + a2 - a1 * a2;
}

float getColor0(float x0, float t, float omiga){
	float xt = omiga * t;
    float xt0 = omiga * (t - 0.1);
    float pi2 = 3.1415926 * 2.0;
    float n = floor((xt - x0)/pi2);
    float factor = 1.0 / (xt - xt0);
    float xx = n * pi2 + x0;
    float color = 0.0;
    float f = 0.0;
    while(xx > xt0 && xx >= x0){
    	f = factor * (xx - xt0);
        color = addAlpha(color , f * f);
        if(color >= 1.0){
            color = 1.0;
            break;
        }
        xx = xx - pi2;
    }
    return color;
}

float getColor(float x, float y, float t, float omiga, float m){
    float PI = 3.1415926;
	float x0 = atan(y, x) + PI;
    float step = PI * 2.0 / m;
    float color = 0.0;
    for(float i = 0.0; i < m; i = i + 1.0){
        color = addAlpha(color , getColor0(x0 + i * step, t, omiga));
        if(color >= 1.0){
            color = 1.0;
            break;
        }
    }
    return color;
}

