float usin(float x){return(sin(x)+1.)/2.;}
#define time iTime
#define SIZE 5
void mainImage(out vec4 fragColor,in vec2 fragCoord){
	vec2 points[]=vec2[](
		vec2(.5,-.75),
		vec2(1.,-1.),
		vec2(-1.,1.),
		vec2(sin(time),cos(time)),
		vec2(-sin(time),cos(time))
	);
	vec3 colors[]=vec3[](
		vec3(1.,.2,.2),
		vec3(1.,.9,.3),
		vec3(.1,.5,.7),
		vec3(.1,.7,.2),
		vec3(1.,1.,1.)
	);
	vec2 p=floor(fragCoord),
	     uv=(p/iResolution.xy)-.5;
	uv.x/=iResolution.y/iResolution.x;
	uv*=5.;
	bool fuzzy=(mod(p.x+p.y,16.)>=1.)&&(mod(p.x-p.y,16.)>=1.);
	if(fuzzy){
		vec3 col=vec3(0.);
		float sum=0.,
		      gamma=2.2;
		for(int i=0;i<SIZE;i++){
			float dist=length(uv-points[i]);
			vec3 color=pow(colors[i],vec3(gamma));
			if(dist<1e-6){col=color;sum=1.;break;} //Avoid dividing by zero.
			dist=1./pow(dist,2.+(usin(time)*5.));
			col+=color*dist;
			sum+=dist;
		}
		//float betweens=1./sum;
		col/=sum;
		fragColor=vec4(pow(col,1./vec3(gamma)),1.);
	}else{
		float minV=length(uv-points[0]);
		int minI=0;
		for(int i=1;i<SIZE;i++){
			float dist=length(uv-points[i]);
			vec3 color=colors[i];
			if(dist<minV){
				minI=i;
				minV=dist;
			}
		}
		fragColor=vec4(colors[minI],1.);
	}
}

