void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord/iResolution.xy;
  vec2 ouv = uv;
  uv *=2.;
  uv -=1.;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  vec3 col = vec3(0.);

  float b = texture(iChannel1, vec2(0.,0.1)).r*10.;

  vec3 bcol = mix( vec3(1., 0.6, 0.), vec3(0., 0.6, 1.), abs(sin(cos(iTime)+uv.y* uv.x + iTime+b)));

  float t = iTime;

  float n1 = sin(  uv.x +t)*0.5 + cos(uv.x*31.)*texture(iChannel1, ouv/3.+iTime).r*0.02;
  float n2 = sin(  uv.x*2.0+t*1.0 )*0.4 + cos(uv.x*31.)*texture(iChannel1, ouv/3.+iTime).r*.03;
  float n3 = sin(  uv.x*3.0+t*1.0 )*0.3 + cos(uv.x*31.)*texture(iChannel1, ouv/3.+iTime).r*.04;
  float n4 = sin(  uv.x*4.0+t*1.0 )*0.2 + cos(uv.x*31.)*texture(iChannel1, ouv/3.+iTime).r*.05;
  float n5 = sin(  uv.x*5.+t*1.0 )*0.1 + cos(uv.x*31.)*texture(iChannel1, ouv/3.+iTime).r*.06;

  n1 = uv.y > n1 ? length(uv.y-n1)*40. : 3.5 *length(uv.y-n1);
  n2 = uv.y > n2 ? length(uv.y-n2)*60. : 3.5 *length(uv.y-n1);
  n3 = uv.y > n3 ? length(uv.y-n3)*80. : 3.5 *length(uv.y-n1);
  n4 = uv.y > n4 ? length(uv.y-n4)*100. : 3.5 *length(uv.y-n1);
  n5 = uv.y > n5 ? length(uv.y-n5)*120. : 3.5 *length(uv.y-n1);

  col += bcol*0.5/n1+vec3(1.)*6./n1 * noise(uv*0.05,bcol);
  col += bcol*0.5/n2+vec3(1.)*6./n2 * noise(uv*0.15,bcol);
  col += bcol*0.5/n3+vec3(1.)*6./n3 * noise(uv*0.3,bcol);
  col += bcol*0.5/n4+vec3(1.)*6./n4 * noise(uv*0.5,bcol);
  col += bcol*0.5/n5+vec3(1.)*6./n5 * noise(uv*0.7,bcol);



  fragColor = vec4(col, 1.);
}

// A single iteration of Bob Jenkins' One-At-A-Time hashing algorithm.
uint hash( uint x ) {
    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x;
}



// Compound versions of the hashing algorithm I whipped together.
uint hash( uvec2 v ) { return hash( v.x ^ hash(v.y)                         ); }
uint hash( uvec3 v ) { return hash( v.x ^ hash(v.y) ^ hash(v.z)             ); }
uint hash( uvec4 v ) { return hash( v.x ^ hash(v.y) ^ hash(v.z) ^ hash(v.w) ); }



// Construct a float with half-open range [0:1] using low 23 bits.
// All zeroes yields 0.0, all ones yields the next smallest representable value below 1.0.
float floatConstruct( uint m ) {
    const uint ieeeMantissa = 0x007FFFFFu; // binary32 mantissa bitmask
    const uint ieeeOne      = 0x3F800000u; // 1.0 in IEEE binary32

    m &= ieeeMantissa;                     // Keep only mantissa bits (fractional part)
    m |= ieeeOne;                          // Add fractional part to 1.0

    float  f = uintBitsToFloat( m );       // Range [1:2]
    return f - 1.0;                        // Range [0:1]
}



// Pseudo-random value in half-open range [0:1].
float random( float x ) { return floatConstruct(hash(floatBitsToUint(x))); }
float random( vec2  v ) { return floatConstruct(hash(floatBitsToUint(v))); }
float random( vec3  v ) { return floatConstruct(hash(floatBitsToUint(v))); }
float random( vec4  v ) { return floatConstruct(hash(floatBitsToUint(v))); }





