//note: see http://vec3.ca/bicubic-filtering-in-fewer-taps/ for more details
//note: also ://mate.tue.nl/mate/pdfs/10318.pdf
//note: polynomials converted to hornerform using wolfram-alpha hornerform()
vec4 SampleTextureCubic( vec2 uv, vec2 texSize, float lambda )
{
    vec2 iTc = uv * texSize;
    vec2 tc = floor(iTc - 0.5) + 0.5;

    vec2 f = iTc - tc;

    vec2 f2 = vec2( f * f );
	vec2 w0 = f * ( f * ( 0.5 - 1.0/6.0 * f ) - 0.5 ) + 1.0/6.0;
	vec2 w1 = (0.5 * f - 1.0) * f2 + 2.0/3.0;
	vec2 w2 = f * ( f * (0.5 - 0.5 * f) + 0.5) + 1.0/6.0;
	vec2 w3 = (1.0/6.0) * f*f2;

   	vec2 s0 = w0 + w1;
    vec2 s1 = w2 + w3;

    vec2 f0 = w1 / s0;
    vec2 f1 = w3 / s1;

    vec2 t0 = tc - 1.0 + f0;
    vec2 t1 = tc + 1.0 + f1;

    return
        (sampleTex( vec2( t0.x, t0.y )/texSize, lambda) * s0.x
      +  sampleTex( vec2( t1.x, t0.y )/texSize, lambda) * s1.x) * s0.y
      + (sampleTex( vec2( t0.x, t1.y )/texSize, lambda) * s0.x
      +  sampleTex( vec2( t1.x, t1.y )/texSize, lambda) * s1.x ) * s1.y;
}


// note: entirely stolen from https://gist.github.com/TheRealMJP/c83b8c0f46b63f3a88a5986f4fa982b1
//
// Samples a texture with Catmull-Rom filtering, using 9 texture fetches instead of 16.
// See http://vec3.ca/bicubic-filtering-in-fewer-taps/ for more details
vec4 SampleTextureCatmullRom( vec2 uv, vec2 texSize, float lambda )
{
    // We're going to sample a a 4x4 grid of texels surrounding the target UV coordinate. We'll do this by rounding
    // down the sample location to get the exact center of our "starting" texel. The starting texel will be at
    // location [1, 1] in the grid, where [0, 0] is the top left corner.
    vec2 samplePos = uv * texSize;
    vec2 texPos1 = floor(samplePos - 0.5) + 0.5;

    // Compute the fractional offset from our starting texel to our original sample location, which we'll
    // feed into the Catmull-Rom spline function to get our filter weights.
    vec2 f = samplePos - texPos1;

    // Compute the Catmull-Rom weights using the fractional offset that we calculated earlier.
    // These equations are pre-expanded based on our knowledge of where the texels will be located,
    // which lets us avoid having to evaluate a piece-wise function.
    vec2 w0 = f * ( -0.5 + f * (1.0 - 0.5*f));
    vec2 w1 = 1.0 + f * f * (-2.5 + 1.5*f);
    vec2 w2 = f * ( 0.5 + f * (2.0 - 1.5*f) );
    vec2 w3 = f * f * (-0.5 + 0.5 * f);

    // Work out weighting factors and sampling offsets that will let us use bilinear filtering to
    // simultaneously evaluate the middle 2 samples from the 4x4 grid.
    vec2 w12 = w1 + w2;
    vec2 offset12 = w2 / w12;

    // Compute the final UV coordinates we'll use for sampling the texture
    vec2 texPos0 = texPos1 - vec2(1,1);
    vec2 texPos3 = texPos1 + vec2(2,2);
    vec2 texPos12 = texPos1 + offset12;

    texPos0 /= texSize;
    texPos3 /= texSize;
    texPos12 /= texSize;

    vec4 result = vec4(0,0,0,0);
    result += sampleTex( vec2(texPos0.x,  texPos0.y), lambda) * w0.x * w0.y;
    result += sampleTex( vec2(texPos12.x, texPos0.y), lambda) * w12.x * w0.y;
    result += sampleTex( vec2(texPos3.x,  texPos0.y), lambda) * w3.x * w0.y;

    result += sampleTex( vec2(texPos0.x,  texPos12.y), lambda) * w0.x * w12.y;
    result += sampleTex( vec2(texPos12.x, texPos12.y), lambda) * w12.x * w12.y;
    result += sampleTex( vec2(texPos3.x,  texPos12.y), lambda) * w3.x * w12.y;

    result += sampleTex( vec2(texPos0.x,  texPos3.y), lambda) * w0.x * w3.y;
    result += sampleTex( vec2(texPos12.x, texPos3.y), lambda) * w12.x * w3.y;
    result += sampleTex( vec2(texPos3.x,  texPos3.y), lambda) * w3.x * w3.y;

    return result;
}

