float a = 0.2;
float r = 1.0;
float helixThickRad = 0.3;
float threshold = 0.001;
float zeroThreshold = 0.001;

float TWOPI = 6.283;
float PI = 3.14159;

vec3 sphereP = vec3(0.0, 0.0, 0.0);
float sphereRad = 1.0;


float f(float t, vec3 P)
{
   	float a = 0.8 + 0.7 * sin(2.0 * iTime);
    return r*r + length(P)*length(P) + 2.0*P.x*r*sin(t) - 2.0*P.z*r*cos(t) + a*a*t*t - 2.0*a*t*P.y;
}

float f1(float t, vec3 P)
{
    float a = 0.8 + 0.7 * sin(2.0 * iTime);
    return 2.0*P.x*r*cos(t) + 2.0*P.z*r*sin(t) + 2.0*a*a*t - 2.0*a*P.y;
}

bool sameSign(float a, float b)
{
    if((a >= 0.0)&&(b >= 0.0)) return true;
    if((a < 0.0)&&(b < 0.0)) return true;
    return false;
}


float findSolution(float t1, float t2, vec3 P)
{
   	if((f1(t1, P) > 0.0)&&(f1(t2, P) < 0.0)) return 1000000.0;

	float thalf;
	for(int i = 0; i < 10; i++)
	{
		thalf = 0.5 * (t1 + t2);
		float gradHalf = f1(thalf, P);
		if(abs(gradHalf) < 0.01) break;

		float d2_1 = f1(t1, P);
		float d2_half = gradHalf;
		float d2_2 = f1(t2, P);

		if(sameSign(d2_1, d2_half) == false)
		{
			t2 = thalf;
			continue;
		}

		t1 = thalf;
	}

	return thalf;
}

float helixDist(vec3 P, out float tout)
{
   	float a = 0.8 + 0.7 * sin(2.0 * iTime);

	float t0 = P.y / a;

	float closest = f(t0, P);
	float closestT = t0;


    for(float i = 0.0; i < 4.0; i+=1.0)
    {
        float tTest = findSolution(t0 + (i - 2.0)*PI, t0 + (i - 1.0)*PI, P);
        float dTest = f(tTest, P);

        if(dTest < closest)
        {
            closest = dTest;
			closestT = tTest;
        }
    }

	tout = closestT;
	return sqrt(closest);
}

vec3 helixPos(float t)
{
    float a = 0.8 + 0.7 * sin(2.0 * iTime);

    return vec3(-1.0 * r * sin(t), a * t, r * cos(t));
}

bool rayMarchHelix(vec3 rayPos, vec3 rayDir, out vec3 Pout, out vec3 Nout, out int countout)
{   
    vec3 P = rayPos;
    float t;
    float dist = helixDist(P, t) - helixThickRad;

    int count = 0;

    while(dist > threshold)
    {
        P += dist * rayDir;
        if(P.z > 2.2) return false;

        dist = helixDist(P, t) - helixThickRad;

        if(count == 128) return false;

        count++;
    }

    Pout = P + dist * rayDir;
    Nout = normalize(Pout - helixPos(t));
    countout = count;

    return true;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 imgPlane = 5.0 * (2.0 / iResolution.x) * vec2(fragCoord.x - 0.5*iResolution.x, fragCoord.y - 0.5 * iResolution.y);

    vec3 rayPos = vec3(0.0, 0.0, -5.0);
    vec3 rayDir = normalize(vec3(imgPlane.x, imgPlane.y, 5.0));

    vec3 P, N;
    int count;

    if(rayMarchHelix(rayPos, rayDir, P, N, count))
    {
        float radiance = 0.0;
        vec3 L;
        float ldiff, lspec;

        for(int i = 0; i < 2; i++)
        {
            if(i == 0)
            {
               L = vec3(3.0, 2.0, -3.0);
               ldiff = 200.0;
               lspec = 0.4;
            }

            if(i == 1)
            {
                L = vec3(-10.0, 2.0, 10.0);
              	ldiff = 200.0;
               	lspec = 0.4;
            }

       		vec3 PL = L - P;

       		float diff = (ldiff  / (4.0 * PI * pow(length(PL), 2.0))) * (1.0 / PI) * dot(normalize(PL), N);
       		if(diff < 0.0) diff = 0.0;

       		vec3 EP = normalize(P - rayPos);
       		vec3 specVec = EP - 2.0 * (dot(N, EP) * N);
       		float spec = lspec * pow(dot(specVec, normalize(PL)), 5.0);
       		if(spec < 0.0) spec = 0.0;

           	radiance += (diff + spec);
       	}	

    	fragColor = vec4(vec3(radiance), 1.0);
    }

    else fragColor = vec4(0.0);
}

const float PI = 3.14159;
const float PI2 = PI*2.;

float dtoa(float d, float amount){
    return 1. / clamp(d*amount, 1., amount);
}
float nsin(float x) {
    return cos(x)*.5+.5;
}
vec3 hash32(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 19.19;
    p *= p + p;
    return fract(p);
}

float check(float a, float r, float t) {
    if (r < 0.0001) return 0.;
    float z = 1./r;
    z-=t;
    a += sin(z);
    return min(min(nsin(a*4.),nsin(a*14.)),pow(nsin(z*24.),.4));
}

void mainImage( out vec4 o, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-.5;
    vec2 N = uv;
    uv.x *= iResolution.x / iResolution.y;

    float t = -iTime + 2.;

    float sd = 1e6;
    float a = atan(uv.x,uv.y);

    float ch = check(a+t*.1, length(uv), t);
    o.rgb = N.xyy * vec3(ch) * pow(length(uv),2.) * 4.;

    float sg = 3.;
    float r = .44;
    float bh = 1.;
    for (int i = 0; i < 40; ++ i) {
        float id = hash11(float(i));
        sg = -sg;
        r *= .96;
        float h = nsin(a += id*PI2)*.05+.01;
        h *= bh-= .03;
        float rout = nsin((a)*18.)*pow(nsin(8.*a+t*sg),2.)*h+r;
        sd = abs(length(uv)-rout);
        o[i%3] += dtoa(sd, nsin(t)*1000. + 250.);
    }

    o.g += o.r*.4;
    o = clamp(o,o-o,o-o+1.);
    o = pow(o,o-o+.5);
    o.rgb -= (hash32(fragCoord+iTime))*.1;//noise
    o *= 1.-length(12.*pow(abs(N), vec2(4.)));// vingette
    o.a = 1.;
}

// All the distance functions from:http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
// Edge detection code from:https://www.shadertoy.com/view/MsSGD1
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)
#define hash(h) fract(sin(h) * 43758.5453123)
#define EDGE_WIDTH 0.05
#define PI 3.141592653589793
#define SLIME_BODY_COLOR vec3(0.1,0.9,1.0)

vec4 combine(vec4 val1, vec4 val2 ){
    if ( val1.w < val2.w ) {
        return val1;
    }
    return val2;
}

float sdRoundCone( in vec3 p, in float r1, float r2, float h )
{
    vec2 q = vec2( length(p.xz), p.y );

    float b = (r1-r2)/h;
    float a = sqrt(1.0-b*b);
    float k = dot(q,vec2(-b,a));

    if( k < 0.0 ) return length(q) - r1;
    if( k > a*h ) return length(q-vec2(0.0,h)) - r2;

    return dot(q, vec2(a,b) ) - r1;
}

float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
    vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
    return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}

float sdArc( in vec2 p, in vec2 sca, in vec2 scb, in float ra, float rb )
{
    p *= mat2(sca.x,sca.y,-sca.y,sca.x);
    p.x = abs(p.x);
    float k = (scb.y*p.x>scb.x*p.y) ? dot(p.xy,scb) : length(p.xy);
    return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

vec3 crownTex(vec2 uv) {
    vec3 col = vec3(1.0,0.1,0.9);
    col = (uv.x>=-0.3 && uv.x< 0.3)? vec3(0.0,1.0,0.0):col;
    uv *= rot(radians(90.0));
    col = (uv.x>=-0.3 && uv.x< 0.3)? vec3(0.0,1.0,0.0):col;
    return col;   
}

vec4 sdCrown(vec3 p, vec3 crownPos) {
    int num = 8;
    float res = 4.0;
    float prevres = 0.0;
    float deg = 360.0/float(num);
    float crownDist = 1.0;

    for(int i = 0; i<num; i++){
        float rad = radians(deg*float(i));
        float x = cos(rad)*crownDist;
        float z = sin(rad)*crownDist;
        float sp = length(p+vec3(x,-0.75,z)+crownPos)-0.15;
        res = min(res,sdRoundCone((p+vec3(x,0.0,z)+crownPos),0.23,0.1,0.5));
        res = min(res,sp);
    }

    float rcd1 = sdRoundedCylinder(p+vec3(0.0,0.2,0.0)+crownPos, 0.58,0.28,0.07);
    res = min(res,rcd1);

    float rcd2 = sdRoundedCylinder(p+vec3(0.0,-0.6,0.0)+crownPos, 0.45,0.6,0.02);
    float rcd3 = sdRoundCone(p+vec3(0.0,-1.2,0.0)+crownPos, 0.23,0.1,0.5);

    vec4 crownBtm = vec4(vec3(1.1,0.9,0.0),res);
    vec4 crownMid = vec4(crownTex(p.xz),rcd2);
    vec4 crownTop = vec4(vec3(1.1,0.9,0.0),rcd3);

    return combine(combine(crownBtm,crownMid),crownTop);
}

vec3 slimeFaceTex(vec3 p) {
    vec2 uv = p.xy;
    vec2 uvRef = uv;
    vec3 col = SLIME_BODY_COLOR;

    uvRef.x = abs(uvRef.x)-0.21;
    float eyeBg = length(uvRef+vec2(0.0,0.6))-0.15;
    float eye = length(uvRef+vec2(0.0,0.6))-0.13;
    float eyeBall = length(uvRef+vec2(0.0,0.6))-0.03;
    uvRef = uv;

    float mouth = sdArc( (uvRef+vec2(0.0,0.2))*rot(radians(225.0)), vec2(0.6,0.61), vec2(0.2,0.3), 0.71, 0.085 );
    float toungue = sdArc( (uvRef+vec2(0.0,0.2))*rot(radians(225.0)), vec2(0.6,0.6), vec2(0.16,0.3), 0.7, 0.05 );

    col = mix( col, vec3(0.0), 1.0-smoothstep(0.01,0.015,eyeBg) );
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.01,0.015,eye) );
    col = mix( col, vec3(0.0), 1.0-smoothstep(0.01,0.015,eyeBall) );
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.01,0.015,mouth) );
    col = mix( col, vec3(0.7,0.0,0.0), 1.0-smoothstep(0.01,0.015,toungue) );  

    col = (p.z>0.01)? col:SLIME_BODY_COLOR;

    return col;
}

vec4 sdBody(vec3 p) {
    float anim = sin(iTime*5.0)*0.015;
    float rcd = sdRoundedCylinder(p+vec3(0.0,1.2,0.0), 1.0+anim,1.1,0.02);
    vec4 body = vec4(slimeFaceTex(p),rcd);

    p.x = abs(p.x)-0.55;
    vec4 cheek = vec4(SLIME_BODY_COLOR,length(p+vec3(0.0,0.9,-1.65))-(0.3+anim*2.0));

    return combine(body,cheek);
}

float sinOut(float t) {
  return sin(t * PI*0.5);
}

float elasticOut(float t) {
  return sin(-13.0 * (t + 1.0) * PI*0.5) * pow(2.0, -10.0 * t) + 1.0;
}

float bounceOut(float t) {
  const float a = 4.0 / 11.0;
  const float b = 8.0 / 11.0;
  const float c = 9.0 / 10.0;

  const float ca = 4356.0 / 361.0;
  const float cb = 35442.0 / 1805.0;
  const float cc = 16061.0 / 1805.0;

  float t2 = t * t;

  return t < a
    ? 7.5625 * t2
    : t < b
      ? 9.075 * t2 - 9.9 * t + 3.4
      : t < c
        ? ca * t2 - cb * t + cc
        : 10.8 * t * t - 20.52 * t + 10.72;
}

// A smart way to control the animation. function is from "http://qiita.com/gaziya5/items/29a51b066cb7d24983d6"
float animscene(in float t, float w, float s) {
    return clamp(t - w, 0.0, s) / s;  
}

vec4 map(vec3 p){
    float animTime = mod(iTime,3.5);
	float jump = 0.0;
    float jumpDist = 0.5;
	jump -= sinOut(animscene(animTime, 0.5, 0.3))*jumpDist;
	jump += bounceOut(animscene(animTime, 0.8, 0.6))*jumpDist;

    float jump2 = 0.0;
	jump2 -= sinOut(animscene(animTime, 0.52, 0.3))*jumpDist;
	jump2 += bounceOut(animscene(animTime, 0.82, 0.6))*jumpDist;

    float jump3 = 0.0;
    float floorHeight = 0.2;
	jump3 += sinOut(animscene(animTime, 1.02, 0.3))*floorHeight;
	jump3 -= elasticOut(animscene(animTime, 1.22, 2.5))*floorHeight;

    vec3 crownPos = vec3(0.0,-0.3+jump2,0.0);
    vec4 crown = sdCrown(p,crownPos); 
    vec4 body = sdBody(p+vec3(0.0,jump,0.0));

    float height = (sin(p.x*jump3)+sin(p.z*jump3));
    vec4 f = vec4(vec3(0.0,0.9,0.3),p.y+3.0+height);
    return  combine(combine(crown,body),f);
}

vec3 normalMap(vec3 p){
    float d = 0.0001;
    return normalize(vec3(
        map(p + vec3(  d, 0.0, 0.0)).w - map(p + vec3( -d, 0.0, 0.0)).w,
        map(p + vec3(0.0,   d, 0.0)).w - map(p + vec3(0.0,  -d, 0.0)).w,
        map(p + vec3(0.0, 0.0,   d)).w - map(p + vec3(0.0, 0.0,  -d)).w
    ));
}

float shadowMap(vec3 ro, vec3 rd){
    float h = 0.0;
    float c = 0.001;
    float r = 1.0;
    float shadow = 0.5;
    for(float t = 0.0; t < 30.0; t++){
        h = map(ro + rd * c).w;
        if(h < 0.001){
            return shadow;
        }
        r = min(r, h * 16.0 / c);
        c += h;
    }
    return 1.0 - shadow + r * shadow;
}

// from simon green and others
float ambientOcclusion(vec3 p, vec3 n)
{
    const int steps = 4;
    const float delta = 0.15;

    float a = 0.0;
    float weight = 4.;
    for(int i=1; i<=steps; i++) {
        float d = (float(i) / float(steps)) * delta; 
        a += weight*(d - map(p + n*d).w);
        weight *= 0.5;
    }
    return clamp(1.0 - a, 0.0, 1.0);
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
    vec3 cw = normalize(ta-ro);
    vec3 cp = vec3(sin(cr), cos(cr),0.0);
    vec3 cu = normalize( cross(cw,cp) );
    vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 uv = p;

    float time = iTime*2.0;

    vec3 ro = vec3( 3.5*cos(0.1*time + 6.0), 0.0, -0.5+5.5*sin(0.1*time + 6.0) );
    vec3 ta = vec3( 0.0, -0.4, -0.7 );
    mat3 ca = setCamera( ro, ta, 0.0 );
    float zoom = 1.5;
    vec3 rd = ca * normalize( vec3(p.xy,zoom) );

    float t, dist;
    float lastDistEval = 1e10;
    float edge = 0.0;
    t = 0.0;
    vec3 distPos = ro+rd;
    vec4 distCl = vec4(0.0);
    for(int i = 0; i < 64; i++){
        distCl = map(distPos);
        dist = distCl.w;
        t += dist;
        distPos = ro+rd*t;

        if (lastDistEval < EDGE_WIDTH && dist > lastDistEval + 0.001) {
            edge = 1.0;
        }
        if (dist < lastDistEval) lastDistEval = dist;
        if(dist < 0.01 || dist > 30.0) break;
    }

    vec3 color;
    float shadow = 1.0;
    if(dist < 1.0){
        // lighting
        vec3 lightDir = vec3(0.0, 1.0, 0.0);
        vec3 light = normalize(lightDir + vec3(0.5, 0.0, 0.9));
        vec3 normal = normalMap(distPos);

        // difuse color
        float diffuse = clamp(dot(light, normal), 0.5, 1.0);
        float lambert = max(.0, dot( normal, light));

        // ambient occlusion
        float ao = ambientOcclusion(distPos,normal);

        // shadow
        shadow = shadowMap(distPos + normal * 0.001, light);

        // result
        color += vec3(lambert);
        color = ao*diffuse*(distCl.xyz+(.1-length(p.xy)/3.))*vec3(1.0, 1.0, 1.0);

    }else{
        color =.84*max(mix(vec3(0.6,0.9,1.0)+(.1-length(p.xy)/3.),vec3(1),.1),0.);

        uv.x += iTime*0.1;
        float triangleWave = asin(sin(uv.x*3.2))*0.2;
        color = mix( color, vec3(0.4,0.2,0.01), 1.0-smoothstep(0.01,0.015,p.y+0.05+triangleWave) );

        triangleWave = asin(sin(uv.x*3.7))*0.2;
        color = mix( color, vec3(0.5,0.2,0.01), 1.0-smoothstep(0.01,0.015,p.y-0.05+triangleWave) );
    	uv = p;
    }

    // rendering result
    float brightness = 1.5;
    vec3 dst = (color * max(0.8, shadow))*brightness;

    // add edge detection result
    dst = mix(dst,vec3(0.1,0.1,0.1),edge);

    // UI
    vec3 uicol = vec3(0.0);
    vec2 dialogPos = vec2(1.05,-0.65);
    float dialogBg = sdBox(uv+dialogPos, vec2(0.5,0.1));
    uicol = mix( uicol, vec3(0.9), 1.0-smoothstep(0.1,0.09,dialogBg) );
    uicol = mix( uicol, vec3(0.1), 1.0-smoothstep(0.12,0.1,dialogBg) );

    float fakeChar0 = sdBox(uv+ vec2(1.5,dialogPos.y), vec2(0.05,0.05));
    uicol = mix( uicol, vec3(0.5), 1.0-smoothstep(0.01,0.015,fakeChar0) );
    float fakeChar1 = length(uv+ vec2(1.35,dialogPos.y))-0.05;
    uicol = mix( uicol, vec3(0.5), 1.0-smoothstep(0.01,0.015,fakeChar1) );
    float fakeChar2 = sdBox(uv+ vec2(1.2,dialogPos.y), vec2(0.05,0.05));
    uicol = mix( uicol, vec3(0.5), 1.0-smoothstep(0.01,0.015,fakeChar2) );
    float fakeChar3 = length(uv+ vec2(1.05,dialogPos.y))-0.05;
    uicol = mix( uicol, vec3(0.5), 1.0-smoothstep(0.01,0.015,fakeChar3) );
    float fakeChar4 = sdBox(uv+ vec2(0.9,dialogPos.y+0.04), vec2(0.05,0.005));
    uicol = mix( uicol, vec3(0.5+sin(iTime*9.0)*0.5), 1.0-smoothstep(0.01,0.015,fakeChar4) );

    fragColor = vec4(dst+uicol, 1.0);
}

