#ifdef GL_ES
precision mediump float;
#endif


mat2 rotate(float angle)
{
    return mat2(cos(angle + vec4(0.0, 33.0, 11.0, 0.0)));
}

vec3 hsv(float hue, float saturation, float value)
{
    vec4 t = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(vec3(hue) + t.xyz) * 6.0 - vec3(t.w));
    return value * mix(vec3(t.x), clamp(p - vec3(t.x), 0.0, 1.0), saturation);
}

float plane(vec3 p, vec3 orgin, vec3 normal) 
{
	return dot(p - orgin, normal);
}

float tetrahedron(vec3 p, float scale) 
{
    float a = plane(p, vec3( scale,  scale,  scale), vec3(-1.0,  1.0,  1.0));
	float b = plane(p, vec3( scale, -scale, -scale), vec3( 1.0, -1.0,  1.0));
	float c = plane(p, vec3(-scale,  scale, -scale), vec3( 1.0,  1.0, -1.0));
	float d = plane(p, vec3(-scale, -scale,  scale), vec3(-1.0, -1.0, -1.0));
	return max(max(a, b), max(c, d));
}

float map(vec3 p) 
{
    p.xy *= rotate(iTime * 0.6);
	p.xz *= rotate(iTime * 0.6);
    return tetrahedron(p, 0.5);	
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{	
	vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / min(iResolution.x, iResolution.y);

	vec3 color = vec3(0.0);

	vec3 ro = vec3(uv, -1.0);
	vec3 rd = vec3(0,0, 0.5);

	float t = 0.0;
	for (int i = 0; i < 35; ++i) 
	{
	    vec3 p = ro + rd * t;
	    float d = map(p);
	    t += d;
	    color += 1.0 / t;
	    color *= hsv(uv.x * 10.0, 0.5, 1.0);
	    color *= hsv(uv.y * 10.0, 0.5, 1.0);
	}

	if (uv.y > 0.0)
	{
	    color *= 0.5;
	}
	else
	{
	    color += vec3(0.0, 0.0, 0.1);
	}

	fragColor = vec4(pow(color, vec3(0.4545)), 1.0);
}

void mainImage(out vec4 O, in vec2 u) {
    vec2 R = iResolution.xy / 2.0,
         U = u / 1024.0;

    float noise = textureLod(iChannel0, U, 0.0).x;
    if (u.y < R.y) noise = fract(noise + iTime);
    if (u.x > R.x) noise = abs(2.0 * noise - 1.0);
    O = vec4(noise);

    u = abs(u - R);
    if (u.x < 1.0 || u.y < 1.0)
        O.rgb = vec3(0.75);
}

