vec2 foliage( in vec3 pos) {

    pos.y=pos.y-pathterrain(pos.x,pos.z);

    float Strand, Spread, Lift, Size;
    float sqr = 1.;
    float d = length(pos.xz)+1.;
    vec3 pfract = fract(pos / sqr) * sqr;
    vec3 pround = round(pos / sqr) * sqr + vec3(sqr * 0.5, 0, sqr * 0.5);
    vec3 mos = vec3(pfract.x, pos.y, pfract.z);

    float i = (pos - pfract).x;
    float j = (pos - pfract).z;

    Strand = 0.01 + fract( sin(i * 12. + j*9.9) * 9.219869) * 0.0325;
    Spread = 0. + fract( sin (i * 12. + j*9.9) * 99.24927) * .3;
    Lift = .2 + fract(  sin(i * 42. + j*32.9) * 199.210930) * .5;
    Size = .2 + fract( sin (i * 2. + j) * 99.) * .7;

    d = min(d, plant(mos - vec3(sqr * 0.5, 0., sqr * 0.5), vec4(Strand, Spread * 1.1, Lift, Size)));

      d = max(d, -(length(pfract.xz - vec3(sqr * 0.5, 0., sqr * 0.5).xz) -0.01));

    return vec2(d, 3);

} 

