float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( hash12( i + vec2(0.0,0.0) ), 
                     hash12( i + vec2(1.0,0.0) ), u.x),
                mix( hash12( i + vec2(0.0,1.0) ), 
                     hash12( i + vec2(1.0,1.0) ), u.x), u.y);
}
float noise13( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    return mix( 
        mix(
                mix( hash13( i + vec3(0.0,0.0,0.0) ), 
                     hash13( i + vec3(1.0,0.0,0.0) ), u.x),
                mix( hash13( i + vec3(0.0,1.0,0.0) ), 
                     hash13( i + vec3(1.0,1.0,0.0) ), u.x)
               , u.y),
              mix(
                mix( hash13( i + vec3(0.0,0.0,1.0) ), 
                     hash13( i + vec3(1.0,0.0,1.0) ), u.x),
                mix( hash13( i + vec3(0.0,1.0,1.0) ), 
                     hash13( i + vec3(1.0,1.0,1.0) ), u.x)
               , u.y)

         , u.z)
        ;
}

vec3 noise33(  vec3 p )
{
    p=p+10000.;
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u;
    u.x = f.x*f.x*(3.0-2.0*f.x);
    u.y = f.y*f.y*(3.0-2.0*f.y);
    u.z = f.z*f.z*(3.0-2.0*f.z);

    return mix( 
        mix(
                mix( hash33( i + vec3(0.0,0.0,0.0) ), 
                     hash33( i + vec3(1.0,0.0,0.0) ), u.x),
                mix( hash33( i + vec3(0.0,1.0,0.0) ), 
                     hash33( i + vec3(1.0,1.0,0.0) ), u.x)
               , u.y),
              mix(
                mix( hash33( i + vec3(0.0,0.0,1.0) ), 
                     hash33( i + vec3(1.0,0.0,1.0) ), u.x),
                mix( hash33( i + vec3(0.0,1.0,1.0) ), 
                     hash33( i + vec3(1.0,1.0,1.0) ), u.x)
               , u.y)

         , u.z)
        ;
}
vec3 fbm33( vec3 p )
{

     mat3 m = mat3(vec3(0.80,  0.60, -0.6).xyz,
                    vec3(0.80,  0.60, -0.6).yzx,
                    vec3(0.80,  0.60, -0.6).zxy);
    vec3 f = vec3(0.0);
    f += 0.5000*noise33( p ); p = m*p*2.02;
    f += 0.2500*noise33( p ); p = m*p*2.03;
    f += 0.1250*noise33( p ); p = m*p*2.01;
    f += 0.0625*noise33( p );
    return f/0.9375;
}

vec3 getGrassColor(float x) {
     vec3 a = vec3(0.3, 0.3, 0.3);
    vec3 b = vec3(0.4, 0.5, 0.2);
    vec3 c = vec3(0.2, 0.4, 0.2);
    vec3 d = vec3(0.66, 0.77, 0.33);
    vec3 col = a + b * cos(2. * PI * (c * x + d));
    return col;
}

float getGrassBlade(in vec2 position, in vec2 grassPos, out vec4 color) {

       // grassPos+= abs(noise33(vec3(grassPos*.41,0.)*vec3(3,.0,1)).xy-0.5 )*.5;


	// between {-1, -1, -1} and {1, 1, 1}
    vec3 grassVector3 = hash32(grassPos * 123512.41) * 2.0 - vec3(1);
    // keep grass z between 0 and 0.4
    grassVector3.z = grassVector3.z * 0.2 + 0.2;
    vec2 grassVector2 = normalize (
     //vec2(sin( 1.5+iTime*0.1),cos(1.5+iTime*0.1))*2.
     (UV)  *1.5 

    +   grassVector3.xy*.6
    )     ;

    float grassLength = noise13(vec3(grassPos*1.5,10.)) * 0.01 + 0.012*length(UV) ;

    // take coordinates in grass blade frame
    vec2 gv = position - grassPos;
    float gx = dot(grassVector2, gv);
    float gy = dot(vec2(-grassVector2.y, grassVector2.x), gv);
    float gxn = gx / grassLength;

    // TODO make gy depends to gx
    if (gxn >= 0.0 && gxn <= 1.0 && abs(gy) <= 0.0015 * (1. - gxn * gxn)) {

        vec3 thisGrassColor = 
            getGrassColor(hash12(grassPos * 2631.6));
        color =
            vec4(thisGrassColor * (0.2 + 0.8 * gxn) 
            + (fbm33(vec3(position*1.1,0.)*vec3(1,3,1))-0.5 )*.2
            + (fbm33(vec3(position*10.1,0.)*vec3(5,1,1))-0.5 )*.2
                 , 1.0);

     	return grassVector3.z * gxn;
    }
    else {
        color = vec4(0., 0., 0., 1.);
        return -1.0;
    }
}

float getPoint(in vec2 position, out vec4 color) {
   	int xcount = int(1. / BLADES_SPACING);
    int ycount = int(1. / BLADES_SPACING);
    int ox = int(position.x * float(xcount));
    int oy = int(position.y * float(ycount));

    float maxz = 0.0;
            vec4 tempColor =vec4(vec3(0.1,0.35,0)+fbm33(vec3(position*129.,0.)-.5),0.)*.1;

    for (int i = -LOOKUP_DIST; i < LOOKUP_DIST; ++i) {
        for (int j = -LOOKUP_DIST; j < LOOKUP_DIST; ++j) {
            vec2 upos = vec2(ox + i, oy + j);
            vec2 grassPos = (BLADES_SPACING+0.005*BLADES_SPACING*fbm33(vec3(upos*179.,0.)-.5)).x*  (upos 
                             + 4.5* (noise33(vec3(upos*50.,upos.x+upos.y)).xy-.5) );

            float z = getGrassBlade(position, grassPos, tempColor);

            if (z > maxz) {
                maxz = z;
                color = mix(color, tempColor,0.85);
            }
        }
    }
    if (maxz == 0.0) {
        color = vec4(vec3(0.1,0.35,0)+fbm33(vec3(position*129.,0.)-.5),0.)*.12;;
    }

    return maxz;
}

