float distanceToLine(vec2 p1, vec2 p2, vec2 point) {
    float a = p1.y-p2.y;
    float b = p2.x-p1.x;
    return abs(a*point.x+b*point.y+p1.x*p2.y-p2.x*p1.y) / sqrt(a*a+b*b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Output to screen
    vec2 uv = fragCoord.xy / iResolution.xy;

    //Pretty colors from default shader
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    //flatten out waves
    uv.x /= 2.0;

    //how many tiles
    float frac = iResolution.x/22.0;

    //value for which row in the tiling the pixel is in
    float weird = floor(uv.y*frac);

    //if the pixel is in a odd or even column of the tiling
    float even = mod(floor(uv.x*frac),2.0);

    //offset the oscillation of the endpoint on the top of each tile
    float calc = (sin((iTime*2.0)+(weird/4.0)+(even*3.14))/4.0)+.5;

    //declare endpoints to for the two lines in each tile
    vec2 p1 = vec2(calc,floor(uv.y+1.0));
    vec2 p2 = vec2(0.,0.1);
    vec2 p3 = vec2(1.0,0.1);

    //tile our 2space
    uv = fract(uv*frac);

    //thickness of lines
    float radius =0.005;

    //aliasing of lines
    float AA = 0.015;

    //distances from our pixel to the lines we want to draw
    float d = min(distanceToLine (p1,p2,uv),distanceToLine (p1,p3,uv));
	float line = 0.0;
    //smoothstep for drawing our lines
    if(d < radius) {
        line = 1.0;
	} else {
	  // Anti-alias the edge.
        line = 1.0 - smoothstep(d, radius, radius+AA);
	}
   	//float line =  smoothstep (radius/2.,radius/2.-AA,distance);

    //drawing lines
    fragColor = vec4(col*line,1.0);

}

