				Box[i].rad = vec3(0.4+ abs(sin(freq * iTime-time))/10.0, 0.4+ abs(sin(freq * iTime-time)),0.4+ abs(sin(freq * iTime-time))/10.0);

			if (Box[i].rad.y > 0.6) 
				Box[i].materialID = modeNum;
			else 
				Box[i].materialID = 1;
		}
		else
		{
			Box[i].rad = vec3(0.4+ abs(sin(freq * iTime-time))/10.0, 0.4+ abs(sin(freq * iTime-time+10.0*float(i/5)+ 10.0*float(i-(i/5)*5)))/2.0,0.4+ abs(sin(freq * iTime-time))/10.0);
