    float b = dot(d, rd);
    if(b<0.)
        return 1.0;
   	vec3 c = ro+rd*b;
    float s = length(c-p)/radius;
    return max(0.,(min(1.,s)-.7)/.3);
}

float shadow(vec3 ro, vec3 rd, float id)
{
    float v = 1.;
    for(float i=0.; i<sphereNum;i++)
    {
        if(blinkID[int(i)])
            continue;
        if(i!=id)
        {    
            float tt = mt+i*2.;
            float ds = shadowSphere(ro, rd, spherePos[int(i)], sRadius);
            v*=ds;
            if(ds==0.)
                return 0.;
        }
    }
    return v*v*(3.-2.*v);
}
float lineDist(vec3 p, vec3 a, vec3 b)
{
    vec3 pa = p-a;
    vec3 ba = b-a;
    float t = clamp(dot(pa,ba)/dot(ba,ba),0.,1.);
    return length(pa-ba*t);
}
float line(vec3 p, vec3 a, vec3 b)
{
    float d= lineDist(p, a, b);
    float m = smoothstep(.3, .01, d);
    return m;
}

vec3 sphere(vec3 ro, vec3 rd, vec3 p, float radius, float id)
{

    vec3 d = ro-p;
    float a = dot(rd, rd);
    float b = dot(d, rd);
    float c = dot(d,d)-radius*radius;

    float disc = b*b-a*c;
    if(disc<0.)
        return vec3(-1.);

    float sqrtDisc = sqrt(disc);
    float invA = 1.0/a;

    vec3 hit = (ro+(rd*(-b-sqrtDisc)*invA));

    if(blinkID[int(id)])
        return vec3(1.,length(p+hit-ro),disc);

    vec3 norm = normalize(hit-p);
    vec3 ref = reflect(rd,norm);

    float dist = length(hit-ro);

    float litv = 0.;
    vec3 light = normalize(lightPos-(hit));
    float lit = 0.;
    float spec = 0.;

    float shade = shadow(hit, light, id);
    if(shade>0.)
    {


        lit = max(0.,dot(light, norm))*shade;
        spec = pow(max(0.,dot(light, ref)),100.);
        litv+=lit*.5+spec;
    }
    for(int i=0; i<numLights; i++)
    {
        vec3 light = normalize(spherePos[lightID[int(i)]]-(hit));
        float lit = 0.;
        float spec = 0.;

        float shade = shadow(hit, light, id);
        if(shade>0.)
        {


            lit = max(0.,dot(light, norm))*shade;
            spec = pow(max(0.,dot(light, ref)),3.);
            litv+=(lit+spec)*.2;
        }
    }
    ref +=iTime*.2;
    ref *=3.;
    float fresnel = (1.-dot(norm,-rd));
    float wall = fresnel*fresnel*fresnel*smoothstep(.3,0.0,min(4.*length(.5-fract(ref.x)),4.*length(.5-fract(ref.y))));
    vec3 v = vec3(max(0.,litv+wall));


    return vec3(v.x, length(p+hit-ro), disc);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    float lens = .99;
    uv *=lens+(1.-lens)*pow(dot(uv,uv),2.);

    vec2 suv = 4.*uv;
    suv +=iTime*.2;
    float muv = smoothstep(.1,0.0,min(4.*length(.5-fract(suv.x)),4.*length(.5-fract(suv.y))));
    vec3 col = vec3(.5+.2*muv);

    vec3 ro = vec3(0.,0.,-10.-(35.*(iMouse.y/iResolution.y)));
    vec3 rd = normalize(vec3(uv.x,uv.y,3.));

    lightPos = vec3(3.,10.+sin(iTime*.2)*10.,-5.);

    mt = iTime*.75+sin(iTime*.5)*.7+2.*iMouse.x/iResolution.x;

    float dep = 1000.;
    float halo = 0.;
    numLights = 0;
    for(float i=0.; i<sphereNum;i++)
    {
        float blink = fract(mt*length(random(vec2(i+1.))))<.1?1.0:0.;
        float tt = mt+i*2.;
        vec3 sp = vec3(4.*sin(tt*2.),4.*cos(tt*1.5),4.*cos(tt*2.));
        spherePos[int(i)] = sp;
        blinkID[int(i)] = blink>0.;
        if(blinkID[int(i)])
        {
            lightID[numLights]= int(i);
            numLights++;
            halo = max(0.,1.0-length(ro+rd*dot(sp-ro,rd)-sp)*.15);
        }

    }
    vec3 a = vec3(-100.);
    vec3 b = vec3(-100.);
    float keepHighest = 0.;
    for(float i=0.; i<sphereNum;i++)
    {
        if( blinkID[int(i)])
        {

            vec3 sd = ro+rd*dot(spherePos[int(i)] -ro,rd)-spherePos[int(i)];
            halo = 1./dot(sd,sd)*.5;
            if(a.x<-99.)
            	a = sd;
            else if(b.x<-99.)
                b = sd;
        }
        if(a.x>-100.&&b.x>-100.)
            halo+=.15*line(vec3(rd.xy,0.),vec3(a.xy,0.),vec3(b.xy,0.));

        vec3 d = sphere(ro, rd, spherePos[int(i)] , sRadius, i);
        keepHighest = max(keepHighest, d.y);
        if(d.x>-1.)
            if(d.y<dep)
            {
                float edge = min(d.z,.1)/.1;
                edge *=edge *(3.-2.*edge); 
                dep = d.y;
                d.y = (d.y-(ro.z+20.))/80.;
                col =mix(col,mix(vec3(d.x,d.x,d.x),vec3(.5),d.y),edge)+vec3(blinkID[int(i)]?1.:0.);
            }
        if(dep>keepHighest&&blinkID[int(i)])
        {
            col+=halo;//halo*halo*+(3.-2.*halo)*.1;
        }
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

vec2 path(float z){
    float x = sin(z) + 2.0 * cos(z * 0.3) - 1.5 * sin(z * 0.12345);
    float y = cos(z) + 1.5 * sin(z * 0.3) + 2.0 * cos(z * 0.12345);
    return vec2(x,y);
}

float map(vec3 p){
	//p = fract(p/2.0) * 6.0 - 3.0;
    vec2 o = path(p.z) / 4.0;
	float tBase = max(-length(p.xy - o) + 1.0 
                      //* (1.0 + sin(p.z) * 0.5)
                      ,length(p.xy - o) - 1.2 
                      //* (1.0 + sin(p.z) * 0.5)
                     );
    float tdonut = max(-length(p.xy - o) + 0.5
                      ,length(p.xy - o) - 0.6 
                     );
    tdonut = max(tdonut,abs(fract(p.z) - 0.5));
    float tTube1 = length(p.xy - o + vec2(0.3,0.3)) - 0.025;
    float tTube2 = length(p.xy - o + vec2(0.5,0.2)) - 0.025;
    float tTube3 = length(p.xy - o + vec2(0.2,0.5)) - 0.025;    
    float tTube4 = length(p.xy - o + vec2(-0.3,-0.3)) - 0.025;
    float tTube5 = length(p.xy - o + vec2(-0.5,-0.2)) - 0.025;
    float tTube6 = length(p.xy - o + vec2(-0.2,-0.5)) - 0.025;
    float tTube = min(min(tTube1,tTube2),tTube3);
    tTube = min(min(tTube,tTube4),min(tTube5,tTube6));

    float bound = 2.0;
    float tSplit = (fract(p.z)-0.5);
    tSplit = min(tSplit, abs((p.x - o.x))-0.15);
    tSplit = min(tSplit, abs((p.y - o.y))-0.15);

    return min(min(max(tBase,-tSplit),tdonut),tTube);
}
const float EPS = 0.001;
vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 32; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return vec4(getNormal(p),t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float PI = 3.14159265;
    vec2 uv = fragCoord.xy /iResolution.xy;
	uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 r = normalize(vec3(uv,0.5));

    float z = iTime * 4.0 ;

    r.xy *= mat2( sin(iTime),cos(iTime),
    				-cos(iTime),sin(iTime));
	vec2 a = path(z);
    vec3 o = vec3(a / 4.0,z);
    vec4 data = trace(o,r);
    float t = data.w;
    float fog = 1.0 / (1.0 + t * t * 0.3);
    vec3 fc = mix(vec3(0.5 - data.x,0.5 -data.y,0.5-data.z),vec3(0),1.0 - fog);
	fc = vec3(fog);
    // Output to screen
    fragColor = vec4(fc,1.0);
}

