{ return max(length(p.xy)-radius, abs(p.z)-height); }

float unsigned_box (vec3 p, vec3 corner)
{ 
    vec3 q = abs(p)-corner;
    return length(max(q,0.)); 
}

float signed_box (vec3 p, vec3 corner)
{ 
    vec3 q = abs(p)-corner;
    return  min(0.,max(q.x,max(q.y,q.z))) + length(max(q,0.)); 
}

// Signed Distance Function or map
float SDF (vec3 p)
{
    float room = -signed_box(p, vec3(6.,3.,6.));

	// period for the repetition of the space
    float per = 2.;
    // id of each repetition cells in the x and z axis
    vec2 id_xz = floor(p.xz/per);
    p.xz = mod(p.xz, per)-per*0.5;
    // cylinders that cut holes in floor and ceiling
    float c = cylinder(p.xzy, 0.4, 1e10);

	// use the texnoise texture (in Bonzomatic) instead of typing a random function
    p.y += time*texNoise(id_xz*0.1).r;
    p.y = mod(p.y,per)-per*0.5;
    // sphere changing to cube
    float s = mix(sphere(p, 0.3), signed_box(p, vec3(0.25)), sin(time)*0.5+.5);

    p.xz = mod(p.xz, per)-per*0.5;
    // columns
    float c1 = cylinder(p.xzy, 0.15, 1e10);

    // room + holes and columns
    float d = min(c1, max(-c, room));
    // adding moving spheres
    d = min(d,s );

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    float dither = hash(uv);

    // ro
    vec3 ray_origin = vec3(1.,0.,-4.); // step back
    // rd
    vec3 ray_direction = normalize(vec3(uv,1.)); //look forward
    // p
    vec3 position = ray_origin; // start from the origin

    vec3 color = vec3(0.);

    bool hit = false;

    float shad = 0.;

    for(float i=0.; i<iteration_number; ++i)
    {
        // d 
        float _distance = SDF(position);

        if (_distance < 0.01)
        {
            // store normaized iterations to colour shapes later
            shad = i / iteration_number;
            hit = true;
            break;
        }

        // replace banding by nice noise render
        _distance *= 0.9 + dither*0.1;

        position += _distance*ray_direction;
    }

    if (hit) color = vec3(1.-shad); 

    //fragColor = vec4(color.r, color.g, color.b, 1.);
    fragColor = vec4(color,1.);
}

