{
	return 2.0 * hash( iTime * p.x + p.y * 57.1235 ) - 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = iResolution.y / iResolution.x;
    vec2 uv = fragCoord / iResolution.xy;
    uv.y *= ratio;
    vec2 center = vec2(0.5, 0.5 * ratio);
    uv -= center;

    // Anti-aliasing samples
    float samples = 4.0;

    float finalCol = 0.0;

    for (int a = 0; a < int(samples); ++a) {
        vec2 uv = fragCoord / iResolution.xy;
       	float noiseScale = 0.25;
        uv.x += noiseScale * noise(float(a) * uv) / iResolution.x;
        uv.y += noiseScale * noise(float(a) * uv) / iResolution.y;
        uv.y *= ratio;
        vec2 center = vec2(0.5, 0.5 * ratio);
        uv -= center;

        vec2 coord = uv;

        for (int i = 0; i < 5; ++i) {
            // Causes the swipe to the bottom / top left
            float yfuzz = sin(0.005 * iTime) * 0.001;
            float xfuzz = cos(0.005 * iTime) * 0.4;
            xfuzz -= 1.0;
            yfuzz *= 1.0;
            // Dictates the mirroring of the coordinates once it passes a threshold
            // Smaller fstop means brighter image
            float fstop = 0.38;
            // If you want a hyperbola version centered around origin 
            fstop /= iMouse.z > 0.0 ? 28.0 * (uv.x * uv.y): 1.0;
            coord = fract(coord * fstop * sin(0.4 * iTime) * xfuzz + 0.5 + yfuzz) * 2.0 - 1.0;
            // Mirrored
            float len = (2.0 - sin(0.3 * iTime)) * dot(coord, coord);
            // Mirrorlen produces a scanline like effect
            //float mirrorlen = (2.0 - sin(0.75 * iTime) - cos(uv.x * iTime)) * dot(coord, coord);
            //coord = coord / len + 0.05 * coord / mirrorlen;
            coord = coord / len;
        }

        //float circle = length(coord);
        // Elongate the circle to form odd distorted smooth boxes
        // See unit circles in various norms:
        // https://en.wikipedia.org/wiki/Lp_space#/media/File:Vector-p-Norms_qtl1.svg
        float circle = length(coord * coord * coord);

        // The radius of the circle we're interested in shifts over time
        float radius = 1.5 + sin(iTime);
        // Otavio's neat-o trick for signed distance function
        float scale = dFdx(uv.x);

        float col = float((circle - radius) / scale > 0.0 ? 1.0 : 0.0);
        finalCol += col;
    }

    finalCol /= samples;

    // Debug mode makes it easy to see the circle start and end
    //col = texture(iChannel0, coord).xyz;

    fragColor = vec4(finalCol, finalCol, finalCol, 1.0);

}

float hash21(vec2 p) {
    // mod here
    p = mod(p, 6.);
    return fract(43758.5453123 * sin(dot(p, vec2(12.988, 243.233))));
