#if TONEMAP_TYPE == FILMIC_TONEMAP
vec3 Uncharted2Tonemap(vec3 x)
{
   return ((x*(A*x+C*B)+D*E)/(x*(A*x+B)+D*F))-E/F;
}
#endif

vec3 Tonemap(vec3 color)
{
    #if TONEMAP_TYPE == LINEAR_TONEMAP
    color *= vec3(TONEMAP_EXPOSURE);
    #endif
    #if TONEMAP_TYPE == EXPONENTIAL_TONEMAP
    color = 1.0 - exp2(-color * TONEMAP_EXPOSURE);
    #endif
    #if TONEMAP_TYPE == REINHARD_TONEMAP
    color *= TONEMAP_EXPOSURE;
    color = color / (1.0 + color);
    #endif
    #if TONEMAP_TYPE == FILMIC_TONEMAP    
    color *= TONEMAP_EXPOSURE;
    vec3 tonemapedColor = Uncharted2Tonemap(TONEMAP_EXPOSURE_BIAS * color);
    vec3 whiteScale = 1.0 / Uncharted2Tonemap(vec3(W));
    color = tonemapedColor * whiteScale;
    #endif

    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec2 xy = fragCoord.xy / iResolution.xy;

    float intensity = INTENSITY;
    #if DAY_AND_NIGHT
    	intensity *= clamp(sin(time * DAY_AND_NIGHT_TIME) + DAY_AND_NIGHT_MIN_BRIGHTNESS, 
			   DAY_AND_NIGHT_MIN_BRIGHTNESS, 
			   DAY_AND_NIGHT_MAX_BRIGHTNESS);
    #endif

    EPSILON_NRM = 0.5 / iResolution.x;

    #if SIMULATE_MODE == SIMULATE
	SEA_CURRENT_TIME = iTime * SEA_SPEED;
    #elif SIMULATE_MODE == SIMULATE2x
	SEA_CURRENT_TIME = iTime * SEA_SPEED * 2.0;
    #elif SIMULATE_MODE == SIMULATE3x
	SEA_CURRENT_TIME = iTime * SEA_SPEED * 3.0;
    #elif SIMULATE_MODE == SIMULATE4x
	SEA_CURRENT_TIME = iTime * SEA_SPEED * 4.0;
    #elif SIMULATE_MODE == SIMULATE5x
	SEA_CURRENT_TIME = iTime * SEA_SPEED * 4.0;
    #elif SIMULATE_MODE == PAUSED
	SEA_CURRENT_TIME = 0.0;
    #elif SIMULATE_MODE == SLOW_MOTION
	SEA_CURRENT_TIME = iTime * SEA_SLOWMOTION_SPEED;
    #endif

    #if FLIP_WATER_AND_SKY
    vec3 dir = normalize(vec3(-uv.xy, -1.0));
    #else
    vec3 dir = normalize(vec3(uv.xy, -1.0));
    #endif
    dir.z += length(uv) * SEA_DIR_Z_SCALE;
    dir = normalize(dir);

    Material mat;
    #if FRESNEL_FORMULA == FRESNEL_SCHLICK_FORMULA
    mat.fR0 = SEA_R0;
    #endif
    mat.fSmoothFactor = SEA_SMOOTH_FACTOR;

    vec3 p = seaHeightMap(dir);
    vec3 lightDir = vec3(0.0, -1.0, 0.0);

    float smothMixFactor = pow(smoothstep(0.0, SMOOTH_MIX_Y, dir.y), MIX_SEA_AND_SKY_FACTOR);

    vec3 sky = sky(dir);
    vec3 sea = sea(p, lightDir, dir, mat);

    vec3 color = mix(sky, sea, smothMixFactor); 

    #if FOG_MODE != NEVER_FOG
    color = fog(color, dir.z);
    #endif

    #if RAIN
    vec3 rainColor = vec3(1.0, 1.0, 1.0) * 1.5;
    float rainFactor = rain(uv, xy);
    color = mix(color, rainColor, rainFactor);
    #endif

    color = color * intensity + COLOR_GRADING;

    #if STAR_SKY
    color += star(uv);
    #endif

    #if RAINBOW
    color += rainbow();
    #endif

    color = color * CONTRAST + 0.5 - CONTRAST * 0.5;

    float dc = length(uv) * 0.5;

    vec3 circCol = 1.0 * vec3(0.2, 0.9, 0.7);

    float y = 0.0;
    y += 0.2;
    y += circle(uv, dc, 1.0);
    y += circle(uv, dc, 0.5);
    y += circle(uv, dc, 0.2);
    y += grid(uv, y, 0.9);
    y += grid(uv, y, 0.1);	

    color += circCol * sqrt(pow(y, 1.5));

    if (line(uv, vec2(-0.005, -1.0), vec2(0.0015, 1.0)) ||
        line(uv, vec2(-2.0, -0.005), vec2(2.0, 0.0015)))
    {
		color = vec3(0.0, 0.2, 0.0);
    }

    #if APPLY_LUMINANCE
    float luminance = GetFragLuminance(color);
    luminance = saturate(luminance);
    vec3 resLuminance = vec3(length(color.r * luminance), 
			     length(color.g * luminance), 
			     length(color.b * luminance));

    color.rgb = resLuminance;
    #endif

    #if SEPIA_FILTER
    float greyScale = GetFragLuminance(color);
    color = greyScale * SEPIA_COLOR * SEPIA_INTENSITY;
    #endif

    #if VIGNETTE_FILTER
    color *= vec3(VIGNETTE_COLOR) * saturate(1.0 - length(uv / VIGNETTE_ZOOM)) * VIGNETTE_EXPOSURE;
    #endif

    #if APPLY_TONEMAP
    color = Tonemap(color);
    #endif

    #if APPLY_GAMMA_CORRECTION
    color = pow(color, vec3(1.0 / GAMMA));
    #endif

    fragColor = vec4(color, 1.0);
}

const float PI = 3.1415926;

float nPrism(vec3 p,vec2 h,float n,float s){
  float np = 2.0*PI/n;
  float r =  atan(p.x,p.y)-0.5*np;
  r = mod(r,np)-0.5*np;
  float ln = length(p.xy);
  vec2 kp = ln*vec2(cos(r),sin(r));
  vec3 kv = vec3(kp.x-h.x,abs(kp.y)-s*h.x*tan(np*0.5),abs(p.z)-h.y);
  float d = length(vec2(max(kv.x,0.0),max(kv.y,0.0)));
  d = length(vec2(max(kv.z,0.0),max(d,0.0)))-min(min(max(-kv.x,0.0),max(-kv.y,0.0)),max(-kv.z,0.0));
  return d;
}
float cube(vec3 p,vec3 s){
  vec3 q =abs(p);
  return length(max(q-s,0.0));
}

float crsp(vec3 p){
  float d =1.;
  return d;
}
float dist(vec3 p){
  float d = cube(p,vec3(0.6));
  float nd = nPrism(p,vec2(0.3,0.7),8.0+4.0*sin(iTime*1.3),0.7+0.3*(sin(iTime)));

  d = max(d,-nd);
  if(fract(iTime*0.4)>0.5)d =nd;
  return d;
}
vec3 getnormal(vec3 p){
  vec2 e = vec2(0.01,0.0);
  return normalize(vec3(
  dist(p + e.xyy)-dist(p - e.xyy),
  dist(p + e.yxy)-dist(p - e.yxy),
  dist(p + e.yyx)-dist(p - e.yyx)));
}
vec3 lighting(vec3 p){
  vec3 n = getnormal(p);
  vec3 ld =normalize( vec3(0.5,1,3));
  float ndl = dot(n,ld);
  vec3 col = vec3(ndl);
  return col;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.yy;
    vec3 tn = iTime*vec3(0.0,0.0,0.0);
    float tk = iTime;
    vec3 ro = vec3(5.*cos(tk),2.0,5.*sin(tk))+tn;
    vec3 ta = vec3(0.0,0.0,0.0)+tn;
    vec3 cdir = normalize(ta-ro);
    vec3 up =vec3(0.,1.,0.);
    vec3 side = cross(cdir,up);
    up = cross(side,cdir);
    float fov = 3.2;
    vec3 rd = normalize(p.x*side+p.y*up+cdir*fov);
    float t =0.0001;
    float d;
    vec3 ac=vec3(0.0);
    for(int i =0;i<100;i++){
      d = dist(ro+rd*t);
      t += d;
    }

    vec4 col = vec4(0.,0.,0.,1.);
    if(d<0.001){
    col.xyz = lighting(ro+rd*t);
    }
    fragColor = col;
}

