#define PI (3.14159265359)
#define time (PI*iTime) // 4 second loop

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // uv between -.5 - .5
	vec2 uv = fragCoord/iResolution.xy;
	uv -= vec2(.5);

    // square aspect
    uv.x *= iResolution.x/iResolution.y;

    vec3 c1; // blue part
    float s; // stripes
    {
        // angle & distance
        float a = atan(uv.x,uv.y);
        float d = length(uv);

        // waves
        d += 0.1;
        d += .3*pow(d,2.5)*sin(a*18.);
        d = pow(d,0.005);
        d *= 5999.;

        // rings
        d = .5+.5*sin(d-2.0*8.*time);

        // thicker at center
        float b = pow(d, 1.0);

        // fade at sides
        b *= 1.0-length(uv);

        // thin stripes at sides
        s = 1.0+1.0*length(uv)*sin(a*300.);
        b *= s;

        // color
        c1 = vec3(0,0,b);
    }

	vec3 c2; // pink/cyan part
	{
		float y = uv.y;
		float x = uv.x;

		// skew
		x -= sin(y*6.)/9.;

		// perspective
		x /= pow(y+sign(y),6.0);

		// waves
		float a = pow(abs(y)+.03,0.1)*32.;
		x += 0.03*sin(a-time);

		// stripes
		x = sin(x*30.);

		// color - cycle pink and cyan
		float b = pow(abs(x), 20.0);    
		float p = sin(time/2.)*.5+.5;
		c2 = vec3(b*p,b*(1.-p),b*.9);

		// apply inverse stripes
		c2 /= s;
	}

	fragColor = vec4(c1+c2*.5,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{      
    vec2 p=(fragCoord.xy*2.0-iResolution.xy)/iResolution.y;
	p=p*201.0+1.0;
	vec2 i=floor(p*0.5);
	vec2 f=mod(p,2.0)-1.0;

	float d=length(f);
	d=1.-smoothstep(0.6,0.85,d);

	float c=sin(iTime*5.-length(i)/101.*3.14*2.*1.5)*0.5+0.5;

	c*=d;
	c*=0.7;

	fragColor=vec4(c,c,c,1.);
}

#define UVScale 1.0
#define AnimSpeed 2.0
#define AnimScale sin(iTime) *0.65
#define m mat2(7, -5, 5, 7)*0.1

//Hash by Dave Hoskins: https://www.shadertoy.com/view/4djSRW
float hash13(vec3 p3)
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

//Forked from: https://www.shadertoy.com/view/MdKXDD
float FastCellular2D(vec2 p, int count, float speed)
{
	float o = 1.0;
	for (int i=0; i<count; i++)
	{
		p *= m;
        float h = hash13(vec3(floor(p),i));
        float s = sin(iTime*h *AnimSpeed *speed) *AnimScale +0.75;
		o = min(o, length(fract(p) - 0.5)/0.6 + s *0.5);
	}
	return o;
}

void mainImage(out vec4 col, in vec2 uv)
{
    float aspect = iResolution.x / iResolution.y;
    uv /= iResolution.xy / UVScale *0.1; uv.x *= aspect;
    uv.x += iTime *0.35;
    float ptn0 = FastCellular2D(uv *1.26, 5, 1.0);
    float ptn1 = FastCellular2D(uv *0.66, 5, 1.5);
    col = vec4(min(ptn0, ptn1)); 
}

float map(vec3 p){

        p.xz *= mat2(cos(iTime),-sin(iTime)
             ,sin(iTime),cos(iTime));
	float s1 = max(abs(length(p) - 1.8) - 0.05, max(p.x,-p.z));
        p.yz *= mat2(cos(iTime),-sin(iTime)
             ,sin(iTime),cos(iTime));
    float s2 = max(abs(length(p) - 1.5) - 0.05,max(p.y,-p.z));
        p.xy *= mat2(cos(iTime),-sin(iTime)
             ,sin(iTime),cos(iTime));
    float s3 = max(abs(length(p) - 1.2) - 0.05,max(p.x,-p.y));
            p.xz *= mat2(cos(iTime),-sin(iTime)
             ,sin(iTime),cos(iTime));
	float s4 = max(abs(length(p) - 0.9) - 0.05, max(-p.x,p.z));
            p.yz *= mat2(cos(iTime),-sin(iTime)
             ,sin(iTime),cos(iTime));
    float s5 = max(abs(length(p) - 0.6) - 0.05,max(-p.y,p.z));
            p.xy *= mat2(cos(iTime),-sin(iTime)
             ,sin(iTime),cos(iTime));
    float s6 = max(abs(length(p) - 0.3) - 0.05,max(-p.x,p.y));
    float holes = min((length(p.xy) - 0.2),min((length(p.xz) - 0.2),(length(p.yz) - 0.2)));
    return max(min(min(s1,min(s2,s3)),min(s4,min(s5,s6))),  -holes);
}

const float EPS = 0.001;
vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 96; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return vec4(getNormal(p),t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float PI = 3.14159265;
    vec2 uv = fragCoord.xy /iResolution.xy;
	uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 r = normalize(vec3(uv,1.2));

    vec3 o = vec3(0,0,-2.5);
    vec4 data = trace(o,r);
    float t = data.w;
    float fog = 1.0 / (1.0 + t * t * 0.1);
    vec3 fc = mix(vec3(0.5 - data.x,0.5 -data.y,0.5-data.z),vec3(1.0),1.0 - fog);
	//fc = vec3(fog);
    // Output to screen
    fragColor = vec4(fc,1.0);
}

