float N21(vec2 p)
{
    p = fract(p * vec2(8356.655414, 645.8778));
    p += dot(p, p + 72.724);
    return fract(p.x * p.y);
}
vec2 N22(vec2 p)
{
    float x = N21(p);
    float y = N21(p + x);
    return vec2(x, y);
}
float circleMask(vec2 uv, float radius)
{
    float vL = length(uv);
    float circle = smoothstep(radius, radius - 0.04, vL);
    return circle;
}
vec2 GetPos(vec2 id)
{
    vec2 n = N22(id) * iTime * 0.6;
    return sin(n) * .4;
}
float map(float value, float fromSource, float toSource, float fromTarget, float toTarget)
{
    return (value - fromSource) / (toSource - fromSource) * (toTarget - fromTarget) + fromTarget;
}
float Shines(vec2 uv, float size)
{
    uv *= size;
    uv.y -= iTime * 0.2;
    vec2 gv = fract(uv) - 0.5;
    vec2 id = floor(uv);
    float shineRate = map(abs(sin(iTime * N21(id))), -1.0, 1.0, 0.06, 0.2);
    vec2 p = GetPos(id);
    float d = length(gv - p);
    float m = smoothstep(0.1, 0.03, d) * shineRate;
    return m * 1.0;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
