    float posShine = 0.0;
    for(float i = 0.0; i <= 10.0; i += 0.5, posShine += 2.0)
    {
    	shine += Shines(uv + posShine, i);

    }




    uv *=  1.0 - uv.yx;    
    float vig = uv.x*uv.y * 15.0;    
    vig = pow(vig, 0.25);

	uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;


    float t = iTime * 0.8;

    float s = sin(t);
    float c = cos(t);

    mat2 rotmat = mat2(c, -s, s, c);

    uv *= rotmat;

    float angle = atan(uv.y,uv.x);
    vec4 tex = texture(iChannel0, vec2(angle, dot(uv.x, uv.y) + fract(t * 0.3)));
    vec4 tex2 = texture(iChannel1, vec2(angle, dot(uv.x, uv.y) - fract(t * 0.1)));

    vec4 col = vec4(0);

    //Kyle273's band function
    float band = pow(sin(angle * 2.0) * 0.5 + 0.5, 5.0) * 3.0;
    col += tex * band;

    angle += 1.5;
    band = pow(sin(angle * 2.0) * 0.5 + 0.5, 1.0);
    col += tex2 * band;



    fragColor = shine + col * vig;

}

// boilerplate ======================
const float PI = 3.14159;
const float PI2 = PI*2.;

vec3 dtoa(float d, vec3 amount){
    return vec3(1. / clamp(d*amount, vec3(1), amount));
}
mat2 rot2D(float r){
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}
float nsin(float x) {
    return cos(x)*.5+.5;
}
vec3 hash32(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}
float opUnion( float d1, float d2 ) { return min(d1,d2); }
float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }
float opIntersection( float d1, float d2 ) { return max(d1,d2); }
float opXor(float lhs, float rhs) {
    return opUnion(opIntersection(lhs, -(rhs)), opIntersection(rhs, -(lhs)));
}

float sdSquare(vec2 p, vec2 center, float s) {
	vec2 d = abs(p-center) - s;
	return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}
// end boilerplate ======================

const float th = 1./3.;
float hello(float sd, vec2 uv, float off, float a, vec2 sgn) {
    sd = opXor(sd, sdSquare((uv + sgn*vec2(.5,.5)+vec2(-off,0)) * rot2D(a), vec2(-th*.5), th*.5));
    sd = opXor(sd, sdSquare((uv + sgn*vec2(-.5,.5)+vec2(0,-off)) * rot2D(a+PI*.5), vec2(-th*.5), th*.5));
    sd = opXor(sd, sdSquare((uv + sgn*vec2(.5,-.5)+vec2(0,off)) * rot2D(a-PI*.5), vec2(-th*.5), th*.5));
    sd = opXor(sd, sdSquare((uv + sgn*vec2(-.5,-.5)+vec2(off,0)) * rot2D(a-PI), vec2(-th*.5), th*.5));
    return sd;
}

float scurve(float x, float p) {
    x = x / p * PI2;
    return (x + sin(x+PI)) / PI2;
}

void mainImage( out vec4 o, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-.5;
    uv.x *= iResolution.x / iResolution.y;

    uv *= 2.8;
    float tsteady = iTime*.25;
    float t = scurve(tsteady, th);
    float sd = 1e6;
    float sdout = 1e6;

    uv *= rot2D(-tsteady*PI2*.25);
    float r = .5+(sqrt(2.)*th);
    uv -= r;
    float padding = .1; // kinda important because blurriness doesn't cross cells
    uv = mod(uv, r+r+padding)-r-padding*.5; // repetition

    float seg = mod(t, 3.);
    float aout = fract(seg)*PI*.5;
    float ain = -(aout+PI*.5);
    float offout = 0.;
    float offin = th*2.;
    if (seg >= 2.) {
		aout = (fract(seg)-.5)*PI;
        ain = PI;
    } else if (seg >= 1.) {
        offout = th*2.;
    } else {
        offout = th;
        offin = th;
    }

    float tsel = mod(t/3., 3.);
    bool A = false, B = false, C = false, D = false;

    if (tsel >= 2.) {
    	// (none)
    } else if (tsel >= 1.) {
    	// (big plus)
        A = B = true;
    } else {
    	// (minimal + outline)
        B = C = D = true;
    }

    if (A) sd = sdSquare(uv, vec2(0),.5);
   	if (B) sd = opXor(sd, sdSquare(uv, vec2(0),r));
   	if (C) sd = opXor(sd, sdSquare(uv, vec2(0),r));
    if (D) sd = hello(sd, uv, offout, aout, vec2(1));

    sdout = hello(sdout, uv, offout, aout, vec2(1));
    sd = hello(sd, uv, offout, aout, vec2(1));
    sd = hello(sd, uv, offin, ain, vec2(1));

    o.rgb = dtoa(sd, 3.*vec3(50.,100.,200.)) * vec3(.9,.9,.8);
    o.rgb += dtoa(sdout, 2.*vec3(100.,50.,50.)) * vec3(.1,-.8,.4);

    vec2 N = fragCoord / iResolution.xy-.5;

    o.rgb += (hash32(fragCoord+t)-.5)*.1;
    o.rgb += dot(N,N) * vec3(.2,.5,1);
    o = clamp(o,o-o,o-o+1.);
    o *= 1.-length(9.*pow(abs(N), vec2(3.)));// vingette
    o.a = 1.;
}

#define SCALE 6.
#define s 3.      // sinc window size = (2s+1)^2
#define sinc(x) sin(3.1416*(x)+1e-20) / (3.1416*(x)+1e-20)

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         M = iMouse.xy;
    if (length(M)<10.) M = R/2. + R/6.*cos(iTime+vec2(0,33));
    M = SCALE* ( 2.*M - R ) / R.y;
    U = SCALE* ( 2.*U - R ) / R.y;

    // --- draw continuous sinc function
    vec2 D = U-M,
         W = max(abs(D.x),abs(D.y)) <= s ? sinc(D) : vec2(0),
         F = fract(U+.5) - .5;
    O  = W.x*W.y * vec4(1,-1,0,0);

    // --- draw nodes
    D = round(U)-M;
    bool w = max(abs(D.x),abs(D.y)) <= s+.5;
    W =  w ? sinc(D) : vec2(0);

    float l = length(F),
          p = SCALE * 2./R.y;
    O = mix( O, vec4(abs(W.x*W.y)), smoothstep(p,0.,l-.2) ); // draw node weight
    O = mix( O, w?vec4(1):vec4(0,.3,1,0), smoothstep(p,0.,abs(l-.2)) ); // draw node

    // --- to sRGB    
    O = pow( O, vec4(1./2.2) );
}

#define random(p) fract(sin((p)* vec2(12.9898, 78.233)) * 43758.5453)

float diskIn(vec2 r, vec2 center, float radius, float alpha) {
	return alpha * (1.0 - smoothstep( radius - 0.005, radius + 0.005, length(r-center)));
}

float diskOut(vec2 r, vec2 center, float radius, float outline, float alpha) {
	return alpha * 
        ((1.0 - 
          smoothstep(
              radius + outline - 0.005, 
              radius + outline + 0.005, 
              length(r-center)))
          -
         ( 1. -
          smoothstep( 
              radius - 0.005, 
              radius + 0.005, 
              length(r-center))));
}

float disk(vec2 r, vec2 center, float radius, float alpha, float outline, float outlineAlpha) {
	return diskIn(r, center, radius, alpha) + diskOut(r, center, radius, outline, outlineAlpha);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col = vec3(0.);

    float nbCircles = 2.;
    float circleLife = 1.;
    float circleRad = 100.;
	vec3 color = vec3(200./255., 50./255., 100./255.);

	for(float i = 0.; i < nbCircles; i++) {
		float timeOffset = i * circleLife/(nbCircles);
        float uid = floor((iTime - timeOffset)/ circleLife) * nbCircles + i;
        //uid = 1. + i;
		float modCircle = mod((iTime - timeOffset)*circleRad/circleLife, circleRad);
        vec2 randPos = random(sqrt(2.)*uid) * (iResolution.xy);
		col = mix(col, color,
                  disk(fragCoord, randPos, modCircle, (1.0 - modCircle/circleRad), mix(5., 0., (modCircle/circleRad)), 1.));

    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define repid(p, r) (floor((p + r*.5) / r))
#define rep(p, r) (mod(p - r*.5, r) - r*.5)

// Volumetric pointlight by robobo1221
// https://www.shadertoy.com/view/lstfR7
float bayer2(vec2 a){
    a = floor(a);
    return fract( dot(a, vec2(.5, a.y * .75)) );
}

#define bayer4(a)   (bayer2( .5*(a))*.25+bayer2(a))
#define bayer8(a)   (bayer4( .5*(a))*.25+bayer2(a))
#define bayer16(a)  (bayer8( .5*(a))*.25+bayer2(a))

const float pi = acos(-1.0);
const float pi2 = pi*2.;

mat2 rot(float a) {
	float c = cos(a), s = sin(a);
    return mat2(c,s,-s,c);
}

vec2 pmod(vec2 p, float r) {
	float a = pi/r - atan(p.x, p.y);
    float n = pi2/r;
    a = floor(a/n) * n;
    return p * rot(a);
}

float sdHex(vec3 p, vec2 h, float r)
{
    p.zy = p.yz;
    const vec3 k = vec3(-0.8660254, 0.5, 0.57735);
    p = abs(p);
    p.xy -= 2.0*min(dot(k.xy, p.xy), 0.0)*k.xy;
    vec2 d = vec2(
       length(p.xy-vec2(clamp(p.x,-k.z*h.x,k.z*h.x), h.x))*sign(p.y-h.x),
       p.z-h.y );
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

// http://mercury.sexy/hg_sdf/
float ost(float a, float b, float r, float n) {
	float s = r/n;
	float u = b-r;
	return min(min(a,b), 0.5 * (u + a + abs ((mod (u - a + s, 2. * s)) - s)));
}

float sdFloor(vec3 p) {
    vec2  hx = vec2(1.73205081,1)*1.04;
    vec3  q = p;
    q.xz = mod(p.xz + hx*0.5, hx)-hx*0.5;
	float d = sdHex(q, vec2(0.5, 1.0), 0.04);
    q.xz = mod(p.xz, hx)-hx*0.5;
    d = min(d, sdHex(q, vec2(0.5, 1.0), 0.04));
    return d;
}

float sdWall(vec3 p) {
	vec3 q = p;

    // wall
    float d = -(abs(p.x) - 8.);

    // square
    q = p;
    q.z = rep(q.z, 240.);
    d = max(d, -sdHex(q, vec2(40., 100.), 0.0));

    // pillars
    vec3 pp = p;
    pp.z = rep(pp.z, 240.);

    q.x = abs(q.x) - 7.;
    q.z = rep(q.z, 20.);
    q = abs(q) - 1.0;
    // clipping pillars in square
    d = min(d, max(max(q.x, q.z), -(abs(pp.z) - 40. + 5.0)));

	return d;
}

float sdSquareObjects(vec3 p, float r, out float id) {
	vec3 q = p;

    q.z = rep(q.z, 240.);
    vec3 pp = q;
    q.xz *= rot(pi / 6.);
    q.xz = pmod(q.xz, 6.);
    q.z -= 15.;

    id = repid(q.z, 15.);
    q.z = rep(q.z, 15.);
    float d = length(q.xz) - r;
    d = max(d, -length(pp.xz) + 15. - r);
    return d;
}

float tt;

float sdSquareFrame(vec3 p) {
    float id;
    float d = sdSquareObjects(p, 1.5 + 1., id);
    d = max(d, -(abs(p.y - 13.) - (1.0 - exp(sin(tt * 5. - id * 1.25)*5.)/exp(5.)) * 5.));
    return d;
}

float sdSquareEmission(vec3 p) {
    float id;
	float d = sdSquareObjects(p, 1.5, id);
    d = max(d, (abs(p.y - 13.) - (1.0 - exp(sin(tt * 5. - id * 1.25)*5.)/exp(5.)) * 5.));
    return d;
}

float sdCeil(vec3 p) {
	return -(p.y - 30.0);
}

float map(vec3 p) {
    float d = sdFloor(p);

    d = ost(d, sdWall(p), 1.0, 5.0);

    d = min(d, sdSquareFrame(p));

    d = min(d, sdCeil(p));

    return d;
}

vec4 volumeMap0(vec3 p) {
    vec3 q = p;
    q.z = rep(q.z, 30.);
    q.y -= 2.;
    float d = length(q.zy) - .5;
    return vec4(vec3(1., 0.001, .1) * .25, d);
}

vec4 volumeMap1(vec3 p) {
    vec3 q = p;
    q.z = rep(q.z, 30.);
    q.z += 15.;
    q.y -= 20.;
    float d = length(q.zy) - .5;
    return vec4(vec3(.01, 0.01, 1.0) * .4, d);
}

vec4 volumeMap2(vec3 p) {
    vec3 q = p;
    q.z = rep(q.z, 20.);
    q.y -= 12.;
    q.x = abs(q.x) - 6.;
    float d = max(length(q.xy) - .25, abs(q.z) - 3.0);
    return vec4(vec3(.01, 0.01, 1.0) * .3, d);
}

vec4 volumeMap3(vec3 p) {
	float d = sdSquareEmission(p);
    return vec4(vec3(1., 0.01, 0.001) * .2, d);
}

vec3 volumetric(vec3 p, vec3 ray, int slice, float depth, float dither) {
    vec3 ret = vec3(0.);

    float sd = depth / float(slice);
    float t = 0.05 + sd * dither;
    vec4 d;
    for(int i = 0; i < slice; i++) {
        vec3 pos = p + ray * t;
        d = volumeMap0(pos);
        float dd = max(0.01, d.w);
        ret += (d.rgb*sd) / (dd * dd);

        d = volumeMap1(pos);
        dd = max(0.01, d.w);
        ret += (d.rgb*sd) / (dd * dd);

        d = volumeMap2(pos);
        dd = max(0.01, d.w);
        ret += (d.rgb*sd) / (dd * dd);

        d = volumeMap3(pos);
        dd = max(0.01, d.w);
        ret += (d.rgb*sd) / (dd * dd);

        t += sd;
    }
    return ret;
}

void trace(vec3 p, vec3 ray, int iter, out vec3 pos, out float t) {
    t = .1;
    for(int i=0; i<iter; i++) {
    	pos = p + ray * t;
        float d = map(pos);
        if (d < 0.0001) {
            break;
        }
        t += d;
    }
}

vec3 acesFilm(const vec3 x) {
    const float a = 2.51;
    const float b = 0.03;
    const float c = 2.43;
    const float d = 0.59;
    const float e = 0.14;
    return clamp((x * (a * x + b)) / (x * (c * x + d ) + e), 0.0, 1.0);
}

float n3(vec3 p) {
	vec3 r = vec3(1, 99, 999);
	vec4 s = dot(floor(p), r) + vec4(0., r.yz, r.y + r.z);
	p = smoothstep(0., 1., fract(p));
	vec4 a = mix(fract(sin(s)*5555.), fract(sin(s+1.)*5555.), p.x);
	vec2 b = mix(a.xz, a.yw, p.y);
	return mix(b.x, b.y, p.z);
}

vec3 normal(vec3 p) {
	vec2 e = vec2(1., -1.) * 0.001; // 0.005;
    return normalize(e.xyy * map(p + e.xyy) + e.yxy * map(p + e.yxy) + e.yyx * map(p + e.yyx) + e.xxx * map(p + e.xxx));
}

vec3 applyFog(vec3 col, float depth) {
    float fog = 1.0 - exp(-depth * 0.0003);
    col = mix(vec3(col), vec3(1.0, 1.05, 1.8) * 10., fog);
    return col;
}

vec3 getCol(inout vec3 p, vec3 ray, int titer, int viter, float vd, float dither) {
    vec3 col, pos;
    float depth;
    trace(p, ray, titer, pos, depth);
    col += volumetric(p, ray, viter, min(depth, vd), dither);
    p = pos;
    return applyFog(col, depth);
}

float stepup(float t, float len, float smo)
{
    float tt = mod(t += smo, len);
    float stp = floor(t / len) - 1.0;
    return smoothstep(0.0, smo, tt) + stp;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    float nois = (n3(vec3(normalize(p)*20., 1.0)) * 1.0 + n3(vec3(normalize(p)*40., 2.0)) * 1.5  + n3(vec3(normalize(p)*100., 3.0)) * 1.5) / 3.5;
    nois = pow(nois, 4.0) * 2.0 - 1.0;

    tt = iTime + (nois * 0.009 + n3(vec3(p*500., 0.0)) * 0.004) * pow(dot(p, p), 0.6) * 1.;

    float speed = tt * 100.;

    //speed = stepup(tt, 1.0, 0.5) * 80.;

    vec3 ro = vec3(0., 7., -5. + speed);
    vec3 ray = normalize(vec3(p, 1.4 + (1.0 - dot(p, p)) * 0.15));
    //ray.xy *= rot(stepup(tt-0.5, 4.0, 0.1) * pi * 0.5);

    float dither = bayer16(gl_FragCoord.xy);
    vec3 col = getCol(ro, ray, 99, 120, 300., dither);

    vec3 n = normal(ro);
    ray = reflect(ray, n);
    col += getCol(ro, ray, 60, 70, 100., dither) * 0.2;

    n = normal(ro);
    ray = reflect(ray, n);
    col += getCol(ro, ray, 40, 40, 100., dither) * 0.04;

    col = acesFilm(col*0.5);

    col = pow(col, vec3(1./2.2));

    p = fragCoord.xy / iResolution.xy;
    p *=  1.0 - p.yx;
    float vig = p.x*p.y * 30.0;
    vig = pow(vig, 0.1);

    fragColor = vec4(col * vig,1.0);
}

#define UVScale 			 1.0
#define Speed				 1.5

#define FBM_WarpPrimary		-0.65
#define FBM_WarpSecond		 0.34
#define FBM_WarpPersist 	-0.77
#define FBM_EvalPersist 	 0.24
#define FBM_Persistence 	 0.77
#define FBM_Lacunarity 		 1.08
#define FBM_Octaves 		 6



//fork from Dave Hoskins
//https://www.shadertoy.com/view/4djSRW
vec4 hash43(vec3 p)
{
	vec4 p4 = fract(vec4(p.xyzx) * vec4(1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+19.19);
	return -1.0 + 2.0 * fract(vec4(
        (p4.x + p4.y)*p4.z, (p4.x + p4.z)*p4.y,
        (p4.y + p4.z)*p4.w, (p4.z + p4.w)*p4.x)
    );
}

//offsets for noise
const vec3 nbs[] = vec3[8] (
    vec3(0.0, 0.0, 0.0),vec3(0.0, 1.0, 0.0),vec3(1.0, 0.0, 0.0),vec3(1.0, 1.0, 0.0),
    vec3(0.0, 0.0, 1.0),vec3(0.0, 1.0, 1.0),vec3(1.0, 0.0, 1.0),vec3(1.0, 1.0, 1.0)
);

//'Simplex out of value noise', forked from: https://www.shadertoy.com/view/XltXRH
//not sure about performance, is this faster than classic simplex noise?
vec4 AchNoise3D(vec3 x)
{
    vec3 p = floor(x);
    vec3 fr = smoothstep(0.0, 1.0, fract(x));

    vec4 L1C1 = mix(hash43(p+nbs[0]), hash43(p+nbs[2]), fr.x);
    vec4 L1C2 = mix(hash43(p+nbs[1]), hash43(p+nbs[3]), fr.x);
    vec4 L1C3 = mix(hash43(p+nbs[4]), hash43(p+nbs[6]), fr.x);
    vec4 L1C4 = mix(hash43(p+nbs[5]), hash43(p+nbs[7]), fr.x);
    vec4 L2C1 = mix(L1C1, L1C2, fr.y);
    vec4 L2C2 = mix(L1C3, L1C4, fr.y);
    return mix(L2C1, L2C2, fr.z);
}

vec4 ValueSimplex3D(vec3 p)
{
	vec4 a = AchNoise3D(p);
	vec4 b = AchNoise3D(p + 120.5);
	return (a + b) * 0.5;
}

//my FBM
vec4 FBM(vec3 p)
{
    vec4 f, s, n = vec4(0.0);
    float a = 1.0, w = 0.0;
    for (int i=0; i<FBM_Octaves; i++)
    {
        n = ValueSimplex3D(p);
        f += (1.0-abs(n)) *a;	//ridged-like
        s += n.zwxy *a;
        a *= FBM_Persistence;
        w *= FBM_WarpPersist;
        p *= FBM_Lacunarity;
        p += n.xyz * FBM_WarpPrimary *w;
        p += s.xyz * FBM_WarpSecond;
        p.z *= FBM_EvalPersist +(f.w *0.5+0.5) *0.0003;
    }
    return f;
}

void mainImage(out vec4 col, in vec2 uv)
{
    col = vec4(0.0, 0.0, 0.0, 1.0);
    float aspect = iResolution.x / iResolution.y;
    uv /= iResolution.xy / UVScale *0.1; uv.x *= aspect;

    vec4 fbm = (FBM(vec3(uv, iTime *Speed +20.0))) *0.3;
    float causticMask = pow(fbm.x, 10.0);
    col.xyz += causticMask;
;
}

