vec3 surface (vec2 uv)
{
    vec2 k = vec2(0.0,0.0); 
	for (float i=0.0;i<64.0;i++)
	{
		vec2 q = vec2(i*127.1+i*311.7,i*269.5+i*183.3);
		vec2 h = fract(sin(q)*43758.5453);
		vec2 p = cos(h*iTime+1.0);
		float d = length(uv-p);
		k+=(1.0-step(0.06,d))*h;
	}
	return vec3(0.0,k.x,k.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = (2.0 * uv-1.0) / (1.0-uv.y) ;  
    vec3 c = vec3(0.0,0.0,0.0);
    vec2 d = (vec2(0.0,-1.0)-p)/float(80);
    float w = 1.0;
    vec2 s = p;
    for( int i=0; i<80; i++ )
    {
        vec3 res = surface(s);
        c += w*smoothstep( 0.0, 1.0, res );
        w *= .97;
        s += d;
    }
    c = c * 0.12;
	fragColor = vec4( c,1.0 );
}

#define ITERS 100
#define TOL 1e-3
#define fdist 0.5
#define maxdist 100.
#define LEVELS 7
#define PI 3.1415926

float noise(in vec2 uv)
{
	return fract(sin(dot(vec2(1484191., 189159.), uv))*9991415.);   
}

vec4 map(in vec3 pos)   
{
    float t = (clamp(abs(mod(iTime*0.25, 6.0) - 3.0), 1.0, 2.0)-1.0) * PI * 0.5;
    if (iMouse.w > 1.0)
    {
    	t = iMouse.y/iResolution.y*PI*0.5;
    }
    float c = cos(t);
    float s = sin(t);
    mat3 rotZ = mat3(c, -s, 0, s, c, 0, 0, 0, 1);
    mat3 rotX = mat3(1, 0, 0, 0, c, -s, 0, s, c);
    float sc = 0.5;
    for (int i=0; i<LEVELS; i++)
    {
        pos = abs(rotX * rotZ * pos) - sc;
     	sc *= 0.5;
    }

    return vec4(max(pos.x, max(pos.y, pos.z))-sc*2., pos);
}

vec4 march(in vec3 pos, in vec3 dir, in vec2 seed)
{
    float t = 0.;
    vec4 dist;
    for (int i=0; i<ITERS; i++)
    {
        vec3 currpos = t*dir+pos;
    	dist = map(currpos);
        t += dist.x * (1.+(noise(seed)-0.5)*0.5);
        seed.x += dist.x;
        if (abs(dist.x) < TOL)
        {
            return vec4(i, dist.yzw);
        } else if (t > maxdist) {
            return vec4(i, dist.yzw);
        }
    }
    return vec4(ITERS, dist.yzw);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float c = cos(iTime);
    float s = sin(iTime);
    mat3 rot = mat3(c, -s, 0, s, c, 0, 0, 0, 1);
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.x;
    vec3 ro = rot * vec3(1.,1.,1.) * (2.+s*0.3);
    vec3 nw = -normalize(ro);
    vec3 up = vec3(0.,0.,1.);
    vec3 nu = cross(nw, up);
    vec3 nv = cross(nu, nw);
   	vec3 rd = normalize(nw*fdist + uv.x*nu + uv.y*nv);
    vec4 d = march(ro, rd, uv);
    fragColor = vec4(d.x/float(ITERS)*2. * (normalize(d.yzw)+1.)*0.5,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 texColor = texture(iChannel0, uv);
    texColor.b = ((texColor.b / 2.0) + (1.0 - ((uv.x + uv.y) /2.0))) * abs(sin(iTime));
    texColor.r = ((texColor.r / 2.0) + (uv.x + uv.y) /2.0) * abs(cos(iTime));
    texColor.g = ((texColor.g / 2.0) + ((uv.x - uv.y)* 2.0)) * abs(sin(iTime)) * cos(iTime);


    // Output to screen
    fragColor = texColor;
}

/////////////////////////////
// BRADY'S VOLUMETRIC FIRE //
/////////////////////////////

// CREDITS
// Thanks to @shnitzelkiller & @iq for inspiration and help
// noise: https://github.com/BrianSharpe/Wombat/blob/master/Value3D.glsl
// saturation: https://github.com/AnalyticalGraphicsInc/cesium/blob/master/Source/Shaders/Builtin/Functions/saturation.glsl
// fnoise: https://www.shadertoy.com/view/Xsd3R2

float noise( vec3 P )
{
    vec3 Pi = floor(P);
    vec3 Pf = P - Pi;
    vec3 Pf_min1 = Pf - 1.0;
    Pi.xyz = Pi.xyz - floor(Pi.xyz * ( 1.0 / 69.0 )) * 69.0;
    vec3 Pi_inc1 = step( Pi, vec3( 69.0 - 1.5 ) ) * ( Pi + 1.0 );
    vec4 Pt = vec4( Pi.xy, Pi_inc1.xy ) + vec2( 50.0, 161.0 ).xyxy;
    Pt *= Pt;
    Pt = Pt.xzxz * Pt.yyww;
    vec2 hash_mod = vec2( 1.0 / ( 635.298681 + vec2( Pi.z, Pi_inc1.z ) * 48.500388 ) );
    vec4 hash_lowz = fract( Pt * hash_mod.xxxx );
    vec4 hash_highz = fract( Pt * hash_mod.yyyy );
    vec3 blend = Pf * Pf * Pf * (Pf * (Pf * 6.0 - 15.0) + 10.0);
    vec4 res0 = mix( hash_lowz, hash_highz, blend.z );
    vec4 blend2 = vec4( blend.xy, vec2( 1.0 - blend.xy ) );
    return dot( res0, blend2.zxzx * blend2.wwyy );
}

float fnoise(vec3 p, float time)
{
    float f = 0.0;
    p = p - vec3(0.0, 1.0, 0.0) * .5 * time;
    p = p * 3.0;
    f += 0.50000 * noise(p); p = 2.0 * p;
	f += 0.25000 * noise(p); p = 2.0 * p;
	f += 0.12500 * noise(p); p = 2.0 * p;
	f += 0.06250 * noise(p); p = 2.0 * p;
    f += 0.03125 * noise(p);
    return f;
}

float model( in vec3 p )
{
    p.y *= .7;
    float sphere = length(p)-.8;
    float res = sphere + fnoise(p*1.5, iTime*3.)* .4;
    return res*.8;
}

float raymarch(in vec3 ro, in vec3 rd)
{
    float dist = 0.;
    for(int i = 0; i < 30; i++)
    {
		float m = model(ro+rd*dist);
        dist += m;

        if(m < .01) return dist;
        else if(dist > 2.) break;
    }
    return -1.;
}

vec3 hueShift( vec3 color, float hueAdjust ){

    const vec3  kRGBToYPrime = vec3 (0.299, 0.587, 0.114);
    const vec3  kRGBToI      = vec3 (0.596, -0.275, -0.321);
    const vec3  kRGBToQ      = vec3 (0.212, -0.523, 0.311);

    const vec3  kYIQToR     = vec3 (1.0, 0.956, 0.621);
    const vec3  kYIQToG     = vec3 (1.0, -0.272, -0.647);
    const vec3  kYIQToB     = vec3 (1.0, -1.107, 1.704);

    float   YPrime  = dot (color, kRGBToYPrime);
    float   I       = dot (color, kRGBToI);
    float   Q       = dot (color, kRGBToQ);
    float   hue     = atan (Q, I);
    float   chroma  = sqrt (I * I + Q * Q);

    hue += hueAdjust;

    Q = chroma * sin (hue);
    I = chroma * cos (hue);

    vec3    yIQ   = vec3 (YPrime, I, Q);

    return vec3( dot (yIQ, kYIQToR), dot (yIQ, kYIQToG), dot (yIQ, kYIQToB) );

}

vec3 saturation(vec3 rgb, float adjustment)
{
    const vec3 W = vec3(0.2125, 0.7154, 0.0721);
    vec3 intensity = vec3(dot(rgb, W));
    return mix(intensity, rgb, adjustment);
}

vec3 background(in vec2 p)
{
	return vec3(0.);
}

vec3 volume(in vec3 p, in vec3 rd, in vec3 ld, in vec2 sp)
{
    vec3 op = p;
    float trans = 1.0;
    float td = 0.0;
    float dif = 0.0;
    float emit = 0.0;
    float steps = 30.; // increase to smooth

    // march
    for(float i = 0.; i < steps; i++)
    {
        float m = model(p);
        p += rd*.03;

        float dens = 1.-smoothstep(0., .35, -m);
        td += dens;
        trans *= dens;

        if(td > 1.0 && dif <= 0.)
        {
            td = 1.;
        	dif = clamp(1. - model(p - .1*ld), 0., 1.);
            emit = pow(smoothstep(-.3, 0., model(p)), 4.);
        }
    }

    trans = (1.-pow(td/steps, 4.5));
    trans = smoothstep(0., 1., trans);
    float emitmod = (emit*trans)*.8+.2;

    // light
    vec3 lin = vec3(.3, .2, .9);
    lin = hueShift(lin, 4.2 + -trans*.6 + dif*.5);
    lin *= emitmod;

    // bright/sat/contrast
    lin = saturation(lin, pow(trans, .5)*.4);
    lin *= 5.;
    lin -= vec3(.4);

    return mix(background(sp), lin, pow(trans, 1.25));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord - .5*iResolution.xy)/iResolution.y;

    float rs = .5;
    vec3 ro = vec3(cos(iMouse.x/100.), 1., sin(iMouse.x/100.))*1.35;
    vec3 ta = vec3(0., 0.1, .0);				

    vec3 w = normalize (ta-ro);
    vec3 u = normalize (cross (w, vec3(0., 1., 0.)));
    vec3 v = normalize (cross (u, w));
    mat3 mat = mat3(u, v, w);
    vec3 rd = normalize (mat*vec3(p.xy,1.));

    float dist = raymarch(ro, rd);
    vec3 ld = vec3(-1., 1., 0.);
    vec3 col = dist > 0. ? volume(ro+rd*dist, rd, ld, p) : background(p);

    fragColor = vec4(col,1.0);
}

float r2D(vec2 p)
{
    return fract(sin(dot(p, vec2(32.91, 54.28)))*96516.4172);
}

float pattern(vec2 p, float s)
{
    vec2 i = floor(p*s);
    return r2D(i);
}

vec3 normals(vec2 p, float s, float h)
{
    float pixel = 1./iResolution.y;
    vec2 e = vec2(pixel, 0.);
    return normalize(
        vec3(
            (pattern(p-e.xy, s)-h)/e.x,
            (pattern(p-e.yx, s)-h)/e.x,
            1.));
}

#define samples 16
#define num_steps 6
#define PI 3.141592
#define radius 12.

float ambient_occlusion(vec2 p, float h, vec3 n, float s)
{
    float ao = 0.;
    float pixel = 1./iResolution.y;
    vec3 origin = vec3(p, h);
    for (int i = 0; i < samples; i++)
    {
        float angle = float(i)*PI/float(samples);
        vec2 dir = vec2(cos(angle), sin(angle));
        for (int j = 1; j <= num_steps; j++)
        {
            vec2 point = p+float(j)*pixel*dir*radius;
            float r = pattern(point, s);
            vec3 current = vec3(point, r);
            vec3 dir_curr = current - origin;
            float dir = dot(normalize(dir_curr), n);
            if (dir < 0.)
                break;
            if (dir > 0.)
            {
                ao += length(dir_curr)/float(j);
                break;
            }
        }
    }
    ao /= float(samples);
    return ao;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    float s = 6.;
    float h = pattern(uv, s);
    vec3 n = normals(uv, s, h);
    float ao = 1.-ambient_occlusion(uv, h, n, s);

    float t = iTime;
    vec3 ld = normalize(vec3(cos(t), sin(t), .5)*3.-vec3(uv, h));
    float diff = max(dot(n, ld), 0.);
    float l = diff*ao;

    vec3 col = vec3(0.);
    col += l*mix(vec3(1.), vec3(0.0, .6, .8), h);

    fragColor = vec4(sqrt(clamp(col, 0., 1.)),1.0);
}

