vec3 getSceneNormal (vec3 p, vec3 lightPos)
{
    vec3 md = vec3(MIN_MARCH_DIST * 10.0, 0.0, 0.0);
    vec3 delta = vec3(
        getSceneDist(p + md.xyy) - getSceneDist(p - md.xyy), // = getScene(p + vec3(MIN_MARCH_DIST, 0, 0)) - getScene(p - vec3(MIN_MARCH_DIST, 0, 0)
