void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // c64 stuff
    vec3 col = fract(vec3(80.0/255.0 + iTime, 69.0/255.0 - iTime * 0.42, 155.0/255.0 + sin(iTime) * 0.2));
    float jort = fract((uv.y + iTime * 0.5) * 8.0 + uv.x * sin(iTime) * 0.6);
    vec3 final = mix(col, vec3(1.0), smoothstep(0.49, 0.51, jort));

    // World info / camera settings
    float time = iTime;
    ivec2 intRes = ivec2(iResolution.xy);
    vec3 camPos = vec3(0.0, 0.0, -256.0);
    vec3 lightPos = vec3(cos(time) * DIST_MAX, sin(time * 2.0) * DIST_MAX, sin(time) * DIST_MAX);

    // We're doing some ortho camera in this shader so
    // We don't have to worry about spooky projection math
    vec2 screenPos = uv * iResolution.xy;
    vec3 rayOrigin = camPos + vec3(screenPos.x - (iResolution.x * 0.5), screenPos.y - (iResolution.y * 0.5), 0.0);
    vec3 rayPos = rayOrigin;
    vec3 rayDir = vec3(0.0, 0.0, 1.0);
	vec3 mat;

    for (int rs=0; rs<=MAX_STEPS; rs++)
    {
        float dist = getSceneInfo(rayPos, mat);

        if (dist < MIN_MARCH_DIST) // We hit something
        {
            vec3 normal = getSceneNormal(rayPos, lightPos);

            // Diffuse lighting
            vec3 lightDelta = normalize(rayPos - lightPos);
            float lightDot = dot(normal, lightDelta);
            float diffLt = max(lightDot, 0.0) * 0.6 + 0.4;

            final.xyz = mat.xyz * diffLt;
        }
        else // Nope. Keep moving
        {
            rayPos += rayDir * dist;
        }
    }

    // Output to screen
    fragColor = vec4(final,1.0);
}

float PI = 3.14159265;
vec2 path(float z){
    float x = sin(z) + 2.0 * cos(z * 0.3) - 1.5 * sin(z * 0.12345);
    float y = cos(z) + 1.5 * sin(z * 0.3) + 2.0 * cos(z * 0.12345);
    return vec2(x,y);
}
vec2 cir(float rot){
    return vec2(cos(rot),sin(rot));
}
float rate(float t){
	return 1.0 + 0.5*sin(t);
}
float map(vec3 p){
	p.xy = fract(p.xy/5.0) * 8.0 * rate(iTime*2.0) - 4.0*rate(iTime*2.0);
    vec2 o = vec2(0,0);
	float tT1 = length(p.xy - (o/4.0) - (vec2(cir(((0.0/3.0) + (p.z/4.0)) * PI)) * 2.0)) - 0.4 *fract(p.z) ;
    float tT2 = length(p.xy - (o/4.0) - (vec2(cir(((2.0/3.0) + (p.z/4.0)) * PI)) * 2.0)) - 0.4*fract(p.z);
	float tT3 = length(p.xy - (o/4.0) - (vec2(cir(((4.0/3.0) + (p.z/4.0)) * PI)) * 2.0)) - 0.4*fract(p.z);
    return min(min(tT1,tT2),tT3);

}
const float EPS = 0.001;
vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 64; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return vec4(getNormal(p),t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float PI = 3.14159265;
    vec2 uv = fragCoord.xy /iResolution.xy;
	uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 r = normalize(vec3(uv,0.5));
	r.xz *= mat2(cos(0.1 * PI),-sin(0.1 * PI),sin(0.1 * PI),cos(0.1 * PI));
    r.yz *= mat2(cos(0.1 * PI),-sin(0.1 * PI),sin(0.1 * PI),cos(0.1 * PI));
    r.xy *= mat2(cos(iTime),-sin(iTime),sin(iTime),cos(iTime));
    float z = iTime * 12.0 ;

	vec2 a = path(z);
    vec3 o = vec3(a /8.0 + vec2(iTime) * 2.0  ,z);
    vec4 data = trace(o,r);
    float t = data.w;
    float fog = 1.0 / (1.0 + t * t * 0.01);
    vec3 fc = mix(vec3(0.5 - data.x,0.5 -data.y,0.5-data.z),vec3(0),1.0 - fog);
	//fc = vec3(fog);
    // Output to screen
    fragColor = vec4(fc,1.0);
}

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         D = texelFetch(iChannel0, ivec2(U)%256, 0).rg - .5,
         P = ( 2.*U - R ) / R.y;

    float v = fract( atan(P.y,P.x)/6.283 - .1*iTime ); // amount (0..1)

    U +=  v * D;                                       // jittering
    P = ( 2.*U - R ) / R.y;

    O = vec4( .5 + .5* sin( R.y * atan(P.y,P.x)) );
}

