    float beatTime = mod(iTime, BPS);
    float HalfbeatTime = mod(iTime, BPS * 0.5);
    float FullbeatTime = 1.0 - pow(1.0 - (mod(iTime, BPS * 2.0) / (BPS * 2.0)), 2.0);
    float beatIncr = floor(iTime / BPS);

    int colourIdx = int(mod(beatIncr, 2.0)) * 2;
    vec3 hexColour = clamp(mix(hexagon[colourIdx], hexagon[colourIdx + 1], beatTime / BPS) + 0.6, 0.0, 1.0);
    mat = vec3(hexColour);

    // Hexagon
    float powerBeat = 1.0 - pow(1.0 - beatTime / BPS, 4.0);
    float deltaHalfBeatTime = HalfbeatTime / (BPS * 0.5);

    float ang = iTime;
   	vec3 dp = (rotationMatrix(vec3(0.0, 0.0, 1.0), ang) * rotationMatrix(vec3(0.5, 0.5, 0.0), PI * 0.1 + sin(iTime * 0.5) * 0.25) * vec4(p.xyz, 0.0)).xyz;

    float hexSides = 4.0 + mod(floor((iTime * 0.2) / BPS), 3.0);
    float hexSz = 64.0 + 12.0 * powerBeat;
    float hexThick = 18.0 + sin(powerBeat * PI) * 16.0;
    dist = min(dist, sdfPolyDonut(dp, hexSides, vec3(hexSz, hexThick, 2.5)));
    dist = min(dist, sdfPolyDonut(dp, hexSides, vec3(mix(max(iResolution.x, iResolution.y) * 1.2, hexSz + 10.0, FullbeatTime), hexThick, 10.0)));

    float tmpdist = sdfPolyPrism(dp, hexSides, vec2(hexSz, 5.0));
    if (tmpdist < dist)
    {
        dist = tmpdist;
        mat = vec3(hexColour) * 0.2;
    }

    return dist;
}

float getSceneDist (vec3 p, float time, float BPS)
{
    vec3 trash;
    return getSceneInfo(p, trash, iTime, BPS);
}

vec3 getSceneNormal (vec3 p, vec3 lightPos, float time, float BPS)
{
    vec3 md = vec3(MIN_MARCH_DIST * 10.0, 0.0, 0.0);
    vec3 delta = vec3(
        getSceneDist(p + md.xyy, iTime, BPS) - getSceneDist(p - md.xyy, iTime, BPS), // = getScene(p + vec3(MIN_MARCH_DIST, 0, 0)) - getScene(p - vec3(MIN_MARCH_DIST, 0, 0)
        getSceneDist(p + md.yxy, iTime, BPS) - getSceneDist(p - md.yxy, iTime, BPS),
        getSceneDist(p + md.yyx, iTime, BPS) - getSceneDist(p - md.yyx, iTime, BPS)
    );

    vec3 normal = normalize(delta);
    return normal;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // hexagon style bg flash
    const float BPM = 130.0;
    const float BPS = 60.0 / BPM;
    const float BPS2 = 60.0 / (BPM * 0.5);

    float beatTime = mod(iTime, BPS);
    float HalfbeatTime = mod(iTime, BPS * 0.5);
    float beatIncr = floor(iTime / BPS2);
    int colourIdx = int(mod(beatIncr, 2.0)) * 2;

    vec3 final = mix(hexagon[colourIdx], hexagon[colourIdx + 1], mod(iTime, BPS2) / BPS2);

    // World info / camera settings
    float time = iTime;
    ivec2 intRes = ivec2(iResolution.xy);
    vec3 camPos = vec3(0.0, 0.0, -256.0);
    vec3 lightPos = vec3(0.0, 0.0, DIST_MAX);

    // We're doing some ortho camera in this shader so
    // We don't have to worry about spooky projection math
    vec2 screenPos = uv * iResolution.xy;
    vec3 rayOrigin = camPos + vec3(screenPos.x - (iResolution.x * 0.5), screenPos.y - (iResolution.y * 0.5), 0.0);
    vec3 rayPos = rayOrigin;
    vec3 rayDir = vec3(0.0, 0.0, 1.0);
	vec3 mat;

    for (int rs=0; rs<=MAX_STEPS; rs++)
    {
        float dist = getSceneInfo(rayPos, mat, iTime, BPS);

        if (dist < MIN_MARCH_DIST) // We hit something
        {
            vec3 normal = getSceneNormal(rayPos, lightPos, iTime, BPS);

            // Diffuse lighting
            vec3 lightDelta = normalize(rayPos - lightPos);
            float lightDot = dot(normal, lightDelta);
            float diffLt = max(lightDot, 0.0) * 0.6 + 0.4;

            final.xyz = mat.xyz * diffLt;
        }
        else // Nope. Keep moving
        {
            rayPos += rayDir * dist;
        }
    }

    // Output to screen
    fragColor = vec4(final,1.0);
}

// credits: Dave_Hoskins Hash functions: https://www.shadertoy.com/view/4djSRW

const float PI = 3.141592654;

vec3 hash32(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}
// returns { RGB, dist to edge (0 = edge, 1 = center) }
vec4 disco(vec2 uv) {
    float v = abs(cos(uv.x * PI * 2.) + cos(uv.y *PI * 2.)) * .5;
    uv.x -= .5;
    vec3 cid2 = hash32(vec2(floor(uv.x - uv.y), floor(uv.x + uv.y))); // generate a color
    return vec4(cid2, v);
}

void mainImage( out vec4 o, in vec2 fragCoord)
{
    vec2 R = iResolution.xy;
    vec2 uv = fragCoord / R;
    uv.x *= R.x / R.y; // aspect correct

    float t = iTime * .6; //t = 0.;
    uv *= 8.;
    uv -= vec2(t*.5, -t*.3);

    o = vec4(1);
    for(float i = 1.; i <= 4.; ++i) {
        uv /= i*.9;
        vec4 d = disco(uv);
        float curv = pow(d.a, .44-((1./i)*.3));
        curv = pow(curv, .8+(d.b * 2.));
        o *= clamp(d * curv,.35, 1.);
        uv += t*(i+.3);
    }

    // post
    o = clamp(o,.0,1.);
    vec2 N = (fragCoord / R )- .5;
    o = 1.-pow(1.-o, vec4(30.));// curve
    o.rgb += hash32(fragCoord + iTime).r*.07;//noise
    o *= 1.1-smoothstep(.4,.405,abs(N.y));
    o *= 1.0-dot(N,N*1.7);// vingette
    o.a = 1.;
}

#define N_ITERS 100.0
#define PI 3.14159265

vec3 color(float t) {    
	vec3 a = vec3(0.5);
    vec3 b = vec3(0.5);
    vec3 c = vec3(1.0);
    vec3 d = vec3(0.0, 0.33, 0.67);
    return a + b * cos(2.0*PI*(c*t + d));
}

vec2 cmul(vec2 c1, vec2 c2) {
    return vec2(c1.x*c2.x - c1.y*c2.y, c1.x*c2.y + c1.y*c2.x);
}

float linscale(float x, float x1, float x2, float y1, float y2) {
	return (y2-y1)/(x2-x1) * (x-x1) + y1;
}

float shade(vec2 z, float B) {
    float t = iTime;
    float i;
    float K = linscale(sin(iTime), -1.0, 1.0, 0.1, 1.0);
    for (i=0.0; i<N_ITERS; i++) {
        // z[n+1] = z[n]^2 + K*e^(it)
        z = cmul(z,z) + K*vec2(cos(t),sin(t));
        if (length(z) > B) break;
    }
	float sn = i - log(log(length(z))/log(B))/log(2.0);
    return sn;
}

vec2 screen_coord(vec2 xy, vec2 dim) {
    return (xy - 0.5*dim) / min(dim.x, dim.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = 2.0*screen_coord(fragCoord.xy, iResolution.xy);
    float B = 256.0;
    vec3 col = color(shade(uv, B));
    fragColor = vec4(col,1.0);
}

mat2 genRotMat(float val){
	return mat2(cos(val),-sin(val),sin(val),cos(val));
}
float PI = 3.14159265;
float map(vec3 p){
    p.x = fract(abs(p.x) / 1.5) * 1.5;
    p.y = fract(abs(p.y) / 1.5) * 1.5;
    p.z = fract(abs(p.z) / 0.5) * 0.5;
    p -= vec3(0.75,0.75,0.25);

    p.xy *= genRotMat(iTime * 2.0);
	float gear_out = max(length(p.xy) - 0.5 + 0.05 * floor(1.0 * sin(12.0 * atan(p.y,p.x)))
              ,abs(p.z) - 0.05);
    float gear_in = max(length(p.xy) - 0.2 + 0.05 * floor(1.0 * sin(12.0 * atan(p.y,p.x)))
              ,abs(p.z) - 0.1);
    return max(gear_out, -gear_in);
}

const float EPS = 0.001;
vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 96; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return vec4(getNormal(p),t);
}


void mainImage( out vec4 fragColor, in vec2 U )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec3 R = iResolution,
    r = normalize(vec3((2.*U - R.xy )/  R.y,1.2)),
    o = vec3(0.75 + 0.75 * sin(iTime),0.75 + 0.75 * cos(iTime),-1.0 + iTime * 1.5);
    vec4 data = trace(o,r);
    vec3 n = vec3(data.xyz);
    float t = data.w;
    float fog = 1.0 / (1.0 + t * t * 0.05);
    vec3 fc = t > 10000.0 ? vec3(0.8) : mix((vec3(data.x,data.y,data.z) + 1.0)/1.5
                                            ,vec3(0.0), - pow(dot(n,r),1.0));
    fc = mix(fc,vec3(1.0),1.0 - fog);
	//fc = vec3(fog);
    // Output to screen
    fragColor = vec4(fc,1.0);
}

