#define PI acos(-1.0)
#define TAU PI*2.0
vec2 rotate(vec2 p, float a)
{
    return mat2(cos(a),sin(a),-sin(a),cos(a))*p;
}

vec3 rotate(vec3 p,vec3 axis,float theta)
{
    vec3 v = cross(axis,p), u = cross(v, axis);
    return u * cos(theta) + v * sin(theta) + axis * dot(p, axis);   
}

vec2 pmod(vec2 p, float r)
{
    float a = mod(atan(p.y, p.x), TAU / r) - 0.5 * TAU / r;
    return length(p) * vec2(-sin(a), cos(a));
}

vec3 hue(float t){
	return cos((vec3(0,2,-2)/3.+t)*TAU)*.5+.5;
}

float map(vec3 p)
{
    p.xy = rotate(p.xy,iTime*0.05);
    p.yz = rotate(p.yz,iTime*0.1);
    for(int i=0;i<5;i++)
    {
        p.xy = pmod(p.xy,12.0);
        p.y-=4.0;
        p.yz = pmod(p.yz,16.0);
        p.z-=6.8;
    }
    return dot(abs(p),rotate(normalize(vec3(2,1,3)),normalize(vec3(7,1,2)),1.8))-0.3;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;
 	vec3 ro=vec3(0,0,3.5);
 	vec3 ta = vec3(1,0,0);
 	ta.xz=rotate(ta.xz,iTime*0.2);

 	vec3 w=normalize(ta-ro);
 	vec3 u=normalize(cross(w,vec3(0,1,0)));
    vec3 rd=mat3(u,cross(u,w),w)*normalize(vec3(uv,2));
    vec3 col = vec3(0);
    float d,t=0.0;
    for(float i=1.0;i>0.0;i-=1.0/80.0)
    {
     	t+=d=map(ro+t*rd);
        if(d<0.001)
        {
            col+=mix(vec3(1),hue(length(ro+t*rd)*0.1+iTime*0.1),0.6)*i*i;
            break;
        }
    }
    fragColor = vec4(col, 1.0);
}

float speed=.6;

float mirrorlig(vec2 p)
{
	p*=10.;
 	float lig;
 	p.x=abs(p.x);
 	float ligdis=10.;
 	float startoffset=.1;
 	for(float i=1.; i<6.; ++i)
    {	
  		float k= fract(iTime*speed+i*.2);
  		vec2 d = p-vec2(1.2,.5)*(k+startoffset)*ligdis;
  		float len= length(d)*10.;
  		lig+=asin((.05+k*.15)/len)*10.;
 	}
 	p*=10.;
 	for(float i=1.; i<6.; ++i)
 	{
  		float k= fract(iTime*speed+i*.2);
  		vec2 d = p-vec2(1.2,.5)*(k+startoffset)*ligdis;
  		float len= length(d) * 10.;
  		lig+=asin((.05+k*.15)/len)*10.;
 	}

 	return lig;
}

float mirrorligreflect(vec2 p)
{
 	p*=10.;
 	float lig;
 	p.x=abs(p.x);
 	p.y*=-1.;
 	//p.y-=1.5;
 	float ligdis=10.;
 	float startoffset=.1;
 	p.y*=.6;
 	for(float i=1.; i<6.; ++i)
 	{
  		float k= fract(iTime*speed+i*.2);
  		vec2 d = p-vec2(.6,.5)*(k+startoffset)*ligdis;
  		float len= length(d);
  		lig+=smoothstep(1.5*k,.0,len)*1.;
 	}

 	return lig;
}


float side(vec2 p)
{
 	p.x=-abs(p.x);
 	float rate = 1.5;
 	float m=step(rate*p.x,p.y)*step(p.y,p.x+.02)*p.y;
 	p.x*=rate;
 	return m;
}


float road(vec2 p)
{
 	p.y+=.6;
 	p*=10.;
 	p.x *= p.y*.5;
 	float rd=.0;
 	p.x = abs(p.x);
 	p.y*=smoothstep(6., 5.9, p.y);

 	float width=.2;
 	rd = smoothstep(width+.01,width,p.x);
 	rd*=smoothstep(.55,.51,fract(p.y+iTime*speed*5.));
 	if(p.y<=0.)return 0.;
 	return rd;
}

vec3 sun(vec2 p)
{
 	float res;
 	float len = length(p);
 	float r = .2;
 	float cir = smoothstep(r+.01,r,len);
 	vec3 col = vec3(.5,.5,1.)*cir;
 	return col;
}


vec3 background(vec2 p)
{
 	p.y+=.5;

 	vec3 upcol = vec3(0.,0.,0.1);
 	vec3 downcol = vec3(.5,.1,0.05);
 	vec3 col = mix(upcol, downcol, p.y);

 	//sun
 	p.y-=.5;
 	float len = length(p);
 	float r = .2;
 	vec3 cir = smoothstep(r+.01,r,len)*vec3(1.,.3,.2);

 	col=mix(col,cir,p.y+.1);
 	return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

 	float lig = mirrorlig(uv);
 	lig+=mirrorligreflect(uv);
 	vec3 col = vec3(lig);

 	col+=road(uv);
 	col+=background(uv);
 	col+=side(uv);

    fragColor = vec4(col, 1.0);


}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv.x -= .5;

    float speed = .043;
    float waveDiff = .3;

    vec3 color = vec3(.0);

    for (float i = .0; i < .8; i+=.1) {
        float spin = i * 4.;
        vec2 toCenter = vec2(sin(iTime + spin) * .1 + .5, cos(iTime + spin) * .1 + .5)-uv;
        float pct = length(toCenter);

        float waveShift = waveDiff + i * waveDiff;
        float waveSpeed = iTime * (7. - i * waveDiff) * speed + waveShift;

        color = abs(
            color 
            - vec3(
                1. 
                - step(
                    abs(
                        sin(waveSpeed) * .015 + (.03 + i * .5)
                    ), 
                    pct
                )
            )
        );
    }


	fragColor = vec4( color * vec3(1.0, .0, .0), 1.0 );
}

// A stylized variation of the octopus from
// sben "Illustrated Equations"
// https://www.shadertoy.com/view/MtBGDW

float octopus(vec2 p, float t){
	float ret = (p.y+length(p*p.x)-cos(t+p.y));
	ret = (p.y+length(p*ret)-cos(t+p.y));
	ret = (p.y+length(p*ret)-cos(t+p.y));
	ret *= ret*.1;
	return ret;
}
float dtoa(float d, float amount){
    return 1. / clamp(d*amount, 1., amount);
}
float rand(float n){
 	return fract(cos(n*89.42)*343.42);
}
float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 O, in vec2 P )
{
    vec2 V = P/iResolution.x-.5;
    V.y += .1;
    float t = iTime;
    float d = octopus(V*15., t);
    d -= .1;
    float a = dtoa(d, 6. + rand(V.y)*2. + rand(V.x)*1.2);
    float a2 = dtoa(-d, 15. + rand(V.y)*8. + rand(V.x)*4.);

    O = mix(vec4(.95,.9,.7,0), vec4(.5,.1,.1,0), vec4(a));
    O *= a2;

    vec2 N = P/iResolution.xy-.5;
    O += (rand(N*iTime)-.5)*.05;
    O *= 1.-smoothstep(0.,.3,abs(N.x));
    O *= 1.-smoothstep(0.,.7,abs(N.y));
    O = pow(O,vec4(.7));
    O.a = 1.;
}

