    p.x = abs(p.x);  
    p.z -= 3.0;
    p.yz = rotate2D(p.yz, curDolphinAngle);
    q = p - vec3(0.0, 0.0, -0.65);
    dBodyA = sdEllipsoid(q, vec3 (0.7, 0.8, 2.4));

    q = p;
    q.z -= -1.8;
    q.yz = rotate2D(q.yz, curDolphinAngle);
    q.z -= -1.6;
    dBodyB = sdEllipsoid(q, vec3(0.40, 0.5, 2.5));

    q.z -= -2.2;
    q.xy = rotate2D(q.xy, 2.0 * curDolphinAngle);
    q.xz -= vec2(0.5, -0.5);
    q.yz = rotate2D(q.yz, 0.4);
    dFin = sdEllipsoid(q, vec3(0.7, 0.05, 0.3));

    q = p;
    q.xy = rotate2D(q.xy, 2.0 * curDolphinAngle * 1.25);
    q.yz -= vec2(-0.3, 0.1);
    dHands = sdEllipsoid(q, vec3(1.8, 0.07, 0.4));

    dFinTop = 1.0;
    q = p;
    q.y -= 0.8;
    q.z -= -1.5;
    q.yz = rotate2D(q.yz, 0.5);
    q.xz = rotate2D(q.xz, 1.0);
    dFinTop = sdEllipsoid(q, vec3(0.2, 0.6, 0.1));

    dMin = smin(dBodyA, dBodyB,  0.1);
    dMin = smin(dMin,   dFin,    0.1);
    dMin = smin(dMin,   dHands,  0.1);
    dMin = smin(dMin,   dFinTop, 0.1);
    return dMin;
}

vec4 map(in vec3 p)
{	
    p.xz = mod(p.xz + 1.0, 2.0) - 1.0;
    p.y  = mod(p.y  + 1.0, 2.0) - 1.0;

    float d = sdEllipsoid(p, vec3(1.0));
    vec4 res = vec4(d, 1.0, 0.0, 0.0);

    float s = 1.0;
    for(int i = 0; i < MENGER_ITERATIONS; ++i)
    { 
        vec3 a = mod(p * s, 2.0) - 1.0;
        s *= 7.0;
        vec3 r = abs(1.0 - 3.0 * abs(a));
        float da = max(r.x, r.y);
        float db = max(r.y, r.z);
        float dc = max(r.z, r.x);
        float c = (min(da, min(db, dc)) - 0.5) / s;

        if(c > d)
        {
            d = c;
            res = vec4(d, min(res.y, 0.3 * da * db * dc), 0.0, 1.0);
        }
    }

    return res;
}

vec4 intersect(in vec3 ro, in vec3 rd)
{
    float t = 0.0;
    vec4 res = vec4(-1.0);
    vec4 h = vec4(1.0);
    for (int i = 0; i < INTERSECT_STEPS; i++ )
    {
		if(h.x < INTERSECT_MIN_DIST || t > INTERSECT_MAX_DIST) 
		{
	    	break;
		}

        h = map(ro + rd * t);
        res = vec4(t, h.yzw);
        t += h.x;
    }

    if (t > INTERSECT_MAX_DIST) 
    {
		res = vec4(-1.0);
    }

    return res;
}

float softshadow(in vec3 ro, in vec3 rd)
{
    float res = 1.0;
    float t = 0.0;
    for (int i = 0; i < SOFT_SHADOW_STEPS; ++i)
    {
		vec3 pos = ro + rd * t;
        float h = map(pos).x;
        res = min(res, float(SOFT_SHADOW_STEPS) * h / t);
        if(res < 0.001)
		{
	    	break;
		}
        t += clamp(h, 0.01, 0.2);
    }
    return saturate(res);
}

vec3 calcNormal(in vec3 pos)
{
    vec3 eps = vec3(0.0001, 0.0, 0.0);
    vec3 n;
    n.x = map(pos + eps.xyy).x - map(pos - eps.xyy).x;
    n.y = map(pos + eps.yxy).x - map(pos - eps.yxy).x;
    n.z = map(pos + eps.yyx).x - map(pos - eps.yyx).x;
    return normalize(n);
}

float softshadow(in vec3 ro, in vec3 rd, in float k)
{
    float res = 1.0;
    float t = 0.0;
    for (int i = 0; i < SOFT_SHADOW_STEPS; ++i)
    {
		vec3 pos = ro + rd * t;
        float h = map(pos).y;
        res = min(res, k * h / t);
        if(res < 0.001)
		{
	    	break;
    	}
        t += clamp(h, 0.01, 0.2);
    }
    return saturate(res);
}

vec2 castRay(in vec3 ro, in vec3 rd)
{
     float tmin = 0.0;
     float tmax = 40.0;

     float precis = 0.001;
     float t = tmin;
     float m = -1.0;
     for (int i = 0; i < 100; ++i)
     {
    	 float res = deDolphin(ro + rd * t);
    	 if (res < precis || t > tmax)
         {
             break;
         }
   	 	 t += res;
    	 m = res;
     }

     if (t > tmax)
     {
	 	m = -1.0;
     }

     return vec2(t, m);
}

vec3 renderDolphin(in vec3 ro, in vec3 rd)
{ 
    vec3 color = vec3(0.0);	
    vec2 res = castRay(ro, rd);
    if (res.y > -0.5)
    {
	 	const vec3 light = vec3(0.5, 0.5, -0.5);

	 	vec3 pos = ro + res.x * rd;

	 	float red   = clamp(sin(iTime * 0.8), 0.2, 0.8);
	 	float green = clamp(cos(iTime * 0.4), 0.2, 0.8);
	 	float blue  = clamp(sin(iTime * 0.2), 0.2, 0.8);

        vec3 baseColor = vec3(red, green, blue);
	 	vec3 ambient = vec3(0.5) * baseColor;
	 	vec3 normal = calcNormal(pos);

	 	float shadow = softshadow(pos + 0.001 * normal, light, 32.0);	
	 	vec3 diffuse = baseColor * shadow * vec3(0.2, 0.0, 10.0);

	 	color = diffuse + ambient;
    }	
    return color;
}

vec3 render(in vec3 ro, in vec3 rd)
{
    vec3 color = vec3(0.5);
    vec4 res = intersect(ro,rd);
    if(res.x > 0.0)
    {
        const vec3 light = vec3(0.0, 0.0, -0.5);

		vec3 pos = ro + res.x * rd;

        vec3 baseColor = vec3(0.0, 0.2, 0.6);
		vec3 ambient = vec3(0.2) * baseColor;

		vec3 normal = calcNormal(pos);
		vec3 reflection = reflect(rd, normal);

		float occ = res.y;
		float shadow = softshadow(pos + 0.001 * normal, light);

		vec3 diffuse = baseColor * shadow * occ;	
		color = diffuse + ambient;
    }

    color += renderDolphin(ro, rd);
    color += renderDolphin(ro + vec3(10.5, -1.3, 4.5), rd);
    color += renderDolphin(ro + vec3(15.5, -1.3, -4.5), rd);

    color += vec3(0.0, 0.05, 0.20);
    return pow(color, vec3(0.4545));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 p = 2.0 * (fragCoord.xy / iResolution.xy) - 1.0;
    p.x *= iResolution.x / iResolution.y;

    // camera
    vec3 ro = vec3(12.25, 2.9, -2.0);
    vec3 ww = normalize(vec3(0.0) - ro);
    vec3 uu = normalize(cross(vec3(0.0, 1.0, 0.0), ww));
    vec3 vv = normalize(cross(ww, uu));
    vec3 rd = normalize(p.x * uu + p.y * vv + 2.0 * ww);

    ro.x -= iTime * 0.1; //Camera / Ray moving

 	vec3 color = render(ro + vec3(0.0, -1.0, 0.0), rd);    
    fragColor = vec4(color, 1.0);
}

const float PI = 3.141592654;

vec3 hash32(vec2 p){
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}
vec4 disco(vec2 uv) {
    float v = abs(cos(uv.x * PI * 2.) + cos(uv.y *PI * 2.)) * .5;
    uv.x -= .5;
    vec3 cid2 = hash32(vec2(floor(uv.x - uv.y), floor(uv.x + uv.y)));
    return vec4(cid2, v);
}
float nsin(float t) {return sin(t)*.5+.5; }

void mainImage( out vec4 o, in vec2 fragCoord)
{
    vec2 R = iResolution.xy;
    vec2 uv = fragCoord / R - .5;
    uv.x *= R.x / R.y;

    float t = (iTime + 129.) * .6; //t = 0.;
    uv = uv.yx;
    uv *= 2.+sin(t)*.2;
    uv.x += t*.5;

    o = vec4(1);
    float sgn = -1.;
    for(float i = 1.; i <= 5.; ++i) {
        vec4 d = disco(uv);
        float curv = pow(d.a, .5-((1./i)*.3));
        curv = pow(curv, .8+(d.b * 2.));
        curv = smoothstep(nsin(t)*.3+.2,.8,curv);
        o += sgn * d * curv;
        o *= d.a;
        sgn = -sgn;
        uv += 100.;// move to a different cell
        uv += sin(d.ar*7.33+t*1.77)*(nsin(t*.7)*.1+.04);
    }

    // post
   	o.gb *= vec2(1.,.5);//tint
    vec2 N = (fragCoord / R )- .5;
    o = clamp(o,.0,1.);
    o = pow(o, vec4(.2));
    o.rgb -= hash32(fragCoord + iTime).r*(1./255.);

    N = pow(abs(N), vec2(2.5));
    N *= 7.;
    o *= 1.5-length(N);// ving
    o = clamp(o,.0,1.);
    o.a = 1.;
}

