    float yb = max(abs(fract(pos.y+0.5)-0.5)-le,0.0);

    float la = ya*ya - 2.0*r1*sqrt(pos.x*pos.x+ya*ya);
    float lb = yb*yb - 2.0*r1*sqrt(pos.z*pos.z+yb*yb);

    return sqrt(dot(pos.xz,pos.xz) + r1*r1 + min(la,lb)) - r2;
}
