// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.0005;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) + 
					  e.xxx*map( pos + e.xxx*eps ) );
}

// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ )
    {
		float h = map( ro + rd*t );
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

float calcOcclusion( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.15*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos );
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - occ*1.5, 0.0, 1.0 );
}


#define AA 3

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an = 0.7*iTime;
	vec3 ro = vec3( 1.0*cos(an), 0.2, 1.0*sin(an) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv =          ( cross(uu,ww));

    // render
    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

        // raymarch
        const float tmax = 5.0;
        float t = 0.0;
        for( int i=0; i<256; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }


        // shading/lighting	
        vec3 col = vec3(0.0);
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            vec3 lig = normalize(vec3(0.6,0.2,0.4));
            vec3 hal = normalize(lig-rd);
            float dif = clamp( dot(nor,lig), 0.0, 1.0 );
            float occ = calcOcclusion( pos, nor );
            if( dif>0.001 ) dif *= calcSoftshadow( pos, lig, 0.01, 1.0 );
            float spe = pow(clamp(dot(nor,hal),0.0,1.0),16.0)*dif*(0.04+0.96*pow(clamp(1.0-dot(hal,-rd),0.0,1.0),5.0));
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,1.0,0.0));
            col =  vec3(0.5,1.0,1.2)*amb*occ;
            col += vec3(2.8,2.2,1.8)*dif;

            col *= 0.2;

            col += vec3(2.8,2.2,1.8)*spe*3.0;
        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float x = floor(uv.x * 10.0) * 0.1;
    float y = floor((uv.x * (17.3 / 2.0) + uv.y * 5.0)) * 0.1;
    float z = floor((-uv.x * (17.3 / 2.0) + uv.y * 5.0)) * 0.1;
    vec3 col = 0.5 + 0.5*cos(iTime * 2.0 + 2.0 * vec3(x,y,z)+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}

const float PI = 3.14159;
const float PI2 = PI*2.;

vec3 dtoa(float d, vec3 amount){
    return vec3(1. / clamp(d*amount, vec3(1), amount));
}
mat2 rot2D(float r){
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}
float nsin(float x) {
    return sin(x)*.5+.5;
}
vec3 hash32(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}
float opUnion( float d1, float d2 ) { return min(d1,d2); }
float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }

float sdEquilateralTriangle(  in vec2 p )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - 1.0;
    if( p.x + k*p.y > 0.0 ) p = vec2( p.x - k*p.y, -k*p.x - p.y )/2.0;
    p.x += 2.0 - 2.0*clamp( (p.x+2.0)/2.0, 0.0, 1.0 );
    return -length(p)*sign(p.y);
}

void mainImage( out vec4 o, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-.5;
    uv.x *= iResolution.x / iResolution.y;
    uv.y += .133;
    uv *= 2.;
    float t = iTime*.5;
    float centerDist = sqrt(3.)/3.;
    float sd = 1.;
    float sgn = 1.;
    vec2 uv2 = uv;
    for (float i = 0.; i < 20.; ++ i) {
        float a = t*(sgn*=-1.);
        float sd2 = sdEquilateralTriangle(uv2*.9*rot2D(a)+vec2(0,centerDist));
    	sd2 = opSubtraction(sd2, sdEquilateralTriangle(uv2+vec2(0,centerDist)));
        sd = opUnion(sd, sd2);
        //uv2 *= nsin(t*1.22)*.5+1.5;
        uv2 *= 1.5;
    }

    o.rgb = dtoa(sd, vec3(50,400,400)) * vec3(.9,.8,.5);
    o.rgb += (hash32(fragCoord+t)-.5)*.1;

    vec2 N = fragCoord / iResolution.xy-.5;
    o.rgb += dot(N,N) * vec3(0,1,1);
    o.a = 1.;
}

const int max_iter = 500;
const float min_pow = 0.0;
const float max_pow = 10.0;
const float mid_pow = (min_pow + max_pow) * 0.5;

vec2 comp_pow(vec2 c, float n) {
    /*demoivre*/
    float theta = atan(c.y, c.x);
    float absv = sqrt((c.x * c.x) + (c.y * c.y));
    return pow(absv, n) * vec2(cos(theta * n), sin(theta * n));
}

float mandelbrot(vec2 c, float n, int maxIter) {
    vec2 z = vec2(0.0, 0.0);
    for (int i = 0; i < maxIter; i++) {
        vec2 zn = comp_pow(z, n) + c;
        if (zn.x > 2.0 || zn.y > 2.0) {
	        return float(i) / float(maxIter);
	    }
        z = zn;
    }
    return 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 coord = ((fragCoord / iResolution.xy) * vec2(3.5, 2.0)) - vec2(2.0, 1);
    float power = mid_pow - cos(iTime * 0.1) * mid_pow;
    float val = sqrt(mandelbrot(coord, power, max_iter));
    fragColor = vec4(val, val, val, 1.0);
}

const int MAX_ITER = 100;
const float MAX_DIST = 20.0; 
const float EPSILON = 0.001; 

vec3 cameraOrigin = vec3(2.0, 3.0, 2.0);
vec3 cameraTarget = vec3(0.0, 0.0, 0.0);
vec3 upDirection = vec3(0.0, 1.0, 0.0);

vec4 shapeColor = vec4(0.99, 0.5, 1.0, 1.0);
float specularPower = 16.0;

// a sphere is defined as the position and a radius 
float sphere(vec3 pos, float radius)
{
    return length(pos) - radius;
}

float distfunc(vec3 pos)
{
    return sphere(pos, 2.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 cameraDir = normalize(cameraTarget - cameraOrigin);
    vec3 cameraRight = normalize(cross(upDirection, cameraOrigin));
    vec3 cameraUp = cross(cameraDir, cameraRight);    
    vec2 screenPos = -1.0 + 2.0 * fragCoord.xy / iResolution.xy; // rescale screenPos to be from -1 to 1

    screenPos.x *= iResolution.x / iResolution.y; // now scale it by the aspect ratio

    // ray from camera position to screen pixel
    vec3 rayDir = normalize(cameraRight * screenPos.x + cameraUp * screenPos.y + cameraDir);

    // The raymarching loop
    float totalDist = 0.0;
    vec3 pos = cameraOrigin;
    float dist = EPSILON;

    // trying to find a point of intersection 
    for (int i = 0; i < MAX_ITER; i++)
    {
        // Either we've hit the object or hit nothing at all, either way we should break out of the loop
        if (dist < EPSILON || totalDist > MAX_DIST)
        break; // If you use windows and the shader isn't working properly, change this to continue;

        dist = distfunc(pos); // Evalulate the distance at the current point
        totalDist += dist;
        pos += dist * rayDir; // Advance the point forwards in the ray direction by the distance
    }

    if(dist < EPSILON) 
    {
        // calculate lighting

        // need to find the normal of the surface 
        // using distance, can calculate this by sampling a few points
        vec2 eps = vec2(0.0, EPSILON);
        // creating points that are +/- the pos in x/y/z 
        // (since eps.x is 0 and eps.y is actual EPSILON)
        vec3 normal = normalize(vec3(
            distfunc(pos + eps.yxx) - distfunc(pos - eps.yxx),
            distfunc(pos + eps.xyx) - distfunc(pos - eps.xyx),
            distfunc(pos + eps.xxy) - distfunc(pos - eps.xxy)
        ));


        vec3 lightPos = vec3(9.0*cos(iTime*3.0),3.0*sin(iTime*3.0),9.0);

        // the unlit colour
        vec3 ambientColor = vec3(0.2, 0.0, 0.0);
        // the lit colour of the shape 
        vec3 diffuseColor = vec3(0.3, 0.3, 0.3);
        // the reflection highlight
        vec3 specColor = vec3(1.0, 1.0, 1.0);

        // light direction is the light position to the vertex position
        vec3 lightDir = normalize(lightPos - pos);

        float lambertian = max(dot(lightDir, normal), 0.0);
        vec3 worldHalfDir = normalize(lightDir + cameraDir);
        float spec = dot(worldHalfDir,normal);

        float s = step(0.05,lambertian);
        float t = step(0.001,0.1*spec);
        float e = step(0.3,lambertian);
        float p = step(0.5,lambertian);

        float specular = 0.3;

        fragColor = vec4(ambientColor +
                      s * diffuseColor +
                      e * vec3(0.1,0.1,0.1) +
                      p * vec3(0.1,0.1,0.1) +  
                      p * t * specular * specColor, 1.0);
    }

    else 
    {
        fragColor = vec4(0.3,0.3,0.3,1.0);
    }

}

